INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20682, 'nvi', 'Ezequiel', 'ez', 12, 1, 'Veio a mim esta palavra do Senhor:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20683, 'nvi', 'Ezequiel', 'ez', 12, 2, '"Filho do homem, você vive no meio de uma nação rebelde. Eles têm olhos para ver, mas não vêem, e ouvidos para ouvir, mas não ouvem, pois são uma nação rebelde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20684, 'nvi', 'Ezequiel', 'ez', 12, 3, '"Portanto, filho do homem, arrume os seus pertences para o exílio e, durante o dia, à vista de todos, parta, e vá para outro lugar. Talvez eles compreendam, embora sejam uma nação rebelde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20685, 'nvi', 'Ezequiel', 'ez', 12, 4, 'Durante o dia, sem fugir aos olhares do povo, leve para fora os seus pertences arrumados para o exílio. Então, à tarde, saia como aqueles que vão para o exílio. E que os outros o vejam fazer isso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20686, 'nvi', 'Ezequiel', 'ez', 12, 5, 'Enquanto eles o observam, faça um buraco no muro e passe os seus pertences através dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20687, 'nvi', 'Ezequiel', 'ez', 12, 6, 'Ponha-os nos ombros, enquanto o povo estiver observando, e carregue-os ao entardecer. Cubra o rosto para que você não possa ver nada do país, pois eu fiz de você um sinal para a nação de Israel".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20688, 'nvi', 'Ezequiel', 'ez', 12, 7, 'Então eu fiz o que me foi ordenado. Durante o dia levei para fora as minhas coisas arrumadas para o exílio. Depois, à tarde, fiz com as mãos um buraco no muro. Ao entardecer saí com os meus pertences carregando-os nos ombros à vista de todos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20689, 'nvi', 'Ezequiel', 'ez', 12, 8, 'De manhã me veio esta palavra do Senhor:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20690, 'nvi', 'Ezequiel', 'ez', 12, 9, '"Filho do homem, acaso aquela nação rebelde de Israel não lhe perguntou: ‘Que é que você está fazendo? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20691, 'nvi', 'Ezequiel', 'ez', 12, 10, '"Diga-lhes: ‘Assim diz o Soberano Senhor: Esta advertência diz respeito ao príncipe de Jerusalém e a toda a nação de Israel que está ali’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20692, 'nvi', 'Ezequiel', 'ez', 12, 11, 'Diga-lhes: ‘Eu sou um sinal para vocês. Como eu fiz, assim lhes será feito. Eles irão para o exílio como prisioneiros’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20693, 'nvi', 'Ezequiel', 'ez', 12, 12, '"O príncipe deles porá os seus pertences nos ombros ao entardecer e sairá por um buraco que será escavado no muro para ele passar. Ele cobrirá o rosto para que não possa ver nada do país.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20694, 'nvi', 'Ezequiel', 'ez', 12, 13, 'Estenderei a minha rede para ele, e ele será apanhado em meu laço; eu o trarei para a Babilônia, terra dos caldeus, mas ele não a verá, e ali morrerá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20695, 'nvi', 'Ezequiel', 'ez', 12, 14, 'Espalharei aos ventos todos os que estão ao seu redor, os seus oficiais e todas as suas tropas, e os perseguirei com a espada em punho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20696, 'nvi', 'Ezequiel', 'ez', 12, 15, '"Eles saberão que eu sou o Senhor, quando eu os dispersar entre as nações e os espalhar pelas terras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20697, 'nvi', 'Ezequiel', 'ez', 12, 16, 'Mas pouparei uns poucos deles da espada, da fome e da peste para que, nas nações aonde forem, contem todas as suas práticas repugnantes. Então saberão que eu sou o Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20698, 'nvi', 'Ezequiel', 'ez', 12, 17, 'Esta palavra do Senhor veio a mim:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20699, 'nvi', 'Ezequiel', 'ez', 12, 18, '"Filho do homem, trema enquanto come a sua comida, e fique arrepiado de medo enquanto bebe a sua água.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20700, 'nvi', 'Ezequiel', 'ez', 12, 19, 'Diga ao povo do país: ‘Assim diz o Senhor Soberano acerca daqueles que vivem em Jerusalém e em Israel: Eles comerão sua comida com ansiedade e beberão sua água desesperados, pois tudo o que existe em sua terra dela será arrancado por causa da violência de todos os que ali vivem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20701, 'nvi', 'Ezequiel', 'ez', 12, 20, 'As cidades habitadas serão arrasadas e a terra ficará abandonada. Então vocês saberão que eu sou o Senhor’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20702, 'nvi', 'Ezequiel', 'ez', 12, 21, 'O Senhor me falou:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20703, 'nvi', 'Ezequiel', 'ez', 12, 22, '"Filho do homem, que provérbio é este que vocês têm em Israel: ‘Os dias passam e todas as visões dão em nada’?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20704, 'nvi', 'Ezequiel', 'ez', 12, 23, 'Diga-lhes, pois: ‘Assim diz o Soberano Senhor: Vou dar fim a esse provérbio, e não será mais citado em Israel’. Diga-lhes: ‘Estão chegando os dias em que toda visão se cumprirá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20705, 'nvi', 'Ezequiel', 'ez', 12, 24, 'Pois não haverá mais visões falsas ou adivinhações bajuladoras entre o povo de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20706, 'nvi', 'Ezequiel', 'ez', 12, 25, 'Mas eu, o Senhor, falarei o que eu quiser, e isso se cumprirá sem demora. Pois em seus dias, ó nação rebelde, cumprirei tudo o que eu disser, palavra do Soberano Senhor’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20707, 'nvi', 'Ezequiel', 'ez', 12, 26, 'Veio a mim esta palavra do Senhor:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20708, 'nvi', 'Ezequiel', 'ez', 12, 27, '"Filho do homem, a nação de Israel está dizendo: ‘A visão que ele vê é para daqui a muitos anos, e ele profetiza sobre o futuro distante’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20709, 'nvi', 'Ezequiel', 'ez', 12, 28, '"Pois diga a eles: ‘Assim diz o Soberano Senhor: Nenhuma de minhas palavras sofrerá mais demora; tudo o que eu disser se cumprirá, palavra do Soberano Senhor’ ".');
