INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20710, 'nvi', 'Ezequiel', 'ez', 13, 1, 'A palavra do Senhor veio a mim. Disse ele:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20711, 'nvi', 'Ezequiel', 'ez', 13, 2, '"Filho do homem, profetize contra os profetas de Israel que estão profetizando agora. Diga àqueles que estão profetizando pela sua própria imaginação: ‘Ouçam a palavra do Senhor!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20712, 'nvi', 'Ezequiel', 'ez', 13, 3, 'Assim diz o Soberano Senhor: Ai dos profetas tolos que seguem o seu próprio espírito e não viram nada!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20713, 'nvi', 'Ezequiel', 'ez', 13, 4, 'Seus profetas, ó Israel, são como chacais no meio de ruínas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20714, 'nvi', 'Ezequiel', 'ez', 13, 5, 'Vocês não foram consertar as brechas do muro para a nação de Israel, para que ela pudesse resistir firme no combate do dia do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20715, 'nvi', 'Ezequiel', 'ez', 13, 6, 'Suas visões são falsas e suas adivinhações, mentira. Dizem "Palavra do Senhor", quando o Senhor não os enviou; contudo, esperam que as suas palavras se cumpram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20716, 'nvi', 'Ezequiel', 'ez', 13, 7, 'Acaso vocês não tiveram visões falsas e não pronunciaram adivinhações mentirosas quando disseram "Palavra do Senhor", sendo que eu não falei?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20717, 'nvi', 'Ezequiel', 'ez', 13, 8, '" ‘Portanto assim diz o Soberano Senhor: Por causa de suas palavras falsas e de suas visões mentirosas, estou contra vocês, palavra do Soberano Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20718, 'nvi', 'Ezequiel', 'ez', 13, 9, 'Minha mão será contra os profetas que têm visões falsas e proferem adivinhações mentirosas. Eles não pertencerão ao conselho de meu povo, não estarão inscritos nos registros da nação de Israel e não entrarão na terra de Israel. Então vocês saberão que eu sou o Soberano Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20719, 'nvi', 'Ezequiel', 'ez', 13, 10, '" ‘Por fazerem o meu povo desviar-se ao dizerem "Paz" quando não há paz, e porque, quando é construído um muro frágil, eles lhe passam cal,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20720, 'nvi', 'Ezequiel', 'ez', 13, 11, 'por isso diga àqueles que lhe passam cal: o muro cairá! Virá chuva torrencial, e derramarei chuva de pedra, e rajarão ventos violentos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20721, 'nvi', 'Ezequiel', 'ez', 13, 12, 'Quando o muro desabar, o povo lhes perguntará: "Onde está a caiação que vocês fizeram? " ’ "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20722, 'nvi', 'Ezequiel', 'ez', 13, 13, '" ‘Por isso, assim diz o Soberano Senhor: Na minha ira permitirei o estouro de um vento violento, e na minha indignação chuva de pedra e um aguaceiro torrencial cairão com ímpeto destruidor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20723, 'nvi', 'Ezequiel', 'ez', 13, 14, 'Despedaçarei o muro que vocês caiaram e o arrasarei para que se desnudem os seus alicerces. Quando ele cair, vocês serão destruídos com ele; e vocês saberão que eu sou o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20724, 'nvi', 'Ezequiel', 'ez', 13, 15, 'Assim esgotarei minha ira contra o muro e contra aqueles que o caiaram. Direi a vocês: O muro se foi, e também aqueles que o caiaram,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20725, 'nvi', 'Ezequiel', 'ez', 13, 16, 'os profetas de Israel que profetizaram sobre Jerusalém e tiveram visões de paz para ela quando não havia paz, palavra do Soberano Senhor. ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20726, 'nvi', 'Ezequiel', 'ez', 13, 17, '"Agora, filho do homem, vire o rosto contra as filhas do seu povo que profetizam pela sua própria imaginação. Profetize contra elas');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20727, 'nvi', 'Ezequiel', 'ez', 13, 18, 'e diga: ‘Assim diz o Senhor Soberano: Ai das mulheres que costuram berloques de feitiço em todos os seus pulsos e fazem véus de vários comprimentos para a cabeça a fim de enlaçarem o povo. Pensam que vão enlaçar as vidas de meu povo e preservar as de vocês?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20728, 'nvi', 'Ezequiel', 'ez', 13, 19, 'Vocês me profanaram no meio de meu povo em troca de uns punhados de cevada e de migalhas de pão. Ao mentirem ao meu povo, que ouve mentiras, vocês mataram aqueles que não deviam ter morrido e pouparam aqueles que não deviam viver.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20729, 'nvi', 'Ezequiel', 'ez', 13, 20, '" ‘Por isso, assim diz o Soberano Senhor: Estou contra os seus berloques de feitiço com os quais vocês prendem o povo como se fossem passarinhos, e os arrancarei dos seus braços; porei em liberdade o povo que vocês prendem como passarinhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20730, 'nvi', 'Ezequiel', 'ez', 13, 21, 'Rasgarei os seus véus e libertarei o meu povo das mãos de vocês, e eles não serão mais presas do seu poder. Então vocês saberão que eu sou o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20731, 'nvi', 'Ezequiel', 'ez', 13, 22, 'Por que, mentindo, vocês desencorajaram o justo, contra a minha vontade, e encorajaram os ímpios a não se desviarem dos seus maus caminhos para salvarem as suas vidas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20732, 'nvi', 'Ezequiel', 'ez', 13, 23, 'vocês não terão mais visões falsas e nunca mais vão praticar adivinhação. Livrarei o meu povo das mãos de vocês. E então vocês saberão que eu sou o Senhor’ ".');
