INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20733, 'nvi', 'Ezequiel', 'ez', 14, 1, 'Algumas das autoridades de Israel vieram e se sentaram diante de mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20734, 'nvi', 'Ezequiel', 'ez', 14, 2, 'Então o Senhor me falou:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20735, 'nvi', 'Ezequiel', 'ez', 14, 3, '"Filho do homem, estes homens ergueram ídolos em seus corações e puseram tropeços ímpios diante de si. Devo deixar que me consultem?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20736, 'nvi', 'Ezequiel', 'ez', 14, 4, 'Ora, diga-lhes: ‘Assim diz o Soberano Senhor: Quando qualquer israelita erguer ídolos em seu coração e puser um tropeço ímpio diante do seu rosto e depois for consultar um profeta, eu o Senhor, eu mesmo, responderei a ele conforme a sua idolatria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20737, 'nvi', 'Ezequiel', 'ez', 14, 5, 'Isto farei para reconquistar o coração da nação de Israel, que me abandonou em troca de seus ídolos’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20738, 'nvi', 'Ezequiel', 'ez', 14, 6, '"Por isso diga à nação de Israel: ‘Assim diz o Soberano Senhor: Arrependem-se! Desviem-se dos seus ídolos e renunciem a todas as práticas detestáveis!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20739, 'nvi', 'Ezequiel', 'ez', 14, 7, '" ‘Quando qualquer israelita ou qualquer estrangeiro residente em Israel separar-se de mim e erguer ídolos em seu coração e puser um tropeço ímpio diante de si e depois for a um profeta para me consultar, eu o Senhor, eu mesmo, responderei a ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20740, 'nvi', 'Ezequiel', 'ez', 14, 8, 'Voltarei o meu rosto contra aquele homem e farei dele um exemplo e um motivo de zombaria. Eu o eliminarei do meio do meu povo. E vocês saberão que eu sou o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20741, 'nvi', 'Ezequiel', 'ez', 14, 9, '" ‘E, se o profeta for enganado e levado a proferir uma profecia, eu o Senhor terei enganado aquele profeta, e estenderei o meu braço contra ele e o destruirei, tirando-o do meio de Israel, meu povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20742, 'nvi', 'Ezequiel', 'ez', 14, 10, 'O profeta será tão culpado quanto aquele que o consultar; ambos serão castigados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20743, 'nvi', 'Ezequiel', 'ez', 14, 11, 'E isso para que a nação de Israel não se desvie mais de mim, nem mais se contamine com todos os seus pecados. Serão o meu povo, e eu serei o seu Deus, palavra do Soberano Senhor’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20744, 'nvi', 'Ezequiel', 'ez', 14, 12, 'Esta palavra do Senhor veio a mim:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20745, 'nvi', 'Ezequiel', 'ez', 14, 13, '"Filho do homem, se uma nação pecar contra mim por infidelidade, estenderei contra ela o meu braço para cortar o seu sustento, enviar fome sobre ela e exterminar seus homens e seus animais.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20746, 'nvi', 'Ezequiel', 'ez', 14, 14, 'Mesmo que estes três homens — Noé, Daniel e Jó — estivessem nela, por sua retidão eles só poderiam livrar a si mesmos, palavra do Soberano Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20747, 'nvi', 'Ezequiel', 'ez', 14, 15, '"Ou, se eu enviar animais selvagens para aquela nação e eles a deixarem sem filhos e ela for abandonada de tal forma que ninguém passe por ela, com medo dos animais,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20748, 'nvi', 'Ezequiel', 'ez', 14, 16, 'juro pela minha vida, palavra do Soberano Senhor, mesmo que aqueles três homens estivessem nela, eles não poderiam livrar os seus próprios filhos ou filhas. Só a si mesmos se livrariam, e a nação seria arrasada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20749, 'nvi', 'Ezequiel', 'ez', 14, 17, '"Ou, se eu trouxer a espada contra aquela nação e disser: ‘Que a espada passe por toda esta terra’, e eu exterminar dela os homens e os animais,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20750, 'nvi', 'Ezequiel', 'ez', 14, 18, 'juro pela minha vida, palavra do Soberano Senhor, mesmo que aqueles três homens estivessem nela, eles não poderiam livrar seus próprios filhos ou filhas. Somente eles se livrariam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20751, 'nvi', 'Ezequiel', 'ez', 14, 19, '"Ou, se eu enviar uma peste contra aquela terra e despejar sobre ela a minha ira derramando sangue, exterminando seus homens e seus animais,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20752, 'nvi', 'Ezequiel', 'ez', 14, 20, 'juro pela minha vida, palavra do Soberano Senhor, mesmo que Noé, Daniel e Jó estivessem nela, eles não poderiam livrar seus filhos e suas filhas. Por sua justiça só poderiam livrar a si mesmos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20753, 'nvi', 'Ezequiel', 'ez', 14, 21, '"Pois assim diz o Soberano Senhor: Quanto pior será quando eu enviar contra Jerusalém os meus quatro terríveis juízos: a espada, a fome, os animais selvagens e a peste, para com eles exterminar os seus homens e os seus animais!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20754, 'nvi', 'Ezequiel', 'ez', 14, 22, 'Contudo, haverá alguns sobreviventes; filhos e filhas que serão retirados dela. Eles virão a vocês, e, quando vocês virem a conduta e as ações deles, vocês se sentirão consolados com relação à desgraça que eu trouxe sobre Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20755, 'nvi', 'Ezequiel', 'ez', 14, 23, 'Vocês se sentirão consolados quando virem a conduta e as ações deles, pois saberão que não agi sem motivo em tudo quanto fiz ali, palavra do Soberano Senhor".');
