INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20827, 'nvi', 'Ezequiel', 'ez', 17, 1, 'Veio a mim esta palavra do Senhor:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20828, 'nvi', 'Ezequiel', 'ez', 17, 2, '"Filho do homem, apresente uma alegoria e conte uma parábola à nação de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20829, 'nvi', 'Ezequiel', 'ez', 17, 3, 'Diga a eles: ‘Assim diz o Soberano Senhor: Uma grande águia, com asas poderosas, penas longas e basta plumagem de cores variadas veio ao Líbano. Apoderando-se do alto de um cedro,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20830, 'nvi', 'Ezequiel', 'ez', 17, 4, 'arrancou o seu broto mais alto e o levou para uma terra de comerciantes, onde o plantou numa cidade de mercadores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20831, 'nvi', 'Ezequiel', 'ez', 17, 5, '" ‘Depois apanhou um pouco de sementes da sua terra e as pôs em solo fértil. Ela as plantou como um salgueiro junto a muita água,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20832, 'nvi', 'Ezequiel', 'ez', 17, 6, 'e elas brotaram e formaram uma videira baixa e copada. Seus ramos se voltaram para a águia, mas as suas raízes permaneceram debaixo da videira. A videira desenvolveu-se e cobriu-se de ramos, brotos e folhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20833, 'nvi', 'Ezequiel', 'ez', 17, 7, '" ‘Mas havia outra águia grande, com asas poderosas e rica plumagem. A videira lançou suas raízes na direção dessa águia, desde o lugar onde estava plantada e estendeu seus ramos para ela em busca de água.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20834, 'nvi', 'Ezequiel', 'ez', 17, 8, 'Ora, ela havia sido plantada em terreno bom, junto a muita água, onde produziria ramos, daria fruto e se tornaria uma videira viçosa’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20835, 'nvi', 'Ezequiel', 'ez', 17, 9, '"Diga a eles: ‘Assim diz o Soberano Senhor: Ela vingará? Não será desarraigada e seus frutos não serão arrancados dela para que ela seque? Tudo o que brotar dela secará. Não serão precisos nem braços fortes nem muitas pessoas para arrancá-la pelas raízes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20836, 'nvi', 'Ezequiel', 'ez', 17, 10, 'Ainda que seja transplantada, será que vingará? Não secará totalmente quando o vento oriental a atingir, murchando e desaparecendo do lugar onde crescia? ’ "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20837, 'nvi', 'Ezequiel', 'ez', 17, 11, 'Veio depois a mim esta palavra do Senhor:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20838, 'nvi', 'Ezequiel', 'ez', 17, 12, '"Diga a essa nação rebelde: ‘Você não sabe o que essas coisas significam? ’ Diga a eles: ‘O rei da Babilônia foi a Jerusalém, tirou de lá o seu rei e os seus nobres, e os levou consigo de volta à Babilônia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20839, 'nvi', 'Ezequiel', 'ez', 17, 13, 'Depois fez um tratado com um membro da família real e o colocou sob juramento. Também levou embora os líderes da terra,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20840, 'nvi', 'Ezequiel', 'ez', 17, 14, 'para humilhar o reino e torná-lo incapaz de voltar a erguer-se, garantindo apenas a sua sobrevivência pelo cumprimento do seu tratado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20841, 'nvi', 'Ezequiel', 'ez', 17, 15, 'Mas o rei se revoltou contra ele e enviou mensagem ao Egito pedindo cavalos e um grande exército. Será que ele se sairá bem? Escapará aquele que age dessa maneira? Romperá ele o tratado e ainda assim escapará?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20842, 'nvi', 'Ezequiel', 'ez', 17, 16, '" ‘Juro pela minha vida, palavra do Soberano Senhor, que ele morrerá na Babilônia, na terra do rei que o pôs no trono, cujo juramento ele desprezou e cujo tratado ele rompeu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20843, 'nvi', 'Ezequiel', 'ez', 17, 17, 'O faraó, com seu poderoso exército e seus batalhões, não será de nenhuma ajuda para ele na guerra, quando rampas são construídas e obras de cerco são erguidas para destruir muitas vidas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20844, 'nvi', 'Ezequiel', 'ez', 17, 18, 'Ele desprezou o juramento quando rompeu o tratado. Como deu a mão em penhor e fez todas essas coisas, de modo algum escapará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20845, 'nvi', 'Ezequiel', 'ez', 17, 19, '" ‘Por isso assim diz o Soberano Senhor: Juro pela minha vida que farei cair sobre a cabeça dele o meu juramento que ele desprezou e a minha aliança que ele rompeu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20846, 'nvi', 'Ezequiel', 'ez', 17, 20, 'Estenderei sobre ele a minha rede, e ele será pego em meu laço. Eu o levarei para a Babilônia e ali executarei juízo sobre ele porque me foi infiel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20847, 'nvi', 'Ezequiel', 'ez', 17, 21, 'Todas as suas tropas em fuga cairão pela espada, e os sobreviventes serão espalhados aos ventos. Então vocês saberão que eu, o Senhor, falei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20848, 'nvi', 'Ezequiel', 'ez', 17, 22, '" ‘Assim diz o Soberano Senhor: Eu mesmo apanharei um broto bem do alto de um cedro e o plantarei; arrancarei um renovo tenro de seus ramos mais altos e o plantarei num monte alto e imponente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20849, 'nvi', 'Ezequiel', 'ez', 17, 23, 'Nos montes altos de Israel eu o plantarei; ele produzirá galhos e dará fruto e se tornará um cedro viçoso. Pássaros de todo tipo se aninharão nele; encontrarão abrigo à sombra de seus galhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20850, 'nvi', 'Ezequiel', 'ez', 17, 24, 'Todas as árvores do campo saberão que eu, o Senhor, faço cair a árvore alta e faço crescer bem alto a árvore baixa. Eu resseco a árvore verde e faço florescer a árvore seca. " ‘Eu o Senhor falei, e eu o farei’ ".');
