INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20851, 'nvi', 'Ezequiel', 'ez', 18, 1, 'Esta palavra do Senhor veio a mim:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20852, 'nvi', 'Ezequiel', 'ez', 18, 2, '"Que é que vocês querem dizer quando citam este provérbio sobre Israel: " ‘Os pais comem uvas verdes, e os dentes dos filhos se embotam’?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20853, 'nvi', 'Ezequiel', 'ez', 18, 3, '"Juro pela minha vida, palavra do Soberano Senhor, que vocês não citarão mais esse provérbio em Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20854, 'nvi', 'Ezequiel', 'ez', 18, 4, 'Pois todos me pertencem. Tanto o pai como o filho me pertencem. Aquele que pecar é que morrerá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20855, 'nvi', 'Ezequiel', 'ez', 18, 5, '"Suponhamos que haja um certo justo que faz o que é certo e direito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20856, 'nvi', 'Ezequiel', 'ez', 18, 6, 'Ele não come nos santuários que há nos montes e nem olha para os ídolos da nação de Israel. Ele não contamina a mulher do próximo nem se deita com uma mulher durante os seus dias de fluxo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20857, 'nvi', 'Ezequiel', 'ez', 18, 7, 'Ele não oprime a ninguém, mas devolve o que tomou como garantia num empréstimo. Não comete roubos, mas dá a comida aos famintos e fornece roupas para os despidos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20858, 'nvi', 'Ezequiel', 'ez', 18, 8, 'Ele não empresta visando lucro nem cobra juros. Ele retém a sua mão para não cometer erro e julga com justiça entre dois homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20859, 'nvi', 'Ezequiel', 'ez', 18, 9, 'Ele age segundo os meus decretos e obedece fielmente às minhas leis. Aquele homem é justo; com certeza ele viverá, palavra do Soberano Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20860, 'nvi', 'Ezequiel', 'ez', 18, 10, '"Suponhamos que ele tenha um filho violento, que derrama sangue ou faz qualquer uma dessas outras coisas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20861, 'nvi', 'Ezequiel', 'ez', 18, 11, 'embora o pai não tenha feito nenhuma delas: "Ele come nos santuários que há nos montes. Contamina a mulher do próximo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20862, 'nvi', 'Ezequiel', 'ez', 18, 12, 'Oprime os pobres e os necessitados. Comete roubos. Não devolve o que tomou como garantia. Volta-se para os ídolos, comete práticas detestáveis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20863, 'nvi', 'Ezequiel', 'ez', 18, 13, 'Empresta visando lucro e cobra juros. Haverá de viver um homem desses? Não! Por todas essas práticas detestáveis, com certeza será morto, e ele será o culpado por sua própria morte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20864, 'nvi', 'Ezequiel', 'ez', 18, 14, '"Mas suponhamos que esse filho tenha ele mesmo um filho que vê todos os pecados que seu pai comete e, embora os veja, não os comete.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20865, 'nvi', 'Ezequiel', 'ez', 18, 15, '"Ele não come nos santuários que há nos montes e nem olha para os ídolos da nação de Israel. Não contamina a mulher do próximo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20866, 'nvi', 'Ezequiel', 'ez', 18, 16, 'Ele não oprime a ninguém, nem exige garantia para um empréstimo. Não comete roubos, mas dá comida aos famintos e fornece roupas aos despidos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20867, 'nvi', 'Ezequiel', 'ez', 18, 17, 'Ele retém a mão para não pecar e não empresta visando lucro nem cobra juros. Guarda as minhas leis e age segundo os meus decretos. Ele não morrerá por causa da iniqüidade do seu pai; certamente viverá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20868, 'nvi', 'Ezequiel', 'ez', 18, 18, 'Mas seu pai morrerá por causa de sua própria iniqüidade, pois praticou extorsão, roubou seu compatriota e fez o que era errado no meio de seu povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20869, 'nvi', 'Ezequiel', 'ez', 18, 19, '"Contudo, vocês perguntam: ‘Por que o filho não partilha da culpa de seu pai? ’ Uma vez que o filho fez o que é justo e direito e teve o cuidado de guardar todos os meus decretos, com certeza ele viverá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20870, 'nvi', 'Ezequiel', 'ez', 18, 20, 'Aquele que pecar é que morrerá. O filho não levará a culpa do pai, nem o pai levará a culpa do filho. A justiça do justo lhe será creditada, e a impiedade do ímpio lhe será cobrada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20871, 'nvi', 'Ezequiel', 'ez', 18, 21, '"Mas, se um ímpio se desviar de todos os pecados que cometeu e obedecer a todos os meus decretos e fizer o que é justo e direito, com certeza viverá; não morrerá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20872, 'nvi', 'Ezequiel', 'ez', 18, 22, 'Não se terá lembrança de nenhuma das ofensas que cometeu. Devido às coisas justas que tiver feito, ele viverá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20873, 'nvi', 'Ezequiel', 'ez', 18, 23, 'Teria eu algum prazer na morte do ímpio?, palavra do Soberano Senhor. Pelo contrário, acaso não me agrada vê-lo desviar-se dos seus caminhos e viver?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20874, 'nvi', 'Ezequiel', 'ez', 18, 24, '"Se, porém, um justo se desviar de sua justiça, e cometer pecado e as mesmas práticas detestáveis dos ímpios, deverá ele viver? Nenhuma das coisas justas que fez será lembrada! Por causa da infidelidade de que é culpado e por causa dos pecados que cometeu, ele morrerá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20875, 'nvi', 'Ezequiel', 'ez', 18, 25, '"Contudo, vocês dizem: ‘O caminho do Senhor não é justo’. Ouça, ó nação de Israel: O meu caminho é injusto? Não são os seus caminhos que são injustos?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20876, 'nvi', 'Ezequiel', 'ez', 18, 26, 'Se um justo desviar-se de sua justiça e cometer pecado, ele morrerá por causa disso; por causa do pecado que cometeu morrerá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20877, 'nvi', 'Ezequiel', 'ez', 18, 27, 'Mas, se um ímpio se desviar de sua maldade e fizer o que é justo e direito, ele salvará sua vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20878, 'nvi', 'Ezequiel', 'ez', 18, 28, 'Por considerar todas as ofensas que cometeu e se desviar delas, ele com certeza viverá; não morrerá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20879, 'nvi', 'Ezequiel', 'ez', 18, 29, 'Contudo, a nação de Israel diz: ‘O caminho do Senhor não é justo’. São injustos os meus caminhos, ó nação de Israel? Não são os seus caminhos que são injustos?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20880, 'nvi', 'Ezequiel', 'ez', 18, 30, '"Portanto, ó nação de Israel, eu os julgarei, a cada um de acordo com os seus caminhos; palavra do Soberano Senhor. Arrependam-se! Desviem-se de todos os seus males, para que o pecado não cause a queda de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20881, 'nvi', 'Ezequiel', 'ez', 18, 31, 'Livrem-se de todos os males que vocês cometeram, e busquem um coração novo e um espírito novo. Por que deveriam morrer, ó nação de Israel?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20882, 'nvi', 'Ezequiel', 'ez', 18, 32, 'Pois não me agrada a morte de ninguém; palavra do Soberano Senhor. Arrependam-se e vivam!');
