INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20946, 'nvi', 'Ezequiel', 'ez', 21, 1, 'Esta palavra do Senhor veio a mim:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20947, 'nvi', 'Ezequiel', 'ez', 21, 2, '"Filho do homem, vire o rosto contra Jerusalém e pregue contra o santuário. Profetize contra Israel,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20948, 'nvi', 'Ezequiel', 'ez', 21, 3, 'dizendo-lhe: ‘Assim diz o Senhor: Estou contra você. Empunharei a minha espada para eliminar tanto o justo quanto o ímpio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20949, 'nvi', 'Ezequiel', 'ez', 21, 4, 'Uma vez que eu vou eliminar o justo e o ímpio, estarei empunhando a minha espada contra todos, desde o Neguebe até o norte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20950, 'nvi', 'Ezequiel', 'ez', 21, 5, 'Então todos saberão que eu, o Senhor, da bainha tirei a espada e não tornarei a guardá-la’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20951, 'nvi', 'Ezequiel', 'ez', 21, 6, '"Portanto, passe a gemer, filho do homem! Passe a gemer diante deles com o coração partido e com amarga tristeza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20952, 'nvi', 'Ezequiel', 'ez', 21, 7, 'E, quando lhe perguntarem: ‘Por que você está gemendo? ’, você dirá: ‘Por causa das notícias que estão vindo. Todo coração se derreterá, e toda mão penderá frouxa; todo espírito desmaiará, e todo joelho se tornará como água, de tão fraco’. E vem chegando! Sem nenhuma dúvida vai acontecer; palavra do Soberano Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20953, 'nvi', 'Ezequiel', 'ez', 21, 8, 'Esta palavra do Senhor veio a mim:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20954, 'nvi', 'Ezequiel', 'ez', 21, 9, '"Filho do homem, profetize e diga: ‘Assim diz o Senhor: " ‘Uma espada, uma espada, afiada e polida;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20955, 'nvi', 'Ezequiel', 'ez', 21, 10, 'afiada para a mortandade, polida para luzir como relâmpago! " ‘Acaso vamos regozijar-nos com o cetro do meu filho Judá? A espada despreza toda e qualquer vareta como essa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20956, 'nvi', 'Ezequiel', 'ez', 21, 11, '" ‘A espada foi destinada a ser polida, a ser pega com as mãos; está afiada e polida, preparada para que a maneje a mão do matador.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20957, 'nvi', 'Ezequiel', 'ez', 21, 12, 'Clame e grite, filho do homem, pois ela está contra o meu povo; está contra todos os príncipes de Israel. Eles são atirados contra a espada juntamente com o meu povo. Lamente-se, pois; bata no peito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20958, 'nvi', 'Ezequiel', 'ez', 21, 13, '" ‘É certo que a prova virá. E que acontecerá, se o cetro de Judá, que a espada despreza, não continuar a existir?; palavra do Soberano Senhor’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20959, 'nvi', 'Ezequiel', 'ez', 21, 14, '"Por isso profetize, então, filho do homem, e bata as mãos uma na outra. Que a espada golpeie duas vezes, aliás, três vezes. É uma espada para matança, para grande matança, avançando sobre eles de todos os lados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20960, 'nvi', 'Ezequiel', 'ez', 21, 15, 'Assim, para que os corações se derretam e os caídos sejam muitos, posicionei a espada para a matança junto a todas as suas portas. Ah! Ela foi feita para luzir como relâmpago; é empunhada firmemente para a matança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20961, 'nvi', 'Ezequiel', 'ez', 21, 16, 'Ó espada, golpeie para todos os lados, para onde quer que se vire a sua lâmina.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20962, 'nvi', 'Ezequiel', 'ez', 21, 17, 'Eu também baterei minhas mãos uma na outra, e a minha ira diminuirá. Eu, o Senhor, falei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20963, 'nvi', 'Ezequiel', 'ez', 21, 18, 'A palavra do Senhor veio a mim:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20964, 'nvi', 'Ezequiel', 'ez', 21, 19, '"Filho do homem, trace as duas estradas que a espada do rei da Babilônia deve tomar, as duas partindo da mesma terra. Em cada uma delas coloque um marco indicando o rumo de uma cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20965, 'nvi', 'Ezequiel', 'ez', 21, 20, 'Trace uma estrada que leve a espada contra Rabá dos amonitas, e a outra contra Judá e contra a Jerusalém fortificada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20966, 'nvi', 'Ezequiel', 'ez', 21, 21, 'Pois o rei da Babilônia parará no local donde partem as duas estradas para sortear a escolha. Ele lançará a sorte com flechas, consultará os ídolos da família, examinará o fígado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20967, 'nvi', 'Ezequiel', 'ez', 21, 22, 'Pela sua mão direita será sorteada Jerusalém, onde deverá preparar aríetes, dar ordens para a matança, soar o grito de guerra, montar aríetes contra as portas, construir uma rampa e levantar obras de cerco.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20968, 'nvi', 'Ezequiel', 'ez', 21, 23, 'Parecerá um falso presságio aos judeus, que tinham feito uma aliança com juramento, mas o rei invasor os fará recordar sua culpa e os levará prisioneiros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20969, 'nvi', 'Ezequiel', 'ez', 21, 24, '"Portanto, assim diz o Soberano Senhor: ‘Visto que vocês trouxeram à lembrança a sua iniqüidade mediante rebelião ostensiva, revelando seus pecados em tudo o que fazem; porque vocês fizeram isso, serão levados prisioneiros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20970, 'nvi', 'Ezequiel', 'ez', 21, 25, '" ‘Ó ímpio e profano príncipe de Israel, o seu dia chegou, a hora do seu castigo é agora,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20971, 'nvi', 'Ezequiel', 'ez', 21, 26, 'assim diz o Soberano Senhor: Tire o turbante e a coroa. Não será como antes: Os humildes serão exaltados, e os exaltados serão humilhados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20972, 'nvi', 'Ezequiel', 'ez', 21, 27, 'Uma desgraça! Uma desgraça! Eu a farei uma desgraça! Não será restaurada, enquanto não vier aquele a quem ela pertence por direito; a ele eu a darei’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20973, 'nvi', 'Ezequiel', 'ez', 21, 28, '"E você, filho do homem, profetize e diga: ‘Assim diz o Soberano Senhor acerca dos amonitas e dos seus insultos: " ‘Uma espada, uma espada, empunhada para matança, polida para consumir e para luzir como relâmpago!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20974, 'nvi', 'Ezequiel', 'ez', 21, 29, 'A despeito das visões falsas e das adivinhações mentirosas sobre você, ela será posta sobre o pescoço dos ímpios que devem ser mortos e cujo dia chegou, cujo momento de castigo é agora.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20975, 'nvi', 'Ezequiel', 'ez', 21, 30, 'Volte a espada à sua bainha. No lugar onde vocês foram criados, na terra dos seus antepassados, eu os julgarei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20976, 'nvi', 'Ezequiel', 'ez', 21, 31, 'Derramarei a minha ira sobre vocês, soprarei a minha ira impetuosa contra vocês; eu os entregarei nas mãos de homens brutais, acostumados à destruição.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20977, 'nvi', 'Ezequiel', 'ez', 21, 32, 'Vocês serão combustível para o fogo, seu sangue será derramado em sua terra e você não será mais lembrado; porque eu o Senhor falei’ ".');
