INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20978, 'nvi', 'Ezequiel', 'ez', 22, 1, 'Veio a mim esta palavra do Senhor:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20979, 'nvi', 'Ezequiel', 'ez', 22, 2, '"Filho do homem, você a julgará? Você julgará essa cidade sanguinária? Então confronte-a com todas as suas práticas repugnantes');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20980, 'nvi', 'Ezequiel', 'ez', 22, 3, 'e diga: ‘Assim diz o Soberano Senhor: Ó cidade que traz condenação sobre si mesma por derramar sangue em seu meio e por se contaminar fazendo ídolos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20981, 'nvi', 'Ezequiel', 'ez', 22, 4, 'Você se tornou culpada por causa do sangue que derramou e por ter se contaminado com os ídolos que fez. Você deu cabo dos seus dias, e chegou o fim dos seus anos. Por isso farei de você objeto de zombaria para as nações e motivo de escárnio em todas as terras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20982, 'nvi', 'Ezequiel', 'ez', 22, 5, 'Tanto as nações vizinhas como as distantes zombarão de você, ó cidade infame e inquieta!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20983, 'nvi', 'Ezequiel', 'ez', 22, 6, '" ‘Veja como cada um dos príncipes de Israel que aí estão usa o seu poder para derramar sangue.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20984, 'nvi', 'Ezequiel', 'ez', 22, 7, 'Em seu meio eles têm tratado pai e mãe com desprezo, e têm oprimido o estrangeiro e maltratado o órfão e a viúva.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20985, 'nvi', 'Ezequiel', 'ez', 22, 8, 'Você desprezou as minhas dádivas sagradas e profanou os meus sábados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20986, 'nvi', 'Ezequiel', 'ez', 22, 9, 'Em seu meio há caluniadores, prontas para derramar sangue; em seu meio há os que comem nos santuários dos montes e praticam atos lascivos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20987, 'nvi', 'Ezequiel', 'ez', 22, 10, 'Em seu meio há aqueles que desonram a cama dos seus pais, e também há aqueles que têm relações com as mulheres nos dias de sua menstruação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20988, 'nvi', 'Ezequiel', 'ez', 22, 11, 'Um homem comete adultério com a mulher do seu próximo, outro contamina vergonhosamente a sua nora, e outro desonra a sua irmã, filha de seu próprio pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20989, 'nvi', 'Ezequiel', 'ez', 22, 12, 'Em seu meio há homens que aceitam suborno para derramar sangue; você empresta visando lucro e a juros e obtém ganhos injustos, extorquindo o próximo. E você se esqueceu de mim; palavra do Soberano Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20990, 'nvi', 'Ezequiel', 'ez', 22, 13, '" ‘Mas você me verá bater as minhas mãos uma na outra contra os ganhos injustos que você obteve e contra o sangue que você derramou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20991, 'nvi', 'Ezequiel', 'ez', 22, 14, 'Será que a sua coragem suportará ou as suas mãos serão fortes para o que eu vou fazer no dia em que eu tratar com você? Eu, o Senhor, falei, e o farei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20992, 'nvi', 'Ezequiel', 'ez', 22, 15, 'Dispersarei você entre as nações e a espalharei pelas terras; e darei fim à sua impureza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20993, 'nvi', 'Ezequiel', 'ez', 22, 16, 'Quando você tiver sido desonrada aos olhos das nações, você saberá que eu sou o Senhor’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20994, 'nvi', 'Ezequiel', 'ez', 22, 17, 'E depois veio a mim esta palavra do Senhor:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20995, 'nvi', 'Ezequiel', 'ez', 22, 18, '"Filho do homem, a nação de Israel tornou-se escória para mim; cobre, estanho, ferro e chumbo deixados na fornalha. Não passa de escória de prata.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20996, 'nvi', 'Ezequiel', 'ez', 22, 19, 'Por isso assim diz o Soberano Senhor: ‘Visto que vocês todos se tornaram escória, eu os ajuntarei em Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20997, 'nvi', 'Ezequiel', 'ez', 22, 20, 'Assim como os homens ajuntam prata, cobre, ferro, chumbo e estanho numa fornalha a fim de fundi-los com um sopro, também na minha ira e na minha indignação ajuntarei vocês dentro da cidade e os fundirei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20998, 'nvi', 'Ezequiel', 'ez', 22, 21, 'Eu os ajuntarei e soprarei sobre vocês a minha ira impetuosa, e vocês se derreterão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20999, 'nvi', 'Ezequiel', 'ez', 22, 22, 'Assim como a prata se derrete numa fornalha, também vocês se derreterão dentro dela, e vocês saberão que eu, o Senhor, derramei a minha ira sobre vocês’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21000, 'nvi', 'Ezequiel', 'ez', 22, 23, 'De novo a palavra do Senhor veio a mim. Disse ele:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21001, 'nvi', 'Ezequiel', 'ez', 22, 24, '"Filho do homem, diga a esta terra: ‘Você é uma terra que não tem tido chuva nem aguaceiros no dia da ira’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21002, 'nvi', 'Ezequiel', 'ez', 22, 25, 'Há nela uma conspiração de seus príncipes como um leão que ruge ao despedaçar sua presa; devoram pessoas, apanham tesouros e coisas preciosas e fazem muitas viúvas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21003, 'nvi', 'Ezequiel', 'ez', 22, 26, 'Seus sacerdotes cometem violência contra a minha lei e profanam minhas ofertas sagradas; não fazem distinção entre o sagrado e o comum; ensinam que não existe nenhuma diferença entre o puro e o impuro; e fecham os olhos quanto à guarda dos meus sábados, de maneira que sou desonrado no meio deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21004, 'nvi', 'Ezequiel', 'ez', 22, 27, 'Seus oficiais são como lobos que despedaçam suas presas; derramam sangue e matam gente para obter ganhos injustos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21005, 'nvi', 'Ezequiel', 'ez', 22, 28, 'Seus profetas disfarçam esses feitos enganando o povo com visões falsas e adivinhações mentirosas. Dizem: ‘Assim diz o Soberano Senhor’, quando o Senhor não falou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21006, 'nvi', 'Ezequiel', 'ez', 22, 29, 'O povo da terra pratica extorsão e comete roubos; oprime os pobres e os necessitados e maltrata os estrangeiros, negando-lhes justiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21007, 'nvi', 'Ezequiel', 'ez', 22, 30, '"Procurei entre eles um homem que erguesse o muro e se pusesse na brecha diante de mim e em favor da terra, para que eu não a destruísse, mas não encontrei nem um só.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21008, 'nvi', 'Ezequiel', 'ez', 22, 31, 'Por isso derramarei a minha ira sobre eles e os consumirei com o meu grande furor; sofrerão as conseqüências de tudo o que eles fizeram, palavra do Soberano Senhor".');
