INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21009, 'nvi', 'Ezequiel', 'ez', 23, 1, 'Esta palavra do Senhor veio a mim:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21010, 'nvi', 'Ezequiel', 'ez', 23, 2, '"Filho do homem, existiam duas mulheres, filhas da mesma mãe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21011, 'nvi', 'Ezequiel', 'ez', 23, 3, 'Elas se tornaram prostitutas no Egito, envolvendo-se na prostituição desde a juventude. Naquela terra os seus peitos foram acariciados e os seus seios virgens foram afagados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21012, 'nvi', 'Ezequiel', 'ez', 23, 4, 'A mais velha chamava-se Oolá, e sua irmã, Oolibá. Elas eram minhas e deram à luz filhos e filhas. Oolá é Samaria, e Oolibá é Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21013, 'nvi', 'Ezequiel', 'ez', 23, 5, '"Oolá envolveu-se em prostituição enquanto ainda era minha; ela se encheu de cobiça por seus amantes, os assírios, guerreiros');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21014, 'nvi', 'Ezequiel', 'ez', 23, 6, 'vestidos de vermelho, governadores e comandantes, todos eles cavaleiros jovens e elegantes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21015, 'nvi', 'Ezequiel', 'ez', 23, 7, 'Ela se entregou como prostituta a toda a elite dos assírios e contaminou-se com todos os ídolos de cada homem por ela cobiçado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21016, 'nvi', 'Ezequiel', 'ez', 23, 8, 'Ela não abandonou a prostituição iniciada no Egito, quando em sua juventude homens dormiram com ela, afagaram seus seios virgens e a envolveram em suas práticas dissolutas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21017, 'nvi', 'Ezequiel', 'ez', 23, 9, '"Por isso eu a entreguei nas mãos de seus amantes, os assírios, os quais ela desejou ardentemente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21018, 'nvi', 'Ezequiel', 'ez', 23, 10, 'Eles lhe arrancaram as roupas, deixando-a nua, levaram embora seus filhos e suas filhas e a mataram com à espada. Ela se tornou um provérbio entre as mulheres. Foi-lhe infligido castigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21019, 'nvi', 'Ezequiel', 'ez', 23, 11, '"Sua irmã Oolibá viu isso. No entanto, em sua cobiça e prostituição, ela foi mais depravada do que a irmã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21020, 'nvi', 'Ezequiel', 'ez', 23, 12, 'Também desejou ardentemente os assírios, governadores e comandantes, guerreiros em uniforme completo, todos eles jovens e belos cavaleiros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21021, 'nvi', 'Ezequiel', 'ez', 23, 13, 'Vi que ela também se contaminou; ambas seguiram o mesmo caminho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21022, 'nvi', 'Ezequiel', 'ez', 23, 14, '"Mas ela levou sua prostituição ainda mais longe. Viu homens desenhados numa parede, figuras de caldeus em vermelho,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21023, 'nvi', 'Ezequiel', 'ez', 23, 15, 'usando cinturões e esvoaçantes turbantes na cabeça; todos se pareciam com oficiais que chefiam os carros da Babilônia, nativos da Caldéia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21024, 'nvi', 'Ezequiel', 'ez', 23, 16, 'Assim que ela os viu, desejou-os ardentemente e lhes mandou mensageiros até a Caldéia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21025, 'nvi', 'Ezequiel', 'ez', 23, 17, 'Então os babilônios vieram procurá-la, até a cama do amor, e em sua cobiça a contaminaram. Depois de haver sido contaminada por eles, ela se afastou deles desgostosa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21026, 'nvi', 'Ezequiel', 'ez', 23, 18, 'Ela, então, prosseguiu abertamente em sua prostituição e expôs a sua nudez, e eu me afastei dela desgostoso, assim como eu tinha me afastado de sua irmã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21027, 'nvi', 'Ezequiel', 'ez', 23, 19, 'Contudo, ela ia se tornando cada vez mais promíscua à medida que se recordava dos dias de sua juventude, quando era prostituta no Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21028, 'nvi', 'Ezequiel', 'ez', 23, 20, 'Desejou ardentemente os seus amantes, cujos membros eram como os de jumentos e cuja ejaculação era como a de cavalos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21029, 'nvi', 'Ezequiel', 'ez', 23, 21, 'De modo que você ansiou pela lascívia de sua juventude, quando no Egito seus peitos eram afagados e seus seios virgens eram acariciados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21030, 'nvi', 'Ezequiel', 'ez', 23, 22, '"Portanto, Oolibá, assim diz o Soberano Senhor: Incitarei os seus amantes contra você, aqueles de quem você se afastou desgostosa, e os trarei para atacá-la de todos os lados,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21031, 'nvi', 'Ezequiel', 'ez', 23, 23, 'os babilônios e todos os caldeus, os homens de Pecode, de Soa e de Coa, e com eles todos os assírios, belos rapazes, todos eles governadores e comandantes, oficiais que chefiam os carros e homens de posto elevado, todos galantes cavaleiros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21032, 'nvi', 'Ezequiel', 'ez', 23, 24, 'Eles virão contra você com armas, carros e carroças e com uma multidão de povos; por todos os lados tomarão posição contra você com escudos grandes e pequenos e com capacetes. Eu a entregarei a eles para castigo, e eles a castigarão conforme o costume deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21033, 'nvi', 'Ezequiel', 'ez', 23, 25, 'Dirigirei contra você a ira do meu ciúme, e, enfurecidos, eles saberão como tratá-la. Cortarão fora o seu nariz e as suas orelhas, e os que forem deixados cairão pela espada. Levarão embora seus filhos e suas filhas, e os que forem deixados serão consumidos pelo fogo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21034, 'nvi', 'Ezequiel', 'ez', 23, 26, 'Também arrancarão as suas roupas e tomarão suas lindas jóias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21035, 'nvi', 'Ezequiel', 'ez', 23, 27, 'Assim darei um basta à lascívia e à prostituição que você começou no Egito. Você deixará de olhar com desejo para essas coisas e não se lembrará mais do Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21036, 'nvi', 'Ezequiel', 'ez', 23, 28, '"Pois assim diz o Soberano Senhor: Estou a ponto de entregá-la nas mãos daqueles que você odeia, daqueles de quem você se afastou desgostosa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21037, 'nvi', 'Ezequiel', 'ez', 23, 29, 'Eles a tratarão com ódio e levarão embora tudo aquilo pelo que você trabalhou. Eles a deixarão despida e nua, e a vergonha de sua prostituição será exposta. Sua lascívia e sua promiscuidade');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21038, 'nvi', 'Ezequiel', 'ez', 23, 30, 'trouxeram isto sobre você, porque você desejou ardentemente as nações e se contaminou com os ídolos delas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21039, 'nvi', 'Ezequiel', 'ez', 23, 31, 'Você seguiu pelo caminho de sua irmã; por essa razão porei o copo dela nas suas mãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21040, 'nvi', 'Ezequiel', 'ez', 23, 32, '"Assim diz o Soberano Senhor: "Você beberá do copo de sua irmã, copo grande e fundo; ele causará riso e zombaria, de tão grande que é.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21041, 'nvi', 'Ezequiel', 'ez', 23, 33, 'Você será dominada pela embriaguez e pela tristeza, com esse copo de desgraça e desolação, o copo de sua irmã Samaria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21042, 'nvi', 'Ezequiel', 'ez', 23, 34, 'Você o beberá, engolindo até a última gota; depois você o despedaçará e mutilará os próprios seios. "Eu o disse: Palavra do Soberano, do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21043, 'nvi', 'Ezequiel', 'ez', 23, 35, '"Agora, assim diz o Soberano Senhor: Visto que você se esqueceu de mim e me deu as costas, você vai sofrer as conseqüências de sua lascívia e de sua prostituição".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21044, 'nvi', 'Ezequiel', 'ez', 23, 36, 'O Senhor me disse: "Filho do homem, você julgará Oolá e Oolibá? Então confronte-as com suas práticas repugnantes,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21045, 'nvi', 'Ezequiel', 'ez', 23, 37, 'pois elas cometeram adultério e há sangue em suas mãos. Cometeram adultério com seus ídolos; até os seus filhos, os quais elas geraram para mim, sacrificaram aos ídolos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21046, 'nvi', 'Ezequiel', 'ez', 23, 38, 'Também me fizeram isto: ao mesmo tempo contaminaram o meu santuário e profanaram os meus sábados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21047, 'nvi', 'Ezequiel', 'ez', 23, 39, 'No mesmo dia em que sacrificavam seus filhos a seus ídolos, elas entravam em meu santuário e o profanavam. Foi isso que elas fizeram em minha casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21048, 'nvi', 'Ezequiel', 'ez', 23, 40, '"Elas até enviaram mensageiros atrás de homens, que vieram de bem longe, e, quando eles chegaram, você se banhou para recebê-los, pintou os olhos e pôs suas jóias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21049, 'nvi', 'Ezequiel', 'ez', 23, 41, 'Você se sentou num belo sofá, tendo à frente uma mesa, na qual você havia colocado o incenso e o óleo que me pertenciam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21050, 'nvi', 'Ezequiel', 'ez', 23, 42, '"O ruído de uma multidão despreocupada estava em torno dela; sabeus foram trazidos do deserto junto com homens do povo, e eles puseram braceletes nos braços da mulher e da sua irmã e belíssimas coroas na cabeças delas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21051, 'nvi', 'Ezequiel', 'ez', 23, 43, 'Então eu disse a respeito daquela que fora destruída pelo adultério: ‘Que agora a usem como prostituta, pois isso é tudo o que ela é’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21052, 'nvi', 'Ezequiel', 'ez', 23, 44, 'E eles dormiram com ela. Dormiram com aquelas mulheres lascivas, Oolá e Oolibá, como quem dorme com uma prostituta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21053, 'nvi', 'Ezequiel', 'ez', 23, 45, 'Mas justos as condenarão ao castigo que merecem as mulheres que cometem adultério e derramam sangue, porque são adúlteras e há sangue em suas mãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21054, 'nvi', 'Ezequiel', 'ez', 23, 46, '"Assim diz o Soberano Senhor: Que uma multidão as ataque e que elas sejam entregues ao pavor e ao saque.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21055, 'nvi', 'Ezequiel', 'ez', 23, 47, 'A multidão as apedrejará e as retalhará à espada; matarão seus filhos e suas filhas e destruirão suas casas, incendiando-as.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21056, 'nvi', 'Ezequiel', 'ez', 23, 48, '"Dessa maneira darei fim à lascívia na terra, para que todas as mulheres fiquem advertidas e não imitem vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21057, 'nvi', 'Ezequiel', 'ez', 23, 49, 'Vocês sofrerão o castigo de sua cobiça e as conseqüências de seus pecados de idolatria. E vocês saberão que eu sou o Soberano Senhor".');
