INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21102, 'nvi', 'Ezequiel', 'ez', 26, 1, 'No décimo primeiro ano, no primeiro dia do mês, veio a mim esta palavra do Senhor:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21103, 'nvi', 'Ezequiel', 'ez', 26, 2, '"Filho do homem, visto que Tiro falou de Jerusalém: ‘Ah! Ah! O portal das nações está quebrado, e as suas portas se me abriram; agora que ela jaz em ruínas, eu prosperarei’,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21104, 'nvi', 'Ezequiel', 'ez', 26, 3, 'por essa razão assim diz o Soberano Senhor: Estou contra você, ó Tiro, e trarei muitas nações contra você; virão como o mar quando eleva as suas ondas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21105, 'nvi', 'Ezequiel', 'ez', 26, 4, 'Elas destruirão os muros de Tiro e derrubarão suas torres; eu espalharei o seu entulho e farei dela uma rocha nua.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21106, 'nvi', 'Ezequiel', 'ez', 26, 5, 'Fora, no mar, ela se tornará um local propício para estender redes de pesca, pois eu falei, palavra do Soberano Senhor. Ela se tornará despojo para as nações,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21107, 'nvi', 'Ezequiel', 'ez', 26, 6, 'e em seus territórios no continente será feita grande destruição pela espada. E saberão que eu sou o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21108, 'nvi', 'Ezequiel', 'ez', 26, 7, '"Pois assim diz o Soberano Senhor: Do norte vou trazer contra você, Tiro, o rei da Babilônia, Nabucodonosor, rei de reis, com cavalos e carros, com cavaleiros e um grande exército.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21109, 'nvi', 'Ezequiel', 'ez', 26, 8, 'Ele desfechará com a espada um violento ataque contra os seus territórios no continente. Construirá obras de cerco e uma rampa de acesso aos seus muros. E armará uma barreira de escudos contra você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21110, 'nvi', 'Ezequiel', 'ez', 26, 9, 'Ele dirigirá as investidas dos seus aríetes contra os seus muros e com armas de ferro demolirá as suas torres.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21111, 'nvi', 'Ezequiel', 'ez', 26, 10, 'Seus cavalos serão tantos que cobrirão você de poeira. Seus muros tremerão com o barulho dos cavalos de guerra, das carroças e dos carros, quando ele entrar por suas portas com a facilidade com que se entra numa cidade cujos muros foram derrubados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21112, 'nvi', 'Ezequiel', 'ez', 26, 11, 'Os cascos de seus cavalos pisarão em todas as suas ruas; ele matará o seu povo à espada, e as suas resistentes colunas ruirão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21113, 'nvi', 'Ezequiel', 'ez', 26, 12, 'Despojarão a sua riqueza e saquearão os seus suprimentos; derrubarão seus muros e demolirão suas lindas casas, e lançarão as suas pedras, o seu madeiramento e todo o entulho ao mar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21114, 'nvi', 'Ezequiel', 'ez', 26, 13, 'Porei fim a seus cânticos barulhentos, e não se ouvirá mais a música de suas harpas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21115, 'nvi', 'Ezequiel', 'ez', 26, 14, 'Farei de você uma rocha nua, e você se tornará um local propício para estender redes de pesca. Você jamais será reconstruída, pois eu, o Senhor, falei, palavra do Soberano Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21116, 'nvi', 'Ezequiel', 'ez', 26, 15, '"Assim diz o Soberano Senhor a Tiro: Acaso as regiões litorâneas não tremerão ao som de sua queda, quando o ferido gemer e a matança acontecer em seu meio?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21117, 'nvi', 'Ezequiel', 'ez', 26, 16, 'Então todos os príncipes do litoral descerão do trono e porão de lado seus mantos e tirarão suas roupas bordadas. Vestidos de pavor, se assentarão no chão, tremendo a todo instante, apavorados por sua causa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21118, 'nvi', 'Ezequiel', 'ez', 26, 17, 'Depois entoarão um lamento acerca de você e lhe dirão: " ‘Como você está destruída, ó cidade de renome, povoada por homens do mar! Você era um poder nos mares, você e os seus cidadãos; você impunha pavor a todos que ali vivem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21119, 'nvi', 'Ezequiel', 'ez', 26, 18, 'Agora as regiões litorâneas tremem no dia de sua queda; as ilhas do mar estão apavoradas diante de sua ruína’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21120, 'nvi', 'Ezequiel', 'ez', 26, 19, '"Assim diz o Soberano Senhor: Quando eu fizer de você uma cidade abandonada, como uma cidade inabitável, e quando eu a cobrir com as vastas águas do abismo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21121, 'nvi', 'Ezequiel', 'ez', 26, 20, 'então farei você descer com os que descem à cova, para fazer companhia aos antigos. Eu a farei habitar embaixo da terra, como em ruínas antigas, com aqueles que descem à cova, e você não voltará e não retomará o seu lugar na terra dos viventes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21122, 'nvi', 'Ezequiel', 'ez', 26, 21, 'Levarei você a um fim terrível e você já não existirá. Você será procurada, e jamais será achada, palavra do Soberano Senhor".');
