INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21123, 'nvi', 'Ezequiel', 'ez', 27, 1, 'Esta palavra do Senhor veio a mim:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21124, 'nvi', 'Ezequiel', 'ez', 27, 2, '"Filho do homem, faça um lamento a respeito de Tiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21125, 'nvi', 'Ezequiel', 'ez', 27, 3, 'Diga a Tiro, que está junto à entrada para o mar, mercadora de povos em muitos litorais: ‘Assim diz o Soberano Senhor: " ‘Você diz, ó Tiro: "Minha beleza é perfeita".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21126, 'nvi', 'Ezequiel', 'ez', 27, 4, 'Seu domínio abrangia o coração dos mares; seus construtores levaram a sua beleza à perfeição.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21127, 'nvi', 'Ezequiel', 'ez', 27, 5, 'Eles fizeram todo o seu madeiramento com pinheiros de Senir; apanharam um cedro do Líbano para fazer-lhe um mastro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21128, 'nvi', 'Ezequiel', 'ez', 27, 6, 'Dos carvalhos de Basã fizeram os seus remos; de cipreste procedente das costas de Chipre fizeram seu convés, revestido de mármore.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21129, 'nvi', 'Ezequiel', 'ez', 27, 7, 'De belo linho bordado, procedente do Egito foram feitas as suas velas e lhe serviu de bandeira; seus toldos, em vermelho e azul, provinham das costas de Elisá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21130, 'nvi', 'Ezequiel', 'ez', 27, 8, 'Habitantes de Sidom e Arvade eram os seus remadores; os seus homens hábeis, ó Tiro, estavam a bordo na qualidade de marinheiros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21131, 'nvi', 'Ezequiel', 'ez', 27, 9, 'Artesãos veteranos de Gebal estavam a bordo como construtores de barcos para calafetarem as suas juntas. Todos os navios do mar e seus marinheiros vinham para negociar as mercadorias que você tem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21132, 'nvi', 'Ezequiel', 'ez', 27, 10, '" ‘Os persas, os lídios e os homens de Fute serviam como soldados em seu exército. Eles penduravam os seus escudos e capacetes nos seus muros, trazendo-lhe esplendor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21133, 'nvi', 'Ezequiel', 'ez', 27, 11, 'Homens de Arvade e de Heleque guarneciam os seus muros em todos os lados; homens de Gamade estavam em suas torres. Eles penduravam os seus escudos em seus muros ao redor; levaram a beleza de você à perfeição.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21134, 'nvi', 'Ezequiel', 'ez', 27, 12, '" ‘Társis fez negócios com você, tendo em vista os seus muitos bens; eles deram prata, ferro, estanho e chumbo em troca de suas mercadorias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21135, 'nvi', 'Ezequiel', 'ez', 27, 13, '" ‘Javã, Tubal e Meseque fizeram comércio com você; trocaram escravos e utensílios de bronze pelos seus bens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21136, 'nvi', 'Ezequiel', 'ez', 27, 14, '" ‘Homens de Bete-Togarma trocaram cavalos de carga, cavalos de guerra e mulas pelas suas mercadorias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21137, 'nvi', 'Ezequiel', 'ez', 27, 15, '" ‘Os homens de Rodes fizeram comércio com você, e muitas regiões costeiras se tornaram seus clientes; eles lhe pagaram com presas de marfim e com ébano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21138, 'nvi', 'Ezequiel', 'ez', 27, 16, '" ‘Arã fez negócios com você atraídos por seus muitos produtos; em troca de suas mercadorias deram-lhe turquesa, tecido vermelho, trabalhos bordados, linho fino, coral e rubis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21139, 'nvi', 'Ezequiel', 'ez', 27, 17, '" ‘Judá e Israel fizeram comércio com você; pelos seus bens eles trocaram trigo de Minite, confeitos, mel, azeite e bálsamo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21140, 'nvi', 'Ezequiel', 'ez', 27, 18, '" ‘Damasco, em razão dos muitos produtos de que você dispõe e da grande riqueza de seus bens, fez negócios com você, pagando-lhe com vinho de Helbom e lã de Zaar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21141, 'nvi', 'Ezequiel', 'ez', 27, 19, '" ‘Também Dã e Javã de Uzal, compraram mercadorias de vocês, trocando-as por ferro, cássia e cálamo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21142, 'nvi', 'Ezequiel', 'ez', 27, 20, '" ‘Dedã negociou com você mantos de sela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21143, 'nvi', 'Ezequiel', 'ez', 27, 21, '" ‘A Arábia e todos os príncipes de Quedar eram seus clientes; fizeram negócios com você, fornecendo-lhes cordeiros, carneiros e bodes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21144, 'nvi', 'Ezequiel', 'ez', 27, 22, '" ‘Os mercadores de Sabá e de Raamá fizeram comércio com você; pelas mercadorias que você vende eles trocaram o que há de melhor em toda espécie de especiarias, pedras preciosas, e ouro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21145, 'nvi', 'Ezequiel', 'ez', 27, 23, '" ‘Harã, Cane e Éden e os mercadores de Sabá, Assur e Quilmade fizeram comércio com você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21146, 'nvi', 'Ezequiel', 'ez', 27, 24, 'No seu mercado eles negociaram com você lindas roupas, tecido azul, trabalhos bordados e tapetes multicoloridos com cordéis retorcidos e de nós firmes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21147, 'nvi', 'Ezequiel', 'ez', 27, 25, '" ‘Os navios de Társis transportam os seus bens. Você está cheia de carga pesada no coração do mar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21148, 'nvi', 'Ezequiel', 'ez', 27, 26, 'Seus remadores a levam para alto mar. Mas o vento oriental a despedaçará no coração do mar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21149, 'nvi', 'Ezequiel', 'ez', 27, 27, 'Sua riqueza, suas mercadorias e seus bens, seus marujos, seus homens do mar e seus construtores de barcos, seus mercadores e todos os seus soldados, todos quantos estão a bordo sucumbirão no coração do mar no dia do seu naufrágio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21150, 'nvi', 'Ezequiel', 'ez', 27, 28, 'As praias tremerão quando os seus marujos clamarem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21151, 'nvi', 'Ezequiel', 'ez', 27, 29, 'Todos os que manejam os remos abandonarão os seus navios; os marujos e todos os marinheiros ficarão na praia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21152, 'nvi', 'Ezequiel', 'ez', 27, 30, 'Erguerão a voz e gritarão com amargura por sua causa; espalharão poeira sobre as suas cabeças e rolarão na cinza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21153, 'nvi', 'Ezequiel', 'ez', 27, 31, 'Raparão a cabeça por sua causa e porão vestes de lamento. Chorarão por você com angústia na alma e com pranto amargurado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21154, 'nvi', 'Ezequiel', 'ez', 27, 32, 'Quando estiverem gritando e pranteando por você, erguerão este lamento a seu respeito: "Quem chegou a ser silenciada como Tiro, cercada pelo mar? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21155, 'nvi', 'Ezequiel', 'ez', 27, 33, 'Quando as suas mercadorias saíam para o mar, você satisfazia muitas nações; com sua grande riqueza e com seus bens você enriqueceu os reis da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21156, 'nvi', 'Ezequiel', 'ez', 27, 34, 'Agora, destruída pelo mar, você jaz nas profundezas das águas; seus bens e todos os que a acompanham afundaram com você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21157, 'nvi', 'Ezequiel', 'ez', 27, 35, 'Todos os que moram nas regiões litorâneas estão chocados com o que aconteceu com você; seus reis arrepiam-se horrorizados e os seus rostos estão desfigurados de medo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21158, 'nvi', 'Ezequiel', 'ez', 27, 36, 'Os mercadores entre as nações gritam de medo, ao vê-la; chegou o seu terrível fim, e você não mais existirá’ ".');
