INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21185, 'nvi', 'Ezequiel', 'ez', 29, 1, 'No décimo segundo dia do décimo mês do décimo ano do exílio, esta palavra do Senhor veio a mim:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21186, 'nvi', 'Ezequiel', 'ez', 29, 2, '"Filho do homem, vire o rosto contra o faraó, rei do Egito, e profetize contra ele e contra todo o Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21187, 'nvi', 'Ezequiel', 'ez', 29, 3, 'Diga-lhe: ‘Assim diz o Soberano Senhor: " ‘Estou contra você, faraó, rei do Egito, contra você, grande monstro deitado em meio a seus riachos. Você diz: "O Nilo é meu; eu o fiz para mim mesmo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21188, 'nvi', 'Ezequiel', 'ez', 29, 4, 'Mas porei anzóis em seu queixo e farei os peixes dos seus regatos se apegarem às suas próprias escamas. Puxarei você para fora dos seus riachos, com todos os peixes grudados em suas escamas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21189, 'nvi', 'Ezequiel', 'ez', 29, 5, 'Deixarei você no deserto, você e todos os peixes dos seus regatos. Você cairá em campo aberto e não será recolhido nem sepultado. Darei você como comida aos animais selvagens e às aves do céu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21190, 'nvi', 'Ezequiel', 'ez', 29, 6, '" ‘Então todos os que vivem no Egito saberão que eu sou o Senhor. " ‘Você tem sido um bordão de junco para a nação de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21191, 'nvi', 'Ezequiel', 'ez', 29, 7, 'Quando eles o pegaram com as mãos, você rachou e rasgou os ombros deles; quando eles se apoiaram em você, você se quebrou, e as costas deles sofreram torção.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21192, 'nvi', 'Ezequiel', 'ez', 29, 8, '" ‘Portanto, assim diz o Soberano Senhor: Trarei uma espada contra você e matarei os seus homens e os seus animais.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21193, 'nvi', 'Ezequiel', 'ez', 29, 9, 'O Egito se tornará um deserto arrasado. Então eles saberão que eu sou o Senhor. " ‘Visto que você disse: "O Nilo é meu; eu o fiz",');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21194, 'nvi', 'Ezequiel', 'ez', 29, 10, 'estou contra você e contra os seus regatos, e tornarei o Egito uma desgraça e um deserto arrasado desde Migdol até Sevene, chegando até a fronteira da Etiópia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21195, 'nvi', 'Ezequiel', 'ez', 29, 11, 'Nenhum pé de homem ou pata de animal o atravessará; ninguém morará ali por quarenta anos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21196, 'nvi', 'Ezequiel', 'ez', 29, 12, 'Farei a terra do Egito arrasada em meio a terras devastadas, e suas cidades estarão arrasadas durante quarenta anos entre cidades em ruínas. E espalharei os egípcios entre as nações e os dispersarei entre os povos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21197, 'nvi', 'Ezequiel', 'ez', 29, 13, '" ‘Contudo, assim diz o Soberano Senhor: Ao fim dos quarenta anos ajuntarei os egípcios de entre as nações nos quais foram espalhados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21198, 'nvi', 'Ezequiel', 'ez', 29, 14, 'Eu os trarei de volta do cativeiro e os farei voltar ao alto Egito, à terra dos seus antepassados. Ali serão um reino humilde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21199, 'nvi', 'Ezequiel', 'ez', 29, 15, 'Será o mais humilde dos reinos, e nunca mais se exaltará sobre as outras nações. Eu o farei tão fraco que nunca mais dominará sobre as nações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21200, 'nvi', 'Ezequiel', 'ez', 29, 16, 'O Egito não inspirará mais confiança a Israel, mas será uma lembrança de sua iniqüidade por ir atrás dele em busca de ajuda. Então eles saberão que eu sou o Soberano Senhor’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21201, 'nvi', 'Ezequiel', 'ez', 29, 17, 'No dia primeiro do primeiro mês do vigésimo sétimo ano do exílio, esta palavra do Senhor veio a mim:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21202, 'nvi', 'Ezequiel', 'ez', 29, 18, '"Filho do homem, o rei Nabucodonosor, da Babilônia, conduziu o seu exército numa dura campanha contra Tiro; toda cabeça foi esfregada até não ter ficar cabelo algum e todo ombro ficou esfolado. Contudo, ele e o seu exército não obtiveram nenhuma recompensa com a campanha que ele conduziu contra Tiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21203, 'nvi', 'Ezequiel', 'ez', 29, 19, 'Por isso, assim diz o Soberano Senhor: Vou dar o Egito ao rei Nabucodonosor, da Babilônia, e ele levará embora a riqueza dessa nação. Ele saqueará e despojará a terra como pagamento para o seu exército.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21204, 'nvi', 'Ezequiel', 'ez', 29, 20, 'Eu lhe dei o Egito como recompensa por seus esforços, por aquilo que ele e o seu exército fizeram para mim, palavra do Soberano Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21205, 'nvi', 'Ezequiel', 'ez', 29, 21, '"Naquele dia farei crescer o poder da nação de Israel, e abrirei a minha boca no meio deles. Então eles saberão que eu sou o Senhor".');
