INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21250, 'nvi', 'Ezequiel', 'ez', 32, 1, 'No dia primeiro do décimo segundo mês do décimo segundo ano, esta palavra do Senhor veio a mim:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21251, 'nvi', 'Ezequiel', 'ez', 32, 2, '"Filho do homem, entoe um lamento a respeito do faraó, rei do Egito, e diga a ele: " ‘Você é como um leão entre as nações, como um monstro nos mares, contorcendo-se em seus riachos, agitando e enlameando as suas águas com os pés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21252, 'nvi', 'Ezequiel', 'ez', 32, 3, '" ‘Assim diz o Soberano Senhor: " ‘Com uma imensa multidão de povos lançarei sobre você a minha rede, e eles o puxarão para cima em minha rede.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21253, 'nvi', 'Ezequiel', 'ez', 32, 4, 'Atirarei você na terra e o lançarei no campo. Deixarei que todas as aves do céu se abriguem em você e os animais de toda a terra hão de empanturrar-se de você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21254, 'nvi', 'Ezequiel', 'ez', 32, 5, 'Estenderei a sua carne sobre os montes e encherei os vales com os seus restos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21255, 'nvi', 'Ezequiel', 'ez', 32, 6, 'Encharcarei a terra com o sangue que sair de você por todo o caminho, até os montes, e os vales ficarão cheias da sua carne.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21256, 'nvi', 'Ezequiel', 'ez', 32, 7, 'Quando eu o extinguir, cobrirei o céu e escurecerei as suas estrelas; cobrirei o sol com uma nuvem, e a lua não dará a sua luz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21257, 'nvi', 'Ezequiel', 'ez', 32, 8, 'Todas as estrelas que brilham nos céus, eu as escurecerei sobre você, e trarei escuridão sobre a sua terra, palavra do Soberano Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21258, 'nvi', 'Ezequiel', 'ez', 32, 9, 'Perturbarei os corações de muitos povos quando eu provocar a sua destruição entre as nações, em terras que você não conheceu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21259, 'nvi', 'Ezequiel', 'ez', 32, 10, 'Farei que muitos povos fiquem chocados ao vê-lo, e que os seus reis fiquem arrepiados de horror por sua causa, quando eu brandir a minha espada diante deles. No dia da sua queda todos eles tremerão de medo a todo instante por suas vidas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21260, 'nvi', 'Ezequiel', 'ez', 32, 11, '" ‘Porque assim diz o Soberano Senhor: " ‘A espada do rei da Babilônia virá contra você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21261, 'nvi', 'Ezequiel', 'ez', 32, 12, 'Farei multidões do seu povo cair pela espada de poderosos, da mais impiedosa das nações. Eles destruirão o orgulho do Egito, e toda a sua população será vencida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21262, 'nvi', 'Ezequiel', 'ez', 32, 13, 'Destruirei todo o seu rebanho, junto às muitas águas, as quais não serão mais agitadas pelo pé do homem nem serão enlameadas pelos cascos do gado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21263, 'nvi', 'Ezequiel', 'ez', 32, 14, 'Então deixarei que as suas águas se assentem e farei os seus riachos fluírem como azeite, palavra do Soberano Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21264, 'nvi', 'Ezequiel', 'ez', 32, 15, 'Quando eu arrasar o Egito e arrancar da terra tudo o que nela existe, quando eu abater todos os que ali moram, então eles saberão que eu sou o Senhor’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21265, 'nvi', 'Ezequiel', 'ez', 32, 16, '"Esse é o lamento que entoarão por causa dele. As filhas das nações o entoarão; por causa do Egito e de todas as suas multidões de povo, elas o entoarão; palavra do Soberano Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21266, 'nvi', 'Ezequiel', 'ez', 32, 17, 'No décimo quinto dia do mês do décimo segundo ano, esta palavra do Senhor veio a mim:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21267, 'nvi', 'Ezequiel', 'ez', 32, 18, '"Filho do homem, lamente pelas multidões do Egito e faça descer para baixo da terra tanto elas como as filhas das nações poderosas, junto com aqueles que descem à cova.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21268, 'nvi', 'Ezequiel', 'ez', 32, 19, 'Diga-lhe: ‘Acaso você merece mais favores do que os outros? Desça e deite-se com os incircuncisos’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21269, 'nvi', 'Ezequiel', 'ez', 32, 20, 'Eles cairão entre os que foram mortos pela espada. A espada está preparada; sejam eles arrastados junto com toda a multidão do seu povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21270, 'nvi', 'Ezequiel', 'ez', 32, 21, 'De dentro da sepultura os poderosos líderes dirão ao Egito e aos seus aliados: ‘Eles desceram e jazem com os incircuncisos, com os que foram mortos à espada’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21271, 'nvi', 'Ezequiel', 'ez', 32, 22, '"A Assíria está ali com todo o seu exército; está cercada pelos túmulos de todos os seus mortos, de todos os que caíram pela espada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21272, 'nvi', 'Ezequiel', 'ez', 32, 23, 'Seus túmulos estão nas profundezas, e o seu exército jaz ao redor de seu túmulo. Todos os que haviam espalhado pavor na terra dos viventes estão mortos, caídos pela espada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21273, 'nvi', 'Ezequiel', 'ez', 32, 24, '"Elão está ali, com toda a sua população ao redor de seu túmulo. Todos eles estão mortos, caídos pela espada. Todos os que haviam espalhado pavor na terra dos viventes desceram incircuncisos para baixo da terra. Carregam sua vergonha com os que descem à cova.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21274, 'nvi', 'Ezequiel', 'ez', 32, 25, 'Uma cama está preparada para ele entre os mortos, com todas as suas hordas em torno de seu túmulo. Todos eles são incircuncisos, mortos pela espada. Porque o seu terror havia se espalhado na terra dos viventes, eles carregam sua desonra com aqueles que descem à cova; jazem entre os mortos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21275, 'nvi', 'Ezequiel', 'ez', 32, 26, '"Meseque e Tubal estão ali, com todas a sua população ao redor de seus túmulos. Todos eles são incircuncisos, mortos à espada porque espalharam o seu terror na terra dos viventes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21276, 'nvi', 'Ezequiel', 'ez', 32, 27, 'Acaso não jazem com os outros guerreiros incircuncisos que caíram, que desceram à sepultura com suas armas de guerra, cujas espadas foram postas debaixo de suas cabeças? O castigo de suas iniqüidades está sobre seus ossos, embora o pavor causado por esses guerreiros tenha percorrido a terra dos viventes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21277, 'nvi', 'Ezequiel', 'ez', 32, 28, '"Você também, ó faraó, será abatido e jazerá entre os incircuncisos, com os que foram mortos à espada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21278, 'nvi', 'Ezequiel', 'ez', 32, 29, '"Edom está ali, seus reis e todos os seus príncipes; a despeito de seu poder, jazem com os que foram mortos à espada. Jazem com os incircuncisos, com aqueles que descem à cova.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21279, 'nvi', 'Ezequiel', 'ez', 32, 30, '"Todos os príncipes do norte e todos os sidônios estão ali; eles desceram com os mortos cobertos de vergonha, apesar do pavor provocado pelo poder que tinham. Eles jazem incircuncisos com os que foram mortos à espada e carregam sua desonra com aqueles que descem à cova.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21280, 'nvi', 'Ezequiel', 'ez', 32, 31, '"O faraó, ele e todo o seu exército, os verá e será consolado da perda de todo o seu povo que foi morto à espada, palavra do Soberano Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21281, 'nvi', 'Ezequiel', 'ez', 32, 32, 'Embora eu o tenha feito espalhar pavor na terra dos viventes, o faraó e todo o seu povo jazerão entre os incircuncisos, com os que foram mortos pela espada, palavra do Soberano Senhor".');
