INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21399, 'nvi', 'Ezequiel', 'ez', 37, 1, 'A mão do Senhor estava sobre mim, e por seu Espírito ele me levou a um vale cheio de ossos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21400, 'nvi', 'Ezequiel', 'ez', 37, 2, 'Ele me levou de um lado para outro, e pude ver que era enorme o número de ossos no vale, e que os ossos estavam muito secos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21401, 'nvi', 'Ezequiel', 'ez', 37, 3, 'Ele me perguntou: "Filho do homem, esses ossos poderão tornar a viver? " Eu respondi: "Ó Soberano Senhor, só tu o sabes".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21402, 'nvi', 'Ezequiel', 'ez', 37, 4, 'Então ele me disse: "Profetize a esses ossos e diga-lhes: ‘Ossos secos, ouçam a palavra do Senhor!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21403, 'nvi', 'Ezequiel', 'ez', 37, 5, 'Assim diz o Soberano Senhor a estes ossos: Farei um espírito entrar em vocês, e vocês terão vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21404, 'nvi', 'Ezequiel', 'ez', 37, 6, 'Porei tendões em vocês e farei aparecer carne sobre vocês e os cobrirei com pele; porei um espírito em vocês, e vocês terão vida. Então vocês saberão que eu sou o Senhor’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21405, 'nvi', 'Ezequiel', 'ez', 37, 7, 'E eu profetizei conforme a ordem recebida. E, enquanto profetizava, houve um barulho, um som de chocalho, e os ossos se juntaram, osso com osso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21406, 'nvi', 'Ezequiel', 'ez', 37, 8, 'Olhei, e os ossos foram cobertos de tendões e de carne, e depois de pele, mas não havia espírito neles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21407, 'nvi', 'Ezequiel', 'ez', 37, 9, 'A seguir ele me disse: "Profetize ao espírito; profetize, filho do homem, e diga-lhe: ‘Assim diz o Soberano Senhor: Venha desde os quatro ventos, ó espírito, e sopre dentro desses mortos, para que vivam’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21408, 'nvi', 'Ezequiel', 'ez', 37, 10, 'Profetizei conforme a ordem recebida, e o espírito entrou neles; eles receberam vida e se puseram de pé. Era um exército enorme!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21409, 'nvi', 'Ezequiel', 'ez', 37, 11, 'Então ele me disse: "Filho do homem, esses ossos são toda a nação de Israel. Eles dizem: ‘Nossos ossos se secaram e nossa esperança se foi; fomos exterminados’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21410, 'nvi', 'Ezequiel', 'ez', 37, 12, 'Por isso profetize e diga-lhes: ‘Assim diz o Soberano Senhor: Ó meu povo, vou abrir os seus túmulos e fazê-los sair; trarei vocês de volta à terra de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21411, 'nvi', 'Ezequiel', 'ez', 37, 13, 'E, quando eu abrir os seus túmulos e os fizer sair, vocês, meu povo, saberão que eu sou o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21412, 'nvi', 'Ezequiel', 'ez', 37, 14, 'Porei o meu Espírito em vocês, e vocês viverão, e eu os estabelecerei em sua própria terra. Então vocês saberão que eu, o Senhor, falei, e o fiz seus companheiros, palavra do Senhor’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21413, 'nvi', 'Ezequiel', 'ez', 37, 15, 'Esta palavra do Senhor veio a mim:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21414, 'nvi', 'Ezequiel', 'ez', 37, 16, '"Filho do homem, escreva num pedaço de madeira: ‘Pertencente a Judá e aos israelitas, seus companheiros’. Depois escreva noutro pedaço de madeira: ‘Vara de Efraim, pertencente a José e a toda a nação de Israel, seus companheiros’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21415, 'nvi', 'Ezequiel', 'ez', 37, 17, 'Junte-os numa única vara para que se tornem uma só em sua mão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21416, 'nvi', 'Ezequiel', 'ez', 37, 18, '"Quando os seus compatriotas lhe perguntarem: ‘Você não vai nos dizer o que isso significa? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21417, 'nvi', 'Ezequiel', 'ez', 37, 19, 'Diga-lhes: ‘Assim diz o Soberano Senhor: Vou apanhar a vara pertencente a José, que está na mão de Efraim, e às tribos israelitas, seus companheiros, e juntá-la com a vara de Judá, fazendo delas um único pedaço de madeira, e elas se tornarão uma só na minha mão’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21418, 'nvi', 'Ezequiel', 'ez', 37, 20, 'Segure diante dos olhos deles os pedaços de madeira em que você escreveu');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21419, 'nvi', 'Ezequiel', 'ez', 37, 21, 'e diga-lhes: ‘Assim diz o Soberano Senhor: Tirarei os israelitas das nações para onde foram. Vou ajuntá-los de todos os lugares ao redor e trazê-los de volta à sua própria terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21420, 'nvi', 'Ezequiel', 'ez', 37, 22, 'Eu os farei uma única nação na terra, nos montes de Israel. Haverá um único rei sobre todos eles, e eles nunca mais serão duas nações nem estarão divididos em dois reinos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21421, 'nvi', 'Ezequiel', 'ez', 37, 23, 'Não se contaminarão mais com seus ídolos e imagens detestáveis nem com nenhuma de suas transgressões, pois eu os salvarei de todas as suas apostasias pecaminosas, e os purificarei. Eles serão o meu povo, e eu serei o seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21422, 'nvi', 'Ezequiel', 'ez', 37, 24, '" ‘O meu servo Davi será rei sobre eles, e todos eles terão um só pastor. Eles seguirão as minhas leis e terão o cuidado de obedecer aos meus decretos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21423, 'nvi', 'Ezequiel', 'ez', 37, 25, 'Viverão na terra que dei ao meu servo Jacó, a terra onde os seus antepassados viveram. Eles e os seus filhos e os filhos de seus filhos viverão ali para sempre, e o meu servo Davi será o seu líder para sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21424, 'nvi', 'Ezequiel', 'ez', 37, 26, 'Farei uma aliança de paz com eles; será uma aliança eterna. Eu os firmarei e os multiplicarei, e porei o meu santuário no meio deles para sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21425, 'nvi', 'Ezequiel', 'ez', 37, 27, 'Minha morada estará com eles; eu serei o seu Deus, e eles serão o meu povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21426, 'nvi', 'Ezequiel', 'ez', 37, 28, 'Então, quando o meu santuário estiver entre eles para sempre, as nações saberão que eu, o Senhor, santifico Israel’ ".');
