INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21427, 'nvi', 'Ezequiel', 'ez', 38, 1, 'Veio a mim esta palavra do Senhor:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21428, 'nvi', 'Ezequiel', 'ez', 38, 2, '"Filho do homem, vire o rosto contra Gogue, da terra de Magogue, o príncipe maior de Meseque e de Tubal; profetize contra ele');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21429, 'nvi', 'Ezequiel', 'ez', 38, 3, 'e diga: ‘Assim diz o Soberano Senhor: Estou contra você, ó Gogue, príncipe maior de Meseque e de Tubal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21430, 'nvi', 'Ezequiel', 'ez', 38, 4, 'Farei você girar, porei anzóis em seu queixo e o farei sair com todo o seu exército: seus cavalos, seus cavaleiros totalmente armados e uma grande multidão com escudos grandes e pequenos, todos eles brandindo suas espadas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21431, 'nvi', 'Ezequiel', 'ez', 38, 5, 'A Pérsia, a Etiópia e a Líbia estarão com eles, todos com escudos e capacetes;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21432, 'nvi', 'Ezequiel', 'ez', 38, 6, 'Gômer com todas as suas tropas, e Bete-Togarma, do extremo norte com todas as suas tropas; muitas nações com você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21433, 'nvi', 'Ezequiel', 'ez', 38, 7, '" ‘Aprontem-se; estejam preparados, você e todas as multidões reunidas ao seu redor, e assuma o comando delas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21434, 'nvi', 'Ezequiel', 'ez', 38, 8, 'Depois de muitos dias você será chamado às armas. Em anos futuros você invadirá uma terra que se recuperou da guerra, cujo povo foi reunido dentre muitas nações nos montes de Israel, os quais por muito tempo estiveram arrasados. Foram trazidos das nações, e agora todos eles vivem em segurança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21435, 'nvi', 'Ezequiel', 'ez', 38, 9, 'Você e todas as suas tropas e as muitas nações com você subirão, avançando como uma tempestade; você será como uma nuvem cobrindo a terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21436, 'nvi', 'Ezequiel', 'ez', 38, 10, '" ‘Assim diz o Soberano Senhor: Naquele dia virão pensamentos à sua cabeça, e você maquinará um plano maligno.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21437, 'nvi', 'Ezequiel', 'ez', 38, 11, 'Você dirá: "Invadirei uma terra de povoados; atacarei um povo pacífico e que de nada suspeita, onde todos moram em cidades sem muros, sem portas e sem trancas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21438, 'nvi', 'Ezequiel', 'ez', 38, 12, 'Despojarei, saquearei e voltarei a minha mão contra as ruínas reerguidas e contra o povo ajuntado de entre as nações, rico em gado e em bens, que vive na parte central do território ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21439, 'nvi', 'Ezequiel', 'ez', 38, 13, 'Sabá e Dedã e os mercadores de Társis e todos os seus povoados dirão a você: "Você veio para tomar despojos? Você reuniu essa multidão para saquear, levar embora prata e ouro, tomar o gado e os bens e apoderar-se de muitos despojos? " ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21440, 'nvi', 'Ezequiel', 'ez', 38, 14, '"Por isso, filho do homem, profetize e diga a Gogue: ‘Assim diz o Soberano Senhor: Naquele dia, quando o meu povo Israel estiver vivendo em segurança, será que você não vai reparar nisso?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21441, 'nvi', 'Ezequiel', 'ez', 38, 15, 'Você virá de seu lugar, do extremo norte, você, acompanhado de muitas nações, todas elas montadas em cavalos, uma grande multidão, um exército numeroso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21442, 'nvi', 'Ezequiel', 'ez', 38, 16, 'Você avançará contra Israel, o meu povo, como uma nuvem que cobre a terra. Nos dias vindouros, ó Gogue, trarei você contra a minha terra, para que as nações me conheçam quando eu me mostrar santo por meio de você diante dos olhos deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21443, 'nvi', 'Ezequiel', 'ez', 38, 17, '" ‘Assim diz o Soberano Senhor: Acaso você não é aquele de quem falei em dias passados por meio dos meus servos, os profetas de Israel? Naquela época eles profetizaram durante anos que eu traria você contra eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21444, 'nvi', 'Ezequiel', 'ez', 38, 18, 'É isto que acontecerá naquele dia: Quando Gogue atacar Israel, será despertado o meu furor, palavra do Soberano Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21445, 'nvi', 'Ezequiel', 'ez', 38, 19, 'Em meu zelo e em meu grande furor declaro que naquela época haverá um grande terremoto em Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21446, 'nvi', 'Ezequiel', 'ez', 38, 20, 'Os peixes do mar, as aves do céu, os animais do campo, toda criatura que rasteja pelo chão e todas as pessoas da face da terra tremerão diante da minha presença. Os montes serão virados de cabeça para baixo, os penhascos se desmoronarão e todos os muros cairão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21447, 'nvi', 'Ezequiel', 'ez', 38, 21, 'Convocarei a espada contra Gogue em todos os meus montes, palavra do Soberano Senhor. A espada de cada um será contra o seu irmão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21448, 'nvi', 'Ezequiel', 'ez', 38, 22, 'Executarei juízo sobre ele com peste e derramamento de sangue; desabarei torrentes de chuva, saraiva e enxofre ardente sobre ele e sobre as suas tropas e sobre as muitas nações que estarão com ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21449, 'nvi', 'Ezequiel', 'ez', 38, 23, 'E assim mostrarei a minha grandeza e a minha santidade, e me farei conhecido de muitas nações. Então eles saberão que eu sou o Senhor’.');
