INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21450, 'nvi', 'Ezequiel', 'ez', 39, 1, '"Filho do homem, profetize contra Gogue e diga: ‘Assim diz o Soberano Senhor: Eu estou contra você, ó Gogue, príncipe maior de Meseque e de Tubal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21451, 'nvi', 'Ezequiel', 'ez', 39, 2, 'Farei você girar e o arrastarei. Eu o trarei do extremo norte e o enviarei contra os montes de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21452, 'nvi', 'Ezequiel', 'ez', 39, 3, 'Então derrubarei o arco da sua mão esquerda e farei suas flechas caírem da sua mão direita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21453, 'nvi', 'Ezequiel', 'ez', 39, 4, 'Nos montes de Israel você cairá, você e todas as suas tropas e as nações que estiverem com você. Eu darei você como comida a todo tipo de ave que come carniça e aos animais do campo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21454, 'nvi', 'Ezequiel', 'ez', 39, 5, 'Você cairá em campo aberto, pois eu falei, palavra do Soberano Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21455, 'nvi', 'Ezequiel', 'ez', 39, 6, 'Mandarei fogo sobre Magogue e sobre aqueles que vivem em segurança nas regiões costeiras, e eles saberão que eu sou o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21456, 'nvi', 'Ezequiel', 'ez', 39, 7, '" ‘Farei conhecido o meu santo nome no meio do meu povo Israel. Não mais deixarei que o meu nome seja profanado, e as nações saberão que eu, o Senhor, sou o Santo de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21457, 'nvi', 'Ezequiel', 'ez', 39, 8, 'E aí vem! É certo que acontecerá, palavra do Soberano Senhor. Este é o dia de que eu falei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21458, 'nvi', 'Ezequiel', 'ez', 39, 9, '" ‘Então aqueles que morarem nas cidades de Israel sairão e usarão armas como combustível e as queimarão: os escudos, pequenos e grandes, os arcos e flechas, os bastões de guerra e as lanças. Durante sete anos eles as utilizarão como combustível.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21459, 'nvi', 'Ezequiel', 'ez', 39, 10, 'Não precisarão ajuntar lenha nos campos nem cortá-la nas florestas, porque eles usarão as armas como combustível. E eles despojarão aqueles que os despojaram e saquearão aqueles que os saquearam, palavra do Soberano Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21460, 'nvi', 'Ezequiel', 'ez', 39, 11, '" ‘Naquele dia darei a Gogue um túmulo em Israel, no vale dos que viajam para o oriente na direção do Mar. Ele bloqueará o caminho dos viajantes porque Gogue e todos os seus batalhões serão sepultados ali. Por isso será chamado vale de Hamom-Gogue.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21461, 'nvi', 'Ezequiel', 'ez', 39, 12, '" ‘Durante sete meses a nação de Israel os estará sepultando a fim de purificar a terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21462, 'nvi', 'Ezequiel', 'ez', 39, 13, 'Todo o povo da terra os sepultará, e o dia em que eu for glorificado será para eles um dia memorável, palavra do Soberano Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21463, 'nvi', 'Ezequiel', 'ez', 39, 14, '" ‘Depois dos sete meses serão contratados homens para percorrer a terra e sepultar os que ainda restarem. E assim a terra será purificada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21464, 'nvi', 'Ezequiel', 'ez', 39, 15, 'Quando estiverem percorrendo a terra e um deles vir um osso humano, fincará um marco ao lado do osso até que os coveiros o sepultem no vale de Hamom-Gogue.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21465, 'nvi', 'Ezequiel', 'ez', 39, 16, '( Também haverá ali uma cidade à qual se dará o nome de Hamoná. ) E assim eles purificarão a terra’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21466, 'nvi', 'Ezequiel', 'ez', 39, 17, '"Filho do homem, assim diz o Soberano Senhor: Chame todo tipo de ave e todos os animais do campo: ‘Venham de todos os lugares ao redor e reúnam-se para o sacrifício que estou preparando para vocês, o grande sacrifício nos montes de Israel. Ali vocês comerão carne e beberão sangue.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21467, 'nvi', 'Ezequiel', 'ez', 39, 18, 'Comerão a carne de poderosos e beberão o sangue dos príncipes da terra como se eles fossem carneiros, cordeiros, bodes e novilhos, todos eles animais gordos de Basã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21468, 'nvi', 'Ezequiel', 'ez', 39, 19, 'No sacrifício que lhes estou preparando, vocês comerão gordura até empanturrar-se e beberão sangue até embriagar-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21469, 'nvi', 'Ezequiel', 'ez', 39, 20, 'À minha mesa vocês comerão sua porção de cavalos e cavaleiros, de homens poderosos e soldados de todo tipo’, palavra do Soberano Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21470, 'nvi', 'Ezequiel', 'ez', 39, 21, '"Exibirei a minha glória entre as nações, e todas as nações verão o castigo que eu trouxer e a mão que eu colocar sobre eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21471, 'nvi', 'Ezequiel', 'ez', 39, 22, 'Daquele dia em diante a nação de Israel saberá que eu sou o Senhor, o seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21472, 'nvi', 'Ezequiel', 'ez', 39, 23, 'E as nações saberão que o povo de Israel foi para o exílio por causa de sua iniqüidade, porque me foram infiéis. Por isso escondi deles o meu rosto e os entreguei nas mãos de seus inimigos, e eles caíram pela espada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21473, 'nvi', 'Ezequiel', 'ez', 39, 24, 'Tratei com eles de acordo com a sua impureza e com as suas transgressões, e escondi deles o meu rosto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21474, 'nvi', 'Ezequiel', 'ez', 39, 25, '"Por isso, assim diz o Soberano Senhor: Agora trarei Jacó de volta do cativeiro e terei compaixão de toda a nação de Israel, e serei zeloso pelo meu santo nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21475, 'nvi', 'Ezequiel', 'ez', 39, 26, 'Eles se esquecerão da vergonha por que passaram e de toda a infidelidade que mostraram para comigo enquanto viviam em segurança em sua terra sem que ninguém lhes causasse medo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21476, 'nvi', 'Ezequiel', 'ez', 39, 27, 'Quando eu os tiver trazido de volta das nações e os tiver ajuntado de entre as terras de seus inimigos, eu me revelarei santo por meio deles à vista de muitas nações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21477, 'nvi', 'Ezequiel', 'ez', 39, 28, 'Então eles saberão que eu sou o Senhor, o seu Deus, pois, embora os tenha enviado para o exílio entre as nações, eu os reunirei em sua própria terra, sem deixar um único deles para trás.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21478, 'nvi', 'Ezequiel', 'ez', 39, 29, 'Não mais esconderei deles o rosto, pois derramarei o meu Espírito sobre a nação de Israel, palavra do Soberano Senhor".');
