INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21554, 'nvi', 'Ezequiel', 'ez', 42, 1, 'Depois disso o homem conduziu-me para o lado norte, para o pátio externo, e levou-me aos quartos opostos ao pátio do templo e ao muro externo do lado norte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21555, 'nvi', 'Ezequiel', 'ez', 42, 2, 'O prédio cuja porta dava para o norte tinha cinqüenta metros de comprimento e vinte e cinco metros de largura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21556, 'nvi', 'Ezequiel', 'ez', 42, 3, 'Tanto na secção que ficava a dez metros de distância do pátio interno quanto na secção oposta ao piso do pátio externo, havia uma galeria frente à outra nos três andares.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21557, 'nvi', 'Ezequiel', 'ez', 42, 4, 'Em frente dos quartos havia uma passagem interna com cinco metros de largura e cinqüenta metros de comprimento. Suas portas ficavam no lado norte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21558, 'nvi', 'Ezequiel', 'ez', 42, 5, 'Ora, os quartos superiores eram mais estreitos, pois as galerias tomavam mais espaço deles do que dos quartos do andar inferior e médio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21559, 'nvi', 'Ezequiel', 'ez', 42, 6, 'Os quartos do terceiro andar não tinham colunas, ao passo que os pátios as tinham. Por isso a área deles era menor do que a dos quartos do andar inferior e o do meio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21560, 'nvi', 'Ezequiel', 'ez', 42, 7, 'Havia uma parede externa paralela aos quartos e ao pátio externo; sua extensão era de vinte e cinco metros, em frente dos quartos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21561, 'nvi', 'Ezequiel', 'ez', 42, 8, 'A carreira de quartos junto ao pátio interno tinha vinte e cinco metros de comprimento, e a que ficava mais próximo do santuário tinha cinqüenta metros de comprimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21562, 'nvi', 'Ezequiel', 'ez', 42, 9, 'Os quartos de baixo tinham entrada pelo lado leste, quando se vem do pátio externo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21563, 'nvi', 'Ezequiel', 'ez', 42, 10, 'No lado sul, ao longo do comprimento da parede do pátio externo, adjacentes ao pátio do templo e no lado oposto do muro externo, havia quartos');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21564, 'nvi', 'Ezequiel', 'ez', 42, 11, 'com uma passagem em frente deles. Eram como os quartos do lado norte; tinham o mesmo comprimento e a mesma largura, com saídas e dimensões semelhantes. As portas do lado norte');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21565, 'nvi', 'Ezequiel', 'ez', 42, 12, 'eram semelhantes às portas dos quartos do lado sul. Havia uma entrada no início do corredor paralelo ao muro correspondente que se estendia para leste; e havia uma entrada para os quartos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21566, 'nvi', 'Ezequiel', 'ez', 42, 13, 'Depois ele me disse: "Os quartos do norte e do sul que dão para o pátio do templo são os quartos dos sacerdotes, onde os sacerdotes que se aproximam do Senhor comerão as ofertas santíssimas. Ali porão as ofertas santíssimas: as ofertas de cereal, as ofertas pelo pecado e as ofertas pela culpa, pois o local é santo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21567, 'nvi', 'Ezequiel', 'ez', 42, 14, 'Assim que os sacerdotes entrarem nos recintos sagrados, só poderão ir para o pátio externo após tirarem as vestes com as quais ministram, pois elas são santas. Porão outras vestes antes de se aproximarem dos lugares reservados para o povo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21568, 'nvi', 'Ezequiel', 'ez', 42, 15, 'Quando ele acabou de medir o que havia dentro da área do templo, levou-me para fora pela porta leste e mediu a área em redor:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21569, 'nvi', 'Ezequiel', 'ez', 42, 16, 'Mediu o lado leste com a vara de medir; e ele tinha duzentos e cinqüenta metros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21570, 'nvi', 'Ezequiel', 'ez', 42, 17, 'Mediu o lado norte; ele tinha duzentos e cinqüenta metros, segundo a vara de medir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21571, 'nvi', 'Ezequiel', 'ez', 42, 18, 'Mediu o lado sul; e tinha duzentos e cinqüenta metros, segundo a vara de medir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21572, 'nvi', 'Ezequiel', 'ez', 42, 19, 'Depois ele foi para o lado oeste e o mediu; ele tinha duzentos e cinqüenta metros, segundo a vara de medir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21573, 'nvi', 'Ezequiel', 'ez', 42, 20, 'Assim ele mediu a área nos quatro lados. Em torno dela havia um muro de duzentos e cinqüenta metros de comprimento e duzentos e cinqüenta metros de largura, para separar o santo do comum.');
