INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21574, 'nvi', 'Ezequiel', 'ez', 43, 1, 'Então o homem levou-me até a porta que dava para o leste,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21575, 'nvi', 'Ezequiel', 'ez', 43, 2, 'e vi a glória do Deus de Israel que vinha do lado leste. Sua voz era como o rugido de águas avançando, e a terra estava refulgente com a sua glória.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21576, 'nvi', 'Ezequiel', 'ez', 43, 3, 'A visão que tive era como a que eu tinha tido quando ele veio destruir a cidade e como as que eu tinha tido junto ao rio Quebar; e eu me prostrei, rosto em terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21577, 'nvi', 'Ezequiel', 'ez', 43, 4, 'A glória do Senhor entrou no templo pela porta que dava para o lado leste.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21578, 'nvi', 'Ezequiel', 'ez', 43, 5, 'Então o Espírito pôs-me de pé e levou-me para dentro do pátio interno, e a glória do Senhor encheu o templo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21579, 'nvi', 'Ezequiel', 'ez', 43, 6, 'Enquanto o homem estava ao meu lado, ouvi alguém falando comigo de dentro do templo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21580, 'nvi', 'Ezequiel', 'ez', 43, 7, 'Ele disse: "Filho do homem, este é o lugar do meu trono e o lugar para a sola dos meus pés. Aqui viverei para sempre entre os israelitas. A nação de Israel jamais contaminará o meu santo nome, nem eles nem seus reis, mediante a sua prostituição e os ídolos sem vida de seus reis em seus santuários nos montes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21581, 'nvi', 'Ezequiel', 'ez', 43, 8, 'Quando eles puseram sua soleira perto de minha soleira e seus batentes junto de meus batentes, com apenas uma parede fazendo separação entre mim e eles, eles contaminaram o meu santo nome com suas práticas repugnantes. Por isso eu os destruí na minha ira.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21582, 'nvi', 'Ezequiel', 'ez', 43, 9, 'Agora, que afastem de mim a sua prostituição e os seus ídolos sem vida de seus reis, e eu viverei entre eles para sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21583, 'nvi', 'Ezequiel', 'ez', 43, 10, '"Filho do homem, descreva o templo para a nação de Israel, para que fiquem envergonhados dos seus pecados. Que eles analisem o modelo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21584, 'nvi', 'Ezequiel', 'ez', 43, 11, 'e, se ficarem envergonhados por tudo o que fizeram, informe-os acerca da planta do templo — sua disposição, suas saídas e suas entradas — toda a sua planta e todas as suas estipulações e leis. Ponha essas coisas por escrito diante deles para que sejam fiéis à planta e sigam as suas estipulações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21585, 'nvi', 'Ezequiel', 'ez', 43, 12, '"Esta é a lei do templo: Toda a área ao redor no topo do monte será santíssima. Essa é a lei do templo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21586, 'nvi', 'Ezequiel', 'ez', 43, 13, '"Estas são as medidas do altar pela medida longa, isto é, de meio metro: Sua calha tem meio metro de fundo e meio metro de largura, com uma aba de um palmo em torno da beirada. E esta é a altura do altar:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21587, 'nvi', 'Ezequiel', 'ez', 43, 14, 'Desde a calha no chão até a saliência inferior, ele tem um metro de altura e um metro de largura, e desde a saliência menor até a saliência maior, tem dois metros de altura e meio metro de largura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21588, 'nvi', 'Ezequiel', 'ez', 43, 15, 'A fornalha do altar tem dois metros de altura, e quatro pontas se projetam dela para cima.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21589, 'nvi', 'Ezequiel', 'ez', 43, 16, 'A fornalha do altar é quadrada, com seis metros de comprimento e seis metros de largura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21590, 'nvi', 'Ezequiel', 'ez', 43, 17, 'A saliência superior também é quadrada, com sete metros de comprimento e sete metros de largura, com uma aba de vinte e cinco centímetros e uma calha de meio metro em toda a sua extensão ao redor. Os degraus do altar estão voltados para o oriente".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21591, 'nvi', 'Ezequiel', 'ez', 43, 18, 'Então ele me disse: "Filho do homem, assim diz o Soberano Senhor: Estes serão os regulamentos que deverão ser seguidos no sacrifício dos holocaustos e na aspersão do sangue no altar, quando ele for construído:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21592, 'nvi', 'Ezequiel', 'ez', 43, 19, 'Você deverá dar um novilho como oferta aos sacerdotes levitas, da família de Zadoque, os quais se aproximam para ministrar diante de mim, palavra do Soberano Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21593, 'nvi', 'Ezequiel', 'ez', 43, 20, 'Você colocará um pouco desse sangue nas quatro pontas do altar, nos quatro cantos da saliência superior e ao redor de toda a aba, e assim purificará o altar e fará propiciação por ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21594, 'nvi', 'Ezequiel', 'ez', 43, 21, 'Você queimará o novilho para a oferta pelo pecado no lugar determinado da área do templo, fora do santuário.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21595, 'nvi', 'Ezequiel', 'ez', 43, 22, '"No segundo dia você oferecerá um bode sem defeito como oferta pelo pecado, e o altar será purificado como foi purificado com o novilho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21596, 'nvi', 'Ezequiel', 'ez', 43, 23, 'Quando terminar de purificá-lo, ofereça um novilho e um carneiro tirados do rebanho, ambos sem defeito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21597, 'nvi', 'Ezequiel', 'ez', 43, 24, 'Você os oferecerá perante o Senhor, e os sacerdotes deverão pôr sal sobre eles e sacrificá-los como holocausto ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21598, 'nvi', 'Ezequiel', 'ez', 43, 25, '"Durante sete dias você fornecerá diariamente um bode como oferta pelo pecado; fornecerá também um novilho e um carneiro tirados do rebanho, ambos sem defeito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21599, 'nvi', 'Ezequiel', 'ez', 43, 26, 'Durante sete dias os sacerdotes farão propiciação pelo altar e o purificarão; assim eles o consagrarão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21600, 'nvi', 'Ezequiel', 'ez', 43, 27, 'No final desses dias, a partir do oitavo dia, os sacerdotes apresentarão os holocaustos e os sacrifícios de comunhão de vocês sobre o altar. Então eu os aceitarei, palavra do Soberano Senhor".');
