INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21601, 'nvi', 'Ezequiel', 'ez', 44, 1, 'Depois o homem trouxe-me de volta para a porta externa do santuário, que dava para o lado leste, e ela estava trancada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21602, 'nvi', 'Ezequiel', 'ez', 44, 2, 'O Senhor me disse: "Esta porta deve permanecer trancada. Não deverá ser aberta; ninguém poderá entrar por ela. Deve permanecer trancada porque o Senhor, o Deus de Israel, entrou por ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21603, 'nvi', 'Ezequiel', 'ez', 44, 3, 'O príncipe é o único que poderá entrar e sentar-se dentro para comer na presença do Senhor. Ele entrará pelo pórtico da entrada e sairá pelo mesmo caminho".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21604, 'nvi', 'Ezequiel', 'ez', 44, 4, 'Então o homem levou-me até a frente do templo, passando pela porta norte. Olhei e vi a glória do Senhor enchendo o templo do Senhor, e prostrei-me, rosto em terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21605, 'nvi', 'Ezequiel', 'ez', 44, 5, 'O Senhor me disse: "Filho do homem preste atenção, olhe e ouça atentamente tudo o que eu lhe disser acerca de todos os regulamentos a respeito do templo do Senhor. Preste atenção à entrada do templo e a todas as saídas do santuário.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21606, 'nvi', 'Ezequiel', 'ez', 44, 6, 'Diga à rebelde nação de Israel: ‘Assim diz o Soberano Senhor: Já bastam suas práticas repugnantes, ó nação de Israel!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21607, 'nvi', 'Ezequiel', 'ez', 44, 7, 'Além de todas as suas outras práticas repugnantes, vocês trouxeram estrangeiros incircuncisos no coração e na carne para dentro do meu santuário, profanando o meu templo enquanto me ofereciam comida, gordura e sangue, e assim vocês romperam a minha aliança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21608, 'nvi', 'Ezequiel', 'ez', 44, 8, 'Ao invés de cumprirem seu dever quanto às minhas coisas sagradas, vocês encarregaram outros do meu santuário.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21609, 'nvi', 'Ezequiel', 'ez', 44, 9, 'Assim diz o Soberano Senhor: Nenhum estrangeiro incircunciso no coração e na carne entrará no meu santuário, nem tampouco os estrangeiros que vivem entre os israelitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21610, 'nvi', 'Ezequiel', 'ez', 44, 10, '" ‘Os levitas que tanto se distanciaram de mim quando Israel se desviou e que vaguearam para longe de mim, indo atrás de seus ídolos, sofrerão as conseqüências de sua iniqüidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21611, 'nvi', 'Ezequiel', 'ez', 44, 11, 'Poderão servir no meu santuário, como encarregados das portas do templo e servindo nele; poderão matar os animais dos holocaustos e outros sacrifícios em lugar do povo e colocar-se diante do povo e servi-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21612, 'nvi', 'Ezequiel', 'ez', 44, 12, 'Mas, porque os serviram na presença de seus ídolos e fizeram a nação de Israel cair em pecado, jurei de mão erguida que eles sofrerão as conseqüências de sua iniqüidade, palavra do Soberano Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21613, 'nvi', 'Ezequiel', 'ez', 44, 13, 'Não se aproximarão para me servir como sacerdotes nem se aproximarão de nenhuma de minhas coisas sagradas e das minhas ofertas santíssimas; carregarão a vergonha de suas práticas repugnantes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21614, 'nvi', 'Ezequiel', 'ez', 44, 14, 'Contudo, eu os encarregarei dos deveres do templo e de todo o trabalho que nele deve ser feito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21615, 'nvi', 'Ezequiel', 'ez', 44, 15, '" ‘Contudo, os sacerdotes levitas e descendentes de Zadoque e que fielmente executaram os deveres do meu santuário quando os israelitas se desviaram de mim, se aproximarão para ministrar diante de mim; eles ficarão diante de mim para oferecer sacrifícios de gordura e de sangue, palavra do Soberano Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21616, 'nvi', 'Ezequiel', 'ez', 44, 16, 'Só eles entrarão em meu santuário e se aproximarão da minha mesa para ministrar diante de mim e realizar o meu serviço.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21617, 'nvi', 'Ezequiel', 'ez', 44, 17, '" ‘Quando entrarem pelas portas do pátio interno, estejam vestindo roupas de linho; não usem nenhuma veste de lã enquanto estiverem ministrando junto às portas do pátio interno ou dentro do templo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21618, 'nvi', 'Ezequiel', 'ez', 44, 18, 'Usarão turbantes de linho na cabeça e calções de linho na cintura. Não vestirão nada que os faça transpirar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21619, 'nvi', 'Ezequiel', 'ez', 44, 19, 'Quando saírem para o pátio externo onde fica o povo, tirarão as roupas com que estiveram ministrando e as deixarão nos quartos sagrados, e vestirão outras roupas, para que não consagrem o povo por meio de suas roupas sacerdotais.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21620, 'nvi', 'Ezequiel', 'ez', 44, 20, '" ‘Não raparão a cabeça nem deixarão o cabelo comprido, mas o manterão aparado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21621, 'nvi', 'Ezequiel', 'ez', 44, 21, 'Nenhum sacerdote beberá vinho quando entrar no pátio interno.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21622, 'nvi', 'Ezequiel', 'ez', 44, 22, 'Eles não se casarão com viúva ou divorciada; só poderão casar-se com mulher virgem, de ascendência israelita, ou com viúva de sacerdote.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21623, 'nvi', 'Ezequiel', 'ez', 44, 23, 'Eles ensinarão ao meu povo a diferença entre o santo e o comum e lhe mostrarão como fazer distinção entre o puro e o impuro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21624, 'nvi', 'Ezequiel', 'ez', 44, 24, '" ‘Em qualquer disputa, os sacerdotes servirão como juízes e a decisão será tomada de acordo com as minhas sentenças. Eles obedecerão às minhas leis e aos meus decretos com respeito a todas as minhas festas fixas, e manterão santos os meus sábados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21625, 'nvi', 'Ezequiel', 'ez', 44, 25, '" ‘O sacerdote não se contaminará por aproximar-se do cadáver de alguém; no entanto, se o morto for seu pai, sua mãe, seu filho, sua filha, seu irmão ou sua irmã não-casada, ele poderá contaminar-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21626, 'nvi', 'Ezequiel', 'ez', 44, 26, 'Depois de se purificar, esperará sete dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21627, 'nvi', 'Ezequiel', 'ez', 44, 27, 'No dia em que entrar no pátio interno do santuário para ministrar no santuário, ele oferecerá em favor de si mesmo uma oferta pelo pecado, palavra do Soberano Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21628, 'nvi', 'Ezequiel', 'ez', 44, 28, '" ‘Eu serei a única herança dos sacerdotes. Vocês não lhes darão propriedade alguma em Israel; eu serei a sua herança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21629, 'nvi', 'Ezequiel', 'ez', 44, 29, 'Eles comerão as ofertas de cereal, as ofertas pelo pecado e as ofertas pela culpa; e tudo o que em Israel for consagrado ao Senhor será deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21630, 'nvi', 'Ezequiel', 'ez', 44, 30, 'O melhor de todos os primeiros frutos e de todas as contribuições que vocês fizerem pertencerá aos sacerdotes. Vocês darão a eles a primeira porção de sua refeição de cereal moído, para que haja bênçãos sobre as suas casas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21631, 'nvi', 'Ezequiel', 'ez', 44, 31, 'Os sacerdotes não comerão a carne de aves ou de animais, encontrados mortos ou despedaçados por animais selvagens.');
