INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20579, 'nvi', 'Ezequiel', 'ez', 7, 1, 'Veio a mim esta palavra do Senhor:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20580, 'nvi', 'Ezequiel', 'ez', 7, 2, '"Filho do homem, assim diz o Soberano Senhor à nação de Israel: Chegou o fim! O fim chegou aos quatro cantos da terra de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20581, 'nvi', 'Ezequiel', 'ez', 7, 3, 'O fim está agora sobre você, e sobre você eu soltarei a minha ira. Eu a julgarei de acordo com a sua conduta e lhe retribuirei todas as suas práticas repugnantes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20582, 'nvi', 'Ezequiel', 'ez', 7, 4, 'Não olharei com piedade para você nem a pouparei; com certeza eu lhe retribuirei sua conduta e suas práticas em seu meio. Então você saberá que eu sou o Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20583, 'nvi', 'Ezequiel', 'ez', 7, 5, 'Assim diz o Soberano Senhor: Chegou a desgraça! Uma desgraça jamais imaginada vem aí.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20584, 'nvi', 'Ezequiel', 'ez', 7, 6, 'Chegou o fim! Chegou o fim! Ele se insurgiu contra você. O fim chegou!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20585, 'nvi', 'Ezequiel', 'ez', 7, 7, 'A condenação chegou sobre você, você que habita no país. Chegou a hora, o dia está próximo; há pânico, e não alegria, sobre os montes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20586, 'nvi', 'Ezequiel', 'ez', 7, 8, 'Estou para derramar a minha ira sobre você e esgotar a minha indignação contra você; eu a julgarei de acordo com a sua conduta e lhe retribuirei todas as suas práticas repugnantes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20587, 'nvi', 'Ezequiel', 'ez', 7, 9, 'Não olharei com piedade para você nem a pouparei; eu lhe retribuirei de acordo com todas as práticas repugnantes que há no seu meio. Então tu saberás que é o Senhor que desfere o golpe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20588, 'nvi', 'Ezequiel', 'ez', 7, 10, '"Aqui está o dia! Já chegou! A condenação irrompeu, a vara brotou, a arrogância floresceu!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20589, 'nvi', 'Ezequiel', 'ez', 7, 11, 'A violência encarnou-se numa vara para castigar a maldade; ninguém do povo será deixado, ninguém daquela multidão, como também nenhuma riqueza, nada que tenha algum valor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20590, 'nvi', 'Ezequiel', 'ez', 7, 12, 'Chegou a hora, o dia chegou. Que o comprador não se regozije nem o vendedor se entristeça, pois a ira está sobre toda a multidão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20591, 'nvi', 'Ezequiel', 'ez', 7, 13, 'Nenhum vendedor viverá o suficiente para recuperar a terra que vendeu, mesmo que viva por muito tempo, pois a visão acerca de toda a multidão não voltará atrás. Por causa de sua iniqüidade, a vida de ninguém será preservada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20592, 'nvi', 'Ezequiel', 'ez', 7, 14, 'Embora toquem a trombeta e deixem tudo pronto, ninguém irá a combate, pois a minha ira está sobre toda a sua multidão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20593, 'nvi', 'Ezequiel', 'ez', 7, 15, '"Fora está a espada, dentro estão a peste e a fome; quem estiver no campo morrerá pela espada, e quem estiver na cidade será devorado pela fome e pela peste.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20594, 'nvi', 'Ezequiel', 'ez', 7, 16, 'Todos os que se livrarem e escaparem estarão nos montes, gemendo como pombas nos vales, cada um por causa de sua própria iniqüidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20595, 'nvi', 'Ezequiel', 'ez', 7, 17, 'Toda mão ficará pendendo, frouxa, e todo joelho ficará como água, de tão fraco.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20596, 'nvi', 'Ezequiel', 'ez', 7, 18, 'Eles se cobrirão de vestes de luto e se vestirão de pavor. Seus rostos serão cobertos de vergonha, e suas cabeças serão rapadas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20597, 'nvi', 'Ezequiel', 'ez', 7, 19, 'Atirarão sua prata nas ruas, e seu ouro será tratado como coisa impura. Sua prata e seu ouro serão incapazes de livrá-los no dia da ira do Senhor e, não poderão saciar sua fome e encher os seus estômagos; serviram apenas para fazê-los tropeçar na iniqüidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20598, 'nvi', 'Ezequiel', 'ez', 7, 20, 'Tinham orgulho de suas lindas jóias e as usavam para fazer os seus ídolos repugnantes e as suas imagens detestáveis. Por isso tornarei essas coisas em algo impuro para eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20599, 'nvi', 'Ezequiel', 'ez', 7, 21, 'Entregarei tudo isso como despojo nas mãos de estrangeiros e como saque nas mãos dos ímpios da terra, e eles o contaminarão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20600, 'nvi', 'Ezequiel', 'ez', 7, 22, 'Desviarei deles o meu rosto, e eles profanarão o lugar que tanto amo; nele entrarão ladrões e o profanarão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20601, 'nvi', 'Ezequiel', 'ez', 7, 23, '"Preparem correntes, porque a terra está cheia de sangue derramado e a cidade está cheia de violência.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20602, 'nvi', 'Ezequiel', 'ez', 7, 24, 'Trarei os piores elementos das nações para se apossarem das casas deles; darei fim ao orgulho dos poderosos, e os santuários deles serão profanados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20603, 'nvi', 'Ezequiel', 'ez', 7, 25, 'Quando chegar o pavor, eles buscarão paz, mas não a encontrarão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20604, 'nvi', 'Ezequiel', 'ez', 7, 26, 'Virá uma desgraça após a outra, e um alarme após o outro. Tentarão conseguir uma visão da parte do profeta; o ensino da lei pelo sacerdote se perderá, como também o conselho das autoridades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20605, 'nvi', 'Ezequiel', 'ez', 7, 27, 'O rei pranteará, o príncipe se vestirá de desespero, e as mãos do povo da terra tremerão. Lidarei com eles de acordo com a sua conduta, e pelos padrões deles mesmos eu os julgarei. Então saberão que eu sou o Senhor".');
