INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29940, 'nvi', 'Filemom', 'fm', 1, 1, 'Paulo, prisioneiro de Cristo Jesus, e o irmão Timóteo, a você, Filemom, nosso amado cooperador,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29941, 'nvi', 'Filemom', 'fm', 1, 2, 'à irmã Áfia, a Arquipo, nosso companheiro de lutas, e à igreja que se reúne com você em sua casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29942, 'nvi', 'Filemom', 'fm', 1, 3, 'A vocês, graça e paz da parte de Deus nosso Pai e do Senhor Jesus Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29943, 'nvi', 'Filemom', 'fm', 1, 4, 'Sempre dou graças a meu Deus, lembrando-me de você nas minhas orações,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29944, 'nvi', 'Filemom', 'fm', 1, 5, 'porque ouço falar da sua fé no Senhor Jesus e do seu amor por todos os santos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29945, 'nvi', 'Filemom', 'fm', 1, 6, 'Oro para que a comunhão que procede da sua fé seja eficaz no pleno conhecimento de todo o bem que temos em Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29946, 'nvi', 'Filemom', 'fm', 1, 7, 'Seu amor me tem dado grande alegria e consolação, porque você, irmão, tem reanimado o coração dos santos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29947, 'nvi', 'Filemom', 'fm', 1, 8, 'Por isso, mesmo tendo em Cristo plena liberdade para mandar que você cumpra o seu dever,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29948, 'nvi', 'Filemom', 'fm', 1, 9, 'prefiro fazer um apelo com base no amor. Eu, Paulo, já velho, e agora também prisioneiro de Cristo Jesus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29949, 'nvi', 'Filemom', 'fm', 1, 10, 'apelo em favor de meu filho Onésimo, que gerei enquanto estava preso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29950, 'nvi', 'Filemom', 'fm', 1, 11, 'Ele antes lhe era inútil, mas agora é útil, tanto para você quanto para mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29951, 'nvi', 'Filemom', 'fm', 1, 12, 'Mando-o de volta a você, como se fosse o meu próprio coração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29952, 'nvi', 'Filemom', 'fm', 1, 13, 'Gostaria de mantê-lo comigo para que me ajudasse em seu lugar enquanto estou preso por causa do evangelho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29953, 'nvi', 'Filemom', 'fm', 1, 14, 'Mas não quis fazer nada sem a sua permissão, para que qualquer favor que você fizer seja espontâneo, e não forçado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29954, 'nvi', 'Filemom', 'fm', 1, 15, 'Talvez ele tenha sido separado de você por algum tempo, para que você o tivesse de volta para sempre,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29955, 'nvi', 'Filemom', 'fm', 1, 16, 'não mais como escravo, mas, acima de escravo, como irmão amado. Para mim ele é um irmão muito amado, e ainda mais para você, tanto como pessoa quanto como cristão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29956, 'nvi', 'Filemom', 'fm', 1, 17, 'Assim, se você me considera companheiro na fé, receba-o como se estivesse recebendo a mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29957, 'nvi', 'Filemom', 'fm', 1, 18, 'Se ele o prejudicou em algo ou lhe deve alguma coisa, ponha na minha conta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29958, 'nvi', 'Filemom', 'fm', 1, 19, 'Eu, Paulo, escrevo de próprio punho: Eu pagarei — para não dizer que você me deve a sua própria pessoa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29959, 'nvi', 'Filemom', 'fm', 1, 20, 'Sim, irmão, eu gostaria de receber de você algum benefício por estarmos no Senhor. Reanime o meu coração em Cristo!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29960, 'nvi', 'Filemom', 'fm', 1, 21, 'Escrevo-lhe certo de que você me obedecerá, sabendo que fará ainda mais do lhe que peço.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29961, 'nvi', 'Filemom', 'fm', 1, 22, 'Além disso, prepare-me um aposento, porque, graças às suas orações, espero poder ser restituído a vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29962, 'nvi', 'Filemom', 'fm', 1, 23, 'Epafras, meu companheiro de prisão por causa de Cristo Jesus, envia-lhe saudações,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29963, 'nvi', 'Filemom', 'fm', 1, 24, 'assim como também Marcos, Aristarco, Demas e Lucas, meus cooperadores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29964, 'nvi', 'Filemom', 'fm', 1, 25, 'A graça do Senhor Jesus Cristo seja com o espírito de todos vocês.');
