INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29363, 'nvi', 'Filipenses', 'fp', 1, 1, 'Paulo e Timóteo, servos de Cristo Jesus, a todos os santos em Cristo Jesus que estão em Filipos, juntamente com os bispos e diáconos:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29364, 'nvi', 'Filipenses', 'fp', 1, 2, 'A vocês, graça e paz da parte de Deus nosso Pai e do Senhor Jesus Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29365, 'nvi', 'Filipenses', 'fp', 1, 3, 'Agradeço a meu Deus toda vez que me lembro de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29366, 'nvi', 'Filipenses', 'fp', 1, 4, 'Em todas as minhas orações em favor de vocês, sempre oro com alegria');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29367, 'nvi', 'Filipenses', 'fp', 1, 5, 'por causa da cooperação que vocês têm dado ao evangelho, desde o primeiro dia até agora.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29368, 'nvi', 'Filipenses', 'fp', 1, 6, 'Estou convencido de que aquele que começou boa obra em vocês, vai completá-la até o dia de Cristo Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29369, 'nvi', 'Filipenses', 'fp', 1, 7, 'É justo que eu assim me sinta a respeito de todos vocês, uma vez que os tenho em meu coração, pois, quer nas correntes que me prendem quer defendendo e confirmando o evangelho, todos vocês participam comigo da graça de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29370, 'nvi', 'Filipenses', 'fp', 1, 8, 'Deus é minha testemunha de como tenho saudade de todos vocês, com a profunda afeição de Cristo Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29371, 'nvi', 'Filipenses', 'fp', 1, 9, 'Esta é a minha oração: que o amor de vocês aumente cada vez mais em conhecimento e em toda a percepção,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29372, 'nvi', 'Filipenses', 'fp', 1, 10, 'para discernirem o que é melhor, a fim de serem puros e irrepreensíveis até o dia de Cristo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29373, 'nvi', 'Filipenses', 'fp', 1, 11, 'cheios do fruto da justiça, fruto que vem por meio de Jesus Cristo, para glória e louvor de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29374, 'nvi', 'Filipenses', 'fp', 1, 12, 'Quero que saibam, irmãos, que aquilo que me aconteceu tem antes servido para o progresso do evangelho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29375, 'nvi', 'Filipenses', 'fp', 1, 13, 'Como resultado, tornou-se evidente a toda a guarda do palácio e a todos os demais que estou na prisão por causa de Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29376, 'nvi', 'Filipenses', 'fp', 1, 14, 'E a maioria dos irmãos, motivados no Senhor pela minha prisão, estão anunciando a palavra com maior determinação e destemor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29377, 'nvi', 'Filipenses', 'fp', 1, 15, 'É verdade que alguns pregam a Cristo por inveja e rivalidade, mas outros o fazem de boa vontade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29378, 'nvi', 'Filipenses', 'fp', 1, 16, 'Estes o fazem por amor, sabendo que aqui me encontro para a defesa do evangelho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29379, 'nvi', 'Filipenses', 'fp', 1, 17, 'Aqueles pregam a Cristo por ambição egoísta, sem sinceridade, pensando que me podem causar sofrimento enquanto estou preso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29380, 'nvi', 'Filipenses', 'fp', 1, 18, 'Mas, que importa? O importante é que de qualquer forma, seja por motivos falsos ou verdadeiros, Cristo está sendo pregado, e por isso me alegro. De fato, continuarei a alegrar-me,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29381, 'nvi', 'Filipenses', 'fp', 1, 19, 'pois sei que o que me aconteceu resultará em minha libertação, graças às orações de vocês e ao auxílio do Espírito de Jesus Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29382, 'nvi', 'Filipenses', 'fp', 1, 20, 'Aguardo ansiosamente e espero que em nada serei envergonhado. Pelo contrário, com toda a determinação de sempre, também agora Cristo será engrandecido em meu corpo, quer pela vida quer pela morte;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29383, 'nvi', 'Filipenses', 'fp', 1, 21, 'porque para mim o viver é Cristo e o morrer é lucro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29384, 'nvi', 'Filipenses', 'fp', 1, 22, 'Caso continue vivendo no corpo, terei fruto do meu trabalho. E já não sei o que escolher!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29385, 'nvi', 'Filipenses', 'fp', 1, 23, 'Estou pressionado dos dois lados: desejo partir e estar com Cristo, o que é muito melhor;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29386, 'nvi', 'Filipenses', 'fp', 1, 24, 'contudo, é mais necessário, por causa de vocês, que eu permaneça no corpo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29387, 'nvi', 'Filipenses', 'fp', 1, 25, 'Convencido disso, sei que vou permanecer e continuar com todos vocês, para o seu progresso e alegria na fé,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29388, 'nvi', 'Filipenses', 'fp', 1, 26, 'a fim de que, pela minha presença, outra vez a exultação de vocês em Cristo Jesus transborde por minha causa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29389, 'nvi', 'Filipenses', 'fp', 1, 27, 'Não importa o que aconteça, exerçam a sua cidadania de maneira digna do evangelho de Cristo, para que assim, quer eu vá e os veja, quer apenas ouça a seu respeito em minha ausência, fique eu sabendo que vocês permanecem firmes num só espírito, lutando unânimes pela fé evangélica,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29390, 'nvi', 'Filipenses', 'fp', 1, 28, 'sem de forma alguma deixar-se intimidar por aqueles que se opõem a vocês. Para eles isso é sinal de destruição, mas para vocês de salvação, e isso da parte de Deus;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29391, 'nvi', 'Filipenses', 'fp', 1, 29, 'pois a vocês foi dado o privilégio de, não apenas crer em Cristo, mas também de sofrer por ele,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29392, 'nvi', 'Filipenses', 'fp', 1, 30, 'já que estão passando pelo mesmo combate que me viram enfrentar e agora ouvem que ainda enfrento.');
