INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29393, 'nvi', 'Filipenses', 'fp', 2, 1, 'Se por estarmos em Cristo, nós temos alguma motivação, alguma exortação de amor, alguma comunhão no Espírito, alguma profunda afeição e compaixão,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29394, 'nvi', 'Filipenses', 'fp', 2, 2, 'completem a minha alegria, tendo o mesmo modo de pensar, o mesmo amor, um só espírito e uma só atitude.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29395, 'nvi', 'Filipenses', 'fp', 2, 3, 'Nada façam por ambição egoísta ou por vaidade, mas humildemente considerem os outros superiores a si mesmos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29396, 'nvi', 'Filipenses', 'fp', 2, 4, 'Cada um cuide, não somente dos seus interesses, mas também dos interesses dos outros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29397, 'nvi', 'Filipenses', 'fp', 2, 5, 'Seja a atitude de vocês a mesma de Cristo Jesus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29398, 'nvi', 'Filipenses', 'fp', 2, 6, 'que, embora sendo Deus, não considerou que o ser igual a Deus era algo a que devia apegar-se;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29399, 'nvi', 'Filipenses', 'fp', 2, 7, 'mas esvaziou-se a si mesmo, vindo a ser servo, tornando-se semelhante aos homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29400, 'nvi', 'Filipenses', 'fp', 2, 8, 'E, sendo encontrado em forma humana, humilhou-se a si mesmo e foi obediente até à morte, e morte de cruz!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29401, 'nvi', 'Filipenses', 'fp', 2, 9, 'Por isso Deus o exaltou à mais alta posição e lhe deu o nome que está acima de todo nome,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29402, 'nvi', 'Filipenses', 'fp', 2, 10, 'para que ao nome de Jesus se dobre todo joelho, no céu, na terra e debaixo da terra,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29403, 'nvi', 'Filipenses', 'fp', 2, 11, 'e toda língua confesse que Jesus Cristo é o Senhor, para a glória de Deus Pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29404, 'nvi', 'Filipenses', 'fp', 2, 12, 'Assim, meus amados, como sempre vocês obedeceram, não apenas em minha presença, porém muito mais agora na minha ausência, ponham em ação a salvação de vocês com temor e tremor,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29405, 'nvi', 'Filipenses', 'fp', 2, 13, 'pois é Deus quem efetua em vocês tanto o querer quanto o realizar, de acordo com a boa vontade dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29406, 'nvi', 'Filipenses', 'fp', 2, 14, 'Façam tudo sem queixas nem discussões,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29407, 'nvi', 'Filipenses', 'fp', 2, 15, 'para que venham a tornar-se puros e irrepreensíveis, filhos de Deus inculpáveis no meio de uma geração corrompida e depravada, na qual vocês brilham como estrelas no universo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29408, 'nvi', 'Filipenses', 'fp', 2, 16, 'retendo firmemente a palavra da vida. Assim, no dia de Cristo eu me orgulharei de não ter corrido nem me esforçado inutilmente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29409, 'nvi', 'Filipenses', 'fp', 2, 17, 'Contudo, mesmo que eu esteja sendo derramado como oferta de bebida sobre o serviço que provém da fé que vocês têm, o sacrifício que oferecem a Deus, estou alegre e me regozijo com todos vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29410, 'nvi', 'Filipenses', 'fp', 2, 18, 'Estejam vocês também alegres, e regozijem-se comigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29411, 'nvi', 'Filipenses', 'fp', 2, 19, 'Espero no Senhor Jesus enviar-lhes Timóteo brevemente, para que eu também me sinta animado quando receber notícias de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29412, 'nvi', 'Filipenses', 'fp', 2, 20, 'Não tenho ninguém como ele, que tenha interesse sincero pelo bem-estar de vocês,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29413, 'nvi', 'Filipenses', 'fp', 2, 21, 'pois todos buscam os seus próprios interesses e não os de Jesus Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29414, 'nvi', 'Filipenses', 'fp', 2, 22, 'Mas vocês sabem que Timóteo foi aprovado, porque serviu comigo no trabalho do evangelho como um filho ao lado de seu pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29415, 'nvi', 'Filipenses', 'fp', 2, 23, 'Portanto, é ele quem espero enviar, tão logo me certifique da minha situação,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29416, 'nvi', 'Filipenses', 'fp', 2, 24, 'confiando no Senhor que em breve também poderei ir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29417, 'nvi', 'Filipenses', 'fp', 2, 25, 'Contudo, penso que será necessário enviar-lhes de volta Epafrodito, meu irmão, cooperador e companheiro de lutas, mensageiro que vocês enviaram para atender às minhas necessidades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29418, 'nvi', 'Filipenses', 'fp', 2, 26, 'Pois ele tem saudade de todos vocês e está angustiado porque ficaram sabendo que ele esteve doente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29419, 'nvi', 'Filipenses', 'fp', 2, 27, 'De fato, ficou doente e quase morreu. Mas Deus teve misericórdia dele, e não somente dele, mas também de mim, para que eu não tivesse tristeza sobre tristeza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29420, 'nvi', 'Filipenses', 'fp', 2, 28, 'Por isso, logo o enviarei, para que, quando o virem novamente, fiquem alegres e eu tenha menos tristeza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29421, 'nvi', 'Filipenses', 'fp', 2, 29, 'E peço que vocês o recebam no Senhor com grande alegria e honrem a homens como este,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29422, 'nvi', 'Filipenses', 'fp', 2, 30, 'porque ele quase morreu por amor à causa de Cristo, arriscando a vida para suprir a ajuda que vocês não me podiam dar.');
