INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29444, 'nvi', 'Filipenses', 'fp', 4, 1, 'Portanto, meus irmãos, a quem amo e de quem tenho saudade, vocês que são a minha alegria e a minha coroa, permaneçam assim firmes no Senhor, ó amados!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29445, 'nvi', 'Filipenses', 'fp', 4, 2, 'O que eu rogo a Evódia e também a Síntique é que vivam em harmonia no Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29446, 'nvi', 'Filipenses', 'fp', 4, 3, 'Sim, e peço a você, leal companheiro de jugo, que as ajude; pois lutaram ao meu lado na causa do evangelho, com Clemente e meus demais cooperadores. Os seus nomes estão no livro da vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29447, 'nvi', 'Filipenses', 'fp', 4, 4, 'Alegrem-se sempre no Senhor. Novamente direi: alegrem-se!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29448, 'nvi', 'Filipenses', 'fp', 4, 5, 'Seja a amabilidade de vocês conhecida por todos. Perto está o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29449, 'nvi', 'Filipenses', 'fp', 4, 6, 'Não andem ansiosos por coisa alguma, mas em tudo, pela oração e súplicas, e com ação de graças, apresentem seus pedidos a Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29450, 'nvi', 'Filipenses', 'fp', 4, 7, 'E a paz de Deus, que excede todo o entendimento, guardará os seus corações e as suas mentes em Cristo Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29451, 'nvi', 'Filipenses', 'fp', 4, 8, 'Finalmente, irmãos, tudo o que for verdadeiro, tudo o que for nobre, tudo o que for correto, tudo o que for puro, tudo o que for amável, tudo o que for de boa fama, se houver algo de excelente ou digno de louvor, pensem nessas coisas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29452, 'nvi', 'Filipenses', 'fp', 4, 9, 'Tudo o que vocês aprenderam, receberam, ouviram e viram em mim, ponham-no em prática. E o Deus da paz estará com vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29453, 'nvi', 'Filipenses', 'fp', 4, 10, 'Alegro-me grandemente no Senhor, porque finalmente vocês renovaram o seu interesse por mim. De fato, vocês já se interessavam, mas não tinham oportunidade para demonstrá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29454, 'nvi', 'Filipenses', 'fp', 4, 11, 'Não estou dizendo isso porque esteja necessitado, pois aprendi a adaptar-me a toda e qualquer circunstância.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29455, 'nvi', 'Filipenses', 'fp', 4, 12, 'Sei o que é passar necessidade e sei o que é ter fartura. Aprendi o segredo de viver contente em toda e qualquer situação, seja bem alimentado, seja com fome, tendo muito, ou passando necessidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29456, 'nvi', 'Filipenses', 'fp', 4, 13, 'Tudo posso naquele que me fortalece.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29457, 'nvi', 'Filipenses', 'fp', 4, 14, 'Apesar disso, vocês fizeram bem em participar de minhas tribulações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29458, 'nvi', 'Filipenses', 'fp', 4, 15, 'Como vocês sabem, filipenses, nos seus primeiros dias no evangelho, quando parti da Macedônia, nenhuma igreja partilhou comigo no que se refere a dar e receber, exceto vocês;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29459, 'nvi', 'Filipenses', 'fp', 4, 16, 'pois, estando eu em Tessalônica, vocês me mandaram ajuda, não apenas uma vez, mas duas, quando tive necessidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29460, 'nvi', 'Filipenses', 'fp', 4, 17, 'Não que eu esteja procurando ofertas, mas o que pode ser creditado na conta de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29461, 'nvi', 'Filipenses', 'fp', 4, 18, 'Recebi tudo, e o que tenho é mais que suficiente. Estou amplamente suprido, agora que recebi de Epafrodito os donativos que vocês enviaram. Elas são uma oferta de aroma suave, um sacrifício aceitável e agradável a Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29462, 'nvi', 'Filipenses', 'fp', 4, 19, 'O meu Deus suprirá todas as necessidades de vocês, de acordo com as suas gloriosas riquezas em Cristo Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29463, 'nvi', 'Filipenses', 'fp', 4, 20, 'A nosso Deus e Pai seja a glória para todo o sempre. Amém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29464, 'nvi', 'Filipenses', 'fp', 4, 21, 'Saúdem a todos os santos em Cristo Jesus. Os irmãos que estão comigo enviam saudações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29465, 'nvi', 'Filipenses', 'fp', 4, 22, 'Todos os santos lhes enviam saudações, especialmente os que estão no palácio de César.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29466, 'nvi', 'Filipenses', 'fp', 4, 23, 'A graça do Senhor Jesus Cristo seja com o espírito de vocês. Amém.');
