INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29059, 'nvi', 'Gálatas', 'gl', 1, 1, 'Paulo, apóstolo enviado, não da parte de homens nem por meio de pessoa alguma, mas por Jesus Cristo e por Deus Pai, que o ressuscitou dos mortos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29060, 'nvi', 'Gálatas', 'gl', 1, 2, 'e todos os irmãos que estão comigo, às igrejas da Galácia:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29061, 'nvi', 'Gálatas', 'gl', 1, 3, 'A vocês, graça e paz da parte de Deus nosso Pai e do Senhor Jesus Cristo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29062, 'nvi', 'Gálatas', 'gl', 1, 4, 'que se entregou a si mesmo por nossos pecados a fim de nos resgatar desta presente era perversa, segundo a vontade de nosso Deus e Pai,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29063, 'nvi', 'Gálatas', 'gl', 1, 5, 'a quem seja a glória para todo o sempre. Amém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29064, 'nvi', 'Gálatas', 'gl', 1, 6, 'Admiro-me de que vocês estejam abandonando tão rapidamente aquele que os chamou pela graça de Cristo, para seguirem outro evangelho');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29065, 'nvi', 'Gálatas', 'gl', 1, 7, 'que, na realidade, não é o evangelho. O que ocorre é que algumas pessoas os estão perturbando, querendo perverter o evangelho de Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29066, 'nvi', 'Gálatas', 'gl', 1, 8, 'Mas ainda que nós ou um anjo do céu pregue um evangelho diferente daquele que lhes pregamos, que seja amaldiçoado!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29067, 'nvi', 'Gálatas', 'gl', 1, 9, 'Como já dissemos, agora repito: Se alguém lhes anuncia um evangelho diferente daquele que já receberam, que seja amaldiçoado!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29068, 'nvi', 'Gálatas', 'gl', 1, 10, 'Acaso busco eu agora a aprovação dos homens ou a de Deus? Ou estou tentando agradar a homens? Se eu ainda estivesse procurando agradar a homens, não seria servo de Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29069, 'nvi', 'Gálatas', 'gl', 1, 11, 'Irmãos, quero que saibam que o evangelho por mim anunciado não é de origem humana.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29070, 'nvi', 'Gálatas', 'gl', 1, 12, 'Não o recebi de pessoa alguma nem me foi ele ensinado; pelo contrário, eu o recebi de Jesus Cristo por revelação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29071, 'nvi', 'Gálatas', 'gl', 1, 13, 'Vocês ouviram qual foi o meu procedimento no judaísmo, como perseguia com violência a igreja de Deus, procurando destruí-la.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29072, 'nvi', 'Gálatas', 'gl', 1, 14, 'No judaísmo, eu superava a maioria dos judeus da minha idade, e era extremamente zeloso das tradições dos meus antepassados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29073, 'nvi', 'Gálatas', 'gl', 1, 15, 'Mas Deus me separou desde o ventre materno e me chamou por sua graça. Quando lhe agradou');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29074, 'nvi', 'Gálatas', 'gl', 1, 16, 'revelar o seu Filho em mim para que eu o anunciasse entre os gentios, não consultei pessoa alguma.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29075, 'nvi', 'Gálatas', 'gl', 1, 17, 'Tampouco subi a Jerusalém para ver os que já eram apóstolos antes de mim, mas de imediato parti para a Arábia, e tornei a voltar a Damasco.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29076, 'nvi', 'Gálatas', 'gl', 1, 18, 'Depois de três anos, subi a Jerusalém para conhecer Pedro pessoalmente, e estive com ele quinze dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29077, 'nvi', 'Gálatas', 'gl', 1, 19, 'Não vi nenhum dos outros apóstolos, a não ser Tiago, irmão do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29078, 'nvi', 'Gálatas', 'gl', 1, 20, 'Quanto ao que lhes escrevo, afirmo diante de Deus que não minto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29079, 'nvi', 'Gálatas', 'gl', 1, 21, 'A seguir, fui para as regiões da Síria e da Cilícia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29080, 'nvi', 'Gálatas', 'gl', 1, 22, 'Eu não era pessoalmente conhecido pelas igrejas da Judéia que estão em Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29081, 'nvi', 'Gálatas', 'gl', 1, 23, 'Apenas ouviam dizer: "Aquele que antes nos perseguia, agora está anunciando a fé que outrora procurava destruir".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29082, 'nvi', 'Gálatas', 'gl', 1, 24, 'E glorificavam a Deus por minha causa.');
