INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29083, 'nvi', 'Gálatas', 'gl', 2, 1, 'Catorze anos depois, subi novamente a Jerusalém, dessa vez com Barnabé, levando também Tito comigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29084, 'nvi', 'Gálatas', 'gl', 2, 2, 'Fui para lá por causa de uma revelação e expus diante deles o evangelho que prego entre os gentios, fazendo-o, porém, em particular aos que pareciam mais influentes, para não correr ou ter corrido em vão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29085, 'nvi', 'Gálatas', 'gl', 2, 3, 'Mas nem mesmo Tito, que estava comigo, foi obrigado a circuncidar-se, apesar de ser grego.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29086, 'nvi', 'Gálatas', 'gl', 2, 4, 'Essa questão foi levantada porque alguns falsos irmãos infiltraram-se em nosso meio para espionar a liberdade que temos em Cristo Jesus e nos reduzir à escravidão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29087, 'nvi', 'Gálatas', 'gl', 2, 5, 'Não nos submetemos a eles nem por um instante, para que a verdade do evangelho permanecesse com vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29088, 'nvi', 'Gálatas', 'gl', 2, 6, 'Quanto aos que pareciam influentes — o que eram então não faz diferença para mim; Deus não julga pela aparência — tais homens influentes não me acrescentaram nada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29089, 'nvi', 'Gálatas', 'gl', 2, 7, 'Pelo contrário, reconheceram que a mim havia sido confiada a pregação do evangelho aos incircuncisos, assim como a Pedro, aos circuncisos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29090, 'nvi', 'Gálatas', 'gl', 2, 8, 'Pois Deus, que operou por meio de Pedro como apóstolo aos circuncisos, também operou por meu intermédio para com os gentios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29091, 'nvi', 'Gálatas', 'gl', 2, 9, 'Reconhecendo a graça que me fora concedida, Tiago, Pedro e João, tidos como colunas, estenderam a mão direita a mim e a Barnabé em sinal de comunhão. Eles concordaram em que devíamos nos dirigir aos gentios, e eles, aos circuncisos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29092, 'nvi', 'Gálatas', 'gl', 2, 10, 'Somente pediram que nos lembrássemos dos pobres, o que me esforcei por fazer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29093, 'nvi', 'Gálatas', 'gl', 2, 11, 'Quando, porém, Pedro veio a Antioquia, enfrentei-o face a face, por sua atitude condenável.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29094, 'nvi', 'Gálatas', 'gl', 2, 12, 'Pois, antes de chegarem alguns da parte de Tiago, ele comia com os gentios. Quando, porém, eles chegaram, afastou-se e separou-se dos gentios, temendo os que eram da circuncisão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29095, 'nvi', 'Gálatas', 'gl', 2, 13, 'Os demais judeus também se uniram a ele nessa hipocrisia, de modo que até Barnabé se deixou levar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29096, 'nvi', 'Gálatas', 'gl', 2, 14, 'Quando vi que não estavam andando de acordo com a verdade do evangelho, declarei a Pedro, diante de todos: "Você é judeu, mas vive como gentio e não como judeu. Portanto, como pode obrigar gentios a viverem como judeus?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29097, 'nvi', 'Gálatas', 'gl', 2, 15, '"Nós, judeus de nascimento e não ‘gentios pecadores’,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29098, 'nvi', 'Gálatas', 'gl', 2, 16, 'sabemos que o ninguém é justificado pela prática da lei, mas mediante a fé em Jesus Cristo. Assim, nós também cremos em Cristo Jesus para sermos justificados pela fé em Cristo, e não pela prática da lei, porque pela prática da lei ninguém será justificado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29099, 'nvi', 'Gálatas', 'gl', 2, 17, '"Se, porém, procurando ser justificados em Cristo descobrimos que nós mesmos somos pecadores, será Cristo então ministro do pecado? De modo algum!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29100, 'nvi', 'Gálatas', 'gl', 2, 18, 'Se reconstruo o que destruí, provo que sou transgressor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29101, 'nvi', 'Gálatas', 'gl', 2, 19, 'Pois, por meio da lei eu morri para a lei, a fim de viver para Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29102, 'nvi', 'Gálatas', 'gl', 2, 20, 'Fui crucificado com Cristo. Assim, já não sou eu quem vive, mas Cristo vive em mim. A vida que agora vivo no corpo, vivo-a pela fé no filho de Deus, que me amou e se entregou por mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29103, 'nvi', 'Gálatas', 'gl', 2, 21, 'Não anulo a graça de Deus; pois, se a justiça vem pela lei, Cristo morreu inutilmente! "');
