INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29133, 'nvi', 'Gálatas', 'gl', 4, 1, 'Digo porém que, enquanto o herdeiro é menor de idade, em nada difere de um escravo, embora seja dono de tudo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29134, 'nvi', 'Gálatas', 'gl', 4, 2, 'No entanto, ele está sujeito a guardiães e administradores até o tempo determinado por seu pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29135, 'nvi', 'Gálatas', 'gl', 4, 3, 'Assim também nós, quando éramos menores, estávamos escravizados aos princípios elementares do mundo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29136, 'nvi', 'Gálatas', 'gl', 4, 4, 'Mas, quando chegou a plenitude do tempo, Deus enviou seu Filho, nascido de mulher, nascido debaixo da lei,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29137, 'nvi', 'Gálatas', 'gl', 4, 5, 'a fim de redimir os que estavam sob a lei, para que recebêssemos a adoção de filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29138, 'nvi', 'Gálatas', 'gl', 4, 6, 'E, porque vocês são filhos, Deus enviou o Espírito de seu Filho aos seus corações, o qual clama: "Aba, Pai".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29139, 'nvi', 'Gálatas', 'gl', 4, 7, 'Assim, você já não é mais escravo, mas filho; e, por ser filho, Deus também o tornou herdeiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29140, 'nvi', 'Gálatas', 'gl', 4, 8, 'Antes, quando vocês não conheciam a Deus, eram escravos daqueles que, por natureza, não são deuses.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29141, 'nvi', 'Gálatas', 'gl', 4, 9, 'Mas agora, conhecendo a Deus, ou melhor, sendo por ele conhecidos, como é que estão voltando àqueles mesmos princípios elementares, fracos e sem poder? Querem ser escravizados por eles outra vez?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29142, 'nvi', 'Gálatas', 'gl', 4, 10, 'Vocês estão observando dias especiais, meses, ocasiões específicas e anos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29143, 'nvi', 'Gálatas', 'gl', 4, 11, 'Temo que os meus esforços por vocês tenham sido inúteis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29144, 'nvi', 'Gálatas', 'gl', 4, 12, 'Eu lhes suplico, irmãos, que se tornem como eu, pois eu me tornei como vocês. Em nada vocês me ofenderam;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29145, 'nvi', 'Gálatas', 'gl', 4, 13, 'como sabem, foi por causa de uma doença que lhes preguei o evangelho pela primeira vez.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29146, 'nvi', 'Gálatas', 'gl', 4, 14, 'Embora a minha doença lhes tenha sido uma provação, vocês não me trataram com desprezo ou desdém; pelo contrário, receberam-me como se eu fosse um anjo de Deus, como o próprio Cristo Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29147, 'nvi', 'Gálatas', 'gl', 4, 15, 'Que aconteceu com a alegria de vocês? Tenho certeza que, se fosse possível, vocês teriam arrancado os próprios olhos para dá-los a mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29148, 'nvi', 'Gálatas', 'gl', 4, 16, 'Tornei-me inimigo de vocês por lhes dizer a verdade?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29149, 'nvi', 'Gálatas', 'gl', 4, 17, 'Os que fazem tanto esforço para agradá-los, não agem bem, mas querem isolá-los a fim de que vocês também mostrem zelo por eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29150, 'nvi', 'Gálatas', 'gl', 4, 18, 'É bom sempre ser zeloso pelo bem, e não apenas quando estou presente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29151, 'nvi', 'Gálatas', 'gl', 4, 19, 'Meus filhos, novamente estou sofrendo dores de parto por sua causa, até que Cristo seja formado em vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29152, 'nvi', 'Gálatas', 'gl', 4, 20, 'Eu gostaria de estar com vocês agora e mudar o meu tom de voz, pois estou perplexo quanto a vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29153, 'nvi', 'Gálatas', 'gl', 4, 21, 'Digam-me vocês, os que querem estar debaixo da lei: Acaso vocês não ouvem a lei?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29154, 'nvi', 'Gálatas', 'gl', 4, 22, 'Pois está escrito que Abraão teve dois filhos, um da escrava e outro da livre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29155, 'nvi', 'Gálatas', 'gl', 4, 23, 'O filho da escrava nasceu de modo natural, mas o filho da livre nasceu mediante promessa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29156, 'nvi', 'Gálatas', 'gl', 4, 24, 'Isso é usado aqui como uma ilustração; estas mulheres representam duas alianças. Uma aliança procede do monte Sinai e gera filhos para a escravidão: esta é Hagar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29157, 'nvi', 'Gálatas', 'gl', 4, 25, 'Hagar representa o monte Sinai, na Arábia, e corresponde à atual cidade de Jerusalém, que está escravizada com os seus filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29158, 'nvi', 'Gálatas', 'gl', 4, 26, 'Mas a Jerusalém do alto é livre, e essa é a nossa mãe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29159, 'nvi', 'Gálatas', 'gl', 4, 27, 'Pois está escrito: "Regozije-se, ó estéril, você que nunca teve um filho; grite de alegria, você que nunca esteve em trabalho de parto; porque mais são os filhos da mulher abandonada do que os daquela que tem marido".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29160, 'nvi', 'Gálatas', 'gl', 4, 28, 'Vocês, irmãos, são filhos da promessa, como Isaque.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29161, 'nvi', 'Gálatas', 'gl', 4, 29, 'Naquele tempo, o filho nascido de modo natural perseguia o filho nascido segundo o Espírito. O mesmo acontece agora.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29162, 'nvi', 'Gálatas', 'gl', 4, 30, 'Mas o que diz a Escritura? "Mande embora a escrava e o seu filho, porque o filho da escrava jamais será herdeiro com o filho da livre".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29163, 'nvi', 'Gálatas', 'gl', 4, 31, 'Portanto, irmãos, não somos filhos da escrava, mas da livre.');
