INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29164, 'nvi', 'Gálatas', 'gl', 5, 1, 'Foi para a liberdade que Cristo nos libertou. Portanto, permaneçam firmes e não se deixem submeter novamente a um jugo de escravidão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29165, 'nvi', 'Gálatas', 'gl', 5, 2, 'Ouçam bem o que eu, Paulo, lhes digo: Caso se deixem circuncidar, Cristo de nada lhes servirá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29166, 'nvi', 'Gálatas', 'gl', 5, 3, 'De novo declaro a todo homem que se deixa circuncidar que está obrigado a cumprir toda a lei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29167, 'nvi', 'Gálatas', 'gl', 5, 4, 'Vocês, que procuram ser justificados pela lei, separaram-se de Cristo; caíram da graça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29168, 'nvi', 'Gálatas', 'gl', 5, 5, 'Pois é mediante o Espírito que nós aguardamos pela fé a justiça que é a nossa esperança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29169, 'nvi', 'Gálatas', 'gl', 5, 6, 'Porque em Cristo Jesus nem circuncisão nem incircuncisão têm efeito algum, mas sim a fé que atua pelo amor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29170, 'nvi', 'Gálatas', 'gl', 5, 7, 'Vocês corriam bem. Quem os impediu de continuar obedecendo à verdade?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29171, 'nvi', 'Gálatas', 'gl', 5, 8, 'Tal persuasão não provém daquele que os chama.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29172, 'nvi', 'Gálatas', 'gl', 5, 9, '"Um pouco de fermento leveda toda a massa".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29173, 'nvi', 'Gálatas', 'gl', 5, 10, 'Estou convencido no Senhor de que vocês não pensarão de nenhum outro modo. Aquele que os perturba, seja quem for, sofrerá a condenação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29174, 'nvi', 'Gálatas', 'gl', 5, 11, 'Irmãos, se ainda estou pregando a circuncisão, por que continuo sendo perseguido? Nesse caso, o escândalo da cruz foi removido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29175, 'nvi', 'Gálatas', 'gl', 5, 12, 'Quanto a esses que os perturbam, quem dera que se castrassem!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29176, 'nvi', 'Gálatas', 'gl', 5, 13, 'Irmãos, vocês foram chamados para a liberdade. Mas não usem a liberdade para dar ocasião à vontade da carne; pelo contrário, sirvam uns aos outros mediante o amor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29177, 'nvi', 'Gálatas', 'gl', 5, 14, 'Toda a lei se resume num só mandamento: "Ame o seu próximo como a si mesmo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29178, 'nvi', 'Gálatas', 'gl', 5, 15, 'Mas se vocês se mordem e se devoram uns aos outros, cuidado para não se destruírem mutuamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29179, 'nvi', 'Gálatas', 'gl', 5, 16, 'Por isso digo: vivam pelo Espírito, e de modo nenhum satisfarão os desejos da carne.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29180, 'nvi', 'Gálatas', 'gl', 5, 17, 'Pois a carne deseja o que é contrário ao Espírito; e o Espírito, o que é contrário à carne. Eles estão em conflito um com o outro, de modo que vocês não fazem o que desejam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29181, 'nvi', 'Gálatas', 'gl', 5, 18, 'Mas, se vocês são guiados pelo Espírito, não estão debaixo da lei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29182, 'nvi', 'Gálatas', 'gl', 5, 19, 'Ora, as obras da carne são manifestas: imoralidade sexual, impureza e libertinagem;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29183, 'nvi', 'Gálatas', 'gl', 5, 20, 'idolatria e feitiçaria; ódio, discórdia, ciúmes, ira, egoísmo, dissensões, facções');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29184, 'nvi', 'Gálatas', 'gl', 5, 21, 'e inveja; embriaguez, orgias e coisas semelhantes. Eu os advirto, como antes já os adverti, que os que praticam essas coisas não herdarão o Reino de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29185, 'nvi', 'Gálatas', 'gl', 5, 22, 'Mas o fruto do Espírito é amor, alegria, paz, paciência, amabilidade, bondade, fidelidade,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29186, 'nvi', 'Gálatas', 'gl', 5, 23, 'mansidão e domínio próprio. Contra essas coisas não há lei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29187, 'nvi', 'Gálatas', 'gl', 5, 24, 'Os que pertencem a Cristo Jesus crucificaram a carne, com as suas paixões e os seus desejos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29188, 'nvi', 'Gálatas', 'gl', 5, 25, 'Se vivemos pelo Espírito, andemos também pelo Espírito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29189, 'nvi', 'Gálatas', 'gl', 5, 26, 'Não sejamos presunçosos, provocando uns aos outros e tendo inveja uns dos outros.');
