INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (0, 'nvi', 'Gênesis', 'gn', 1, 1, 'No princípio Deus criou os céus e a terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1, 'nvi', 'Gênesis', 'gn', 1, 2, 'Era a terra sem forma e vazia; trevas cobriam a face do abismo, e o Espírito de Deus se movia sobre a face das águas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2, 'nvi', 'Gênesis', 'gn', 1, 3, 'Disse Deus: "Haja luz", e houve luz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3, 'nvi', 'Gênesis', 'gn', 1, 4, 'Deus viu que a luz era boa, e separou a luz das trevas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4, 'nvi', 'Gênesis', 'gn', 1, 5, 'Deus chamou à luz dia, e às trevas chamou noite. Passaram-se a tarde e a manhã; esse foi o primeiro dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5, 'nvi', 'Gênesis', 'gn', 1, 6, 'Depois disse Deus: "Haja entre as águas um firmamento que separe águas de águas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6, 'nvi', 'Gênesis', 'gn', 1, 7, 'Então Deus fez o firmamento e separou as águas que estavam embaixo do firmamento das que estavam por cima. E assim foi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7, 'nvi', 'Gênesis', 'gn', 1, 8, 'Ao firmamento Deus chamou céu. Passaram-se a tarde e a manhã; esse foi o segundo dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8, 'nvi', 'Gênesis', 'gn', 1, 9, 'E disse Deus: "Ajuntem-se num só lugar as águas que estão debaixo do céu, e apareça a parte seca". E assim foi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9, 'nvi', 'Gênesis', 'gn', 1, 10, 'À parte seca Deus chamou terra, e chamou mares ao conjunto das águas. E Deus viu que ficou bom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10, 'nvi', 'Gênesis', 'gn', 1, 11, 'Então disse Deus: "Cubra-se a terra de vegetação: plantas que dêem sementes e árvores cujos frutos produzam sementes de acordo com as suas espécies". E assim foi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11, 'nvi', 'Gênesis', 'gn', 1, 12, 'A terra fez brotar a vegetação: plantas que dão sementes de acordo com as suas espécies, e árvores cujos frutos produzem sementes de acordo com as suas espécies. E Deus viu que ficou bom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12, 'nvi', 'Gênesis', 'gn', 1, 13, 'Passaram-se a tarde e a manhã; esse foi o terceiro dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13, 'nvi', 'Gênesis', 'gn', 1, 14, 'Disse Deus: "Haja luminares no firmamento do céu para separar o dia da noite. Sirvam eles de sinais para marcar estações, dias e anos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14, 'nvi', 'Gênesis', 'gn', 1, 15, 'e sirvam de luminares no firmamento do céu para iluminar a terra". E assim foi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15, 'nvi', 'Gênesis', 'gn', 1, 16, 'Deus fez os dois grandes luminares: o maior para governar o dia e o menor para governar a noite; fez também as estrelas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16, 'nvi', 'Gênesis', 'gn', 1, 17, 'Deus os colocou no firmamento do céu para iluminar a terra,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17, 'nvi', 'Gênesis', 'gn', 1, 18, 'governar o dia e a noite, e separar a luz das trevas. E Deus viu que ficou bom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18, 'nvi', 'Gênesis', 'gn', 1, 19, 'Passaram-se a tarde e a manhã; esse foi o quarto dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19, 'nvi', 'Gênesis', 'gn', 1, 20, 'Disse também Deus: "Encham-se as águas de seres vivos, e sobre a terra voem aves sob o firmamento do céu".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20, 'nvi', 'Gênesis', 'gn', 1, 21, 'Assim Deus criou os grandes animais aquáticos e os demais seres vivos que povoam as águas, de acordo com as suas espécies; e todas as aves, de acordo com as suas espécies. E Deus viu que ficou bom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21, 'nvi', 'Gênesis', 'gn', 1, 22, 'Então Deus os abençoou, dizendo: "Sejam férteis e multipliquem-se! Encham as águas dos mares! E multipliquem-se as aves na terra".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22, 'nvi', 'Gênesis', 'gn', 1, 23, 'Passaram-se a tarde e a manhã; esse foi o quinto dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23, 'nvi', 'Gênesis', 'gn', 1, 24, 'E disse Deus: "Produza a terra seres vivos de acordo com as suas espécies: rebanhos domésticos, animais selvagens e os demais seres vivos da terra, cada um de acordo com a sua espécie". E assim foi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24, 'nvi', 'Gênesis', 'gn', 1, 25, 'Deus fez os animais selvagens de acordo com as suas espécies, os rebanhos domésticos de acordo com as suas espécies, e os demais seres vivos da terra de acordo com as suas espécies. E Deus viu que ficou bom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25, 'nvi', 'Gênesis', 'gn', 1, 26, 'Então disse Deus: "Façamos o homem à nossa imagem, conforme a nossa semelhança. Domine ele sobre os peixes do mar, sobre as aves do céu, sobre os animais grandes de toda a terra e sobre todos os pequenos animais que se movem rente ao chão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26, 'nvi', 'Gênesis', 'gn', 1, 27, 'Criou Deus o homem à sua imagem, à imagem de Deus o criou; homem e mulher os criou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27, 'nvi', 'Gênesis', 'gn', 1, 28, 'Deus os abençoou, e lhes disse: "Sejam férteis e multipliquem-se! Encham e subjuguem a terra! Dominem sobre os peixes do mar, sobre as aves do céu e sobre todos os animais que se movem pela terra".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28, 'nvi', 'Gênesis', 'gn', 1, 29, 'Disse Deus: "Eis que lhes dou todas as plantas que nascem em toda a terra e produzem sementes, e todas as árvores que dão frutos com sementes. Elas servirão de alimento para vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29, 'nvi', 'Gênesis', 'gn', 1, 30, 'E dou todos os vegetais como alimento a tudo o que tem em si fôlego de vida: a todos os grandes animais da terra, a todas as aves do céu e a todas as criaturas que se movem rente ao chão". E assim foi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30, 'nvi', 'Gênesis', 'gn', 1, 31, 'E Deus viu tudo o que havia feito, e tudo havia ficado muito bom. Passaram-se a tarde e a manhã; esse foi o sexto dia.');
