INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (267, 'nvi', 'Gênesis', 'gn', 11, 1, 'No mundo todo havia apenas uma língua, um só modo de falar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (268, 'nvi', 'Gênesis', 'gn', 11, 2, 'Saindo os homens do Oriente, encontraram uma planície em Sinear e ali se fixaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (269, 'nvi', 'Gênesis', 'gn', 11, 3, 'Disseram uns aos outros: "Vamos fazer tijolos e queimá-los bem". Usavam tijolos em lugar de pedras, e piche em vez de argamassa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (270, 'nvi', 'Gênesis', 'gn', 11, 4, 'Depois disseram: "Vamos construir uma cidade, com uma torre que alcance os céus. Assim nosso nome será famoso e não seremos espalhados pela face da terra".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (271, 'nvi', 'Gênesis', 'gn', 11, 5, 'O Senhor desceu para ver a cidade e a torre que os homens estavam construindo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (272, 'nvi', 'Gênesis', 'gn', 11, 6, 'E disse o Senhor: "Eles são um só povo e falam uma só língua, e começaram a construir isso. Em breve nada poderá impedir o que planejam fazer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (273, 'nvi', 'Gênesis', 'gn', 11, 7, 'Venham, desçamos e confundamos a língua que falam, para que não entendam mais uns aos outros".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (274, 'nvi', 'Gênesis', 'gn', 11, 8, 'Assim o Senhor os dispersou dali por toda a terra, e pararam de construir a cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (275, 'nvi', 'Gênesis', 'gn', 11, 9, 'Por isso foi chamada Babel, porque ali o Senhor confundiu a língua de todo o mundo. Dali o Senhor os espalhou por toda a terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (276, 'nvi', 'Gênesis', 'gn', 11, 10, 'Este é o registro da descendência de Sem: Dois anos depois do Dilúvio, aos 100 anos de idade, Sem gerou Arfaxade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (277, 'nvi', 'Gênesis', 'gn', 11, 11, 'E depois de ter gerado Arfaxade, Sem viveu 500 anos e gerou outros filhos e filhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (278, 'nvi', 'Gênesis', 'gn', 11, 12, 'Aos 35 anos, Arfaxade gerou Salá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (279, 'nvi', 'Gênesis', 'gn', 11, 13, 'Depois que gerou Salá, Arfaxade viveu 403 anos e gerou outros filhos e filhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (280, 'nvi', 'Gênesis', 'gn', 11, 14, 'Aos 30 anos, Salá gerou Héber.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (281, 'nvi', 'Gênesis', 'gn', 11, 15, 'Depois que gerou Héber, Salá viveu 403 anos e gerou outros filhos e filhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (282, 'nvi', 'Gênesis', 'gn', 11, 16, 'Aos 34 anos, Héber gerou Pelegue.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (283, 'nvi', 'Gênesis', 'gn', 11, 17, 'Depois que gerou Pelegue, Héber viveu 430 anos e gerou outros filhos e filhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (284, 'nvi', 'Gênesis', 'gn', 11, 18, 'Aos 30 anos, Pelegue gerou Reú.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (285, 'nvi', 'Gênesis', 'gn', 11, 19, 'Depois que gerou Reú, Pelegue viveu 209 anos e gerou outros filhos e filhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (286, 'nvi', 'Gênesis', 'gn', 11, 20, 'Aos 32 anos, Reú gerou Serugue.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (287, 'nvi', 'Gênesis', 'gn', 11, 21, 'Depois que gerou Serugue, Reú viveu 207 anos e gerou outros filhos e filhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (288, 'nvi', 'Gênesis', 'gn', 11, 22, 'Aos 30 anos, Serugue gerou Naor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (289, 'nvi', 'Gênesis', 'gn', 11, 23, 'Depois que gerou Naor, Serugue viveu 200 anos e gerou outros filhos e filhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (290, 'nvi', 'Gênesis', 'gn', 11, 24, 'Aos 29 anos, Naor gerou Terá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (291, 'nvi', 'Gênesis', 'gn', 11, 25, 'Depois que gerou Terá, Naor viveu 119 anos e gerou outros filhos e filhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (292, 'nvi', 'Gênesis', 'gn', 11, 26, 'Aos 70 anos, Terá havia gerado Abrão, Naor e Harã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (293, 'nvi', 'Gênesis', 'gn', 11, 27, 'Esta é a história da família de Terá: Terá gerou Abrão, Naor e Harã. E Harã gerou Ló.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (294, 'nvi', 'Gênesis', 'gn', 11, 28, 'Harã morreu em Ur dos caldeus, sua terra natal, quando ainda vivia Terá, seu pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (295, 'nvi', 'Gênesis', 'gn', 11, 29, 'Tanto Abrão como Naor casaram-se. O nome da mulher de Abrão era Sarai, e o nome da mulher de Naor era Milca; esta era filha de Harã, pai de Milca e de Iscá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (296, 'nvi', 'Gênesis', 'gn', 11, 30, 'Ora, Sarai era estéril; não tinha filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (297, 'nvi', 'Gênesis', 'gn', 11, 31, 'Terá tomou seu filho Abrão, seu neto Ló, filho de Harã, e sua nora Sarai, mulher de seu filho Abrão, e juntos partiram de Ur dos caldeus para Canaã. Mas, ao chegarem a Harã, estabeleceram-se ali.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (298, 'nvi', 'Gênesis', 'gn', 11, 32, 'Terá viveu 205 anos e morreu em Harã.');
