INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (299, 'nvi', 'Gênesis', 'gn', 12, 1, 'Então o Senhor disse a Abrão: "Saia da sua terra, do meio dos seus parentes e da casa de seu pai, e vá para a terra que eu lhe mostrarei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (300, 'nvi', 'Gênesis', 'gn', 12, 2, '"Farei de você um grande povo, e o abençoarei. Tornarei famoso o seu nome, e você será uma bênção.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (301, 'nvi', 'Gênesis', 'gn', 12, 3, 'Abençoarei os que o abençoarem, e amaldiçoarei os que o amaldiçoarem; e por meio de você todos os povos da terra serão abençoados".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (302, 'nvi', 'Gênesis', 'gn', 12, 4, 'Partiu Abrão, como lhe ordenara o Senhor, e Ló foi com ele. Abrão tinha setenta e cinco anos quando saiu de Harã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (303, 'nvi', 'Gênesis', 'gn', 12, 5, 'Levou sua mulher Sarai, seu sobrinho Ló, todos os bens que haviam acumulado e os seus servos, comprados em Harã; partiram para a terra de Canaã e lá chegaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (304, 'nvi', 'Gênesis', 'gn', 12, 6, 'Abrão atravessou a terra até o lugar do Carvalho de Moré, em Siquém. Naquela época os cananeus habitavam essa terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (305, 'nvi', 'Gênesis', 'gn', 12, 7, 'O Senhor apareceu a Abrão e disse: "À sua descendência darei esta terra". Abrão construiu ali um altar dedicado ao Senhor, que lhe havia aparecido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (306, 'nvi', 'Gênesis', 'gn', 12, 8, 'Dali prosseguiu em direção às colinas a leste de Betel, onde armou acampamento, tendo Betel a oeste e Ai a leste. Construiu ali um altar dedicado ao Senhor e invocou o nome do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (307, 'nvi', 'Gênesis', 'gn', 12, 9, 'Depois Abrão partiu e prosseguiu em direção ao Neguebe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (308, 'nvi', 'Gênesis', 'gn', 12, 10, 'Houve fome naquela terra, e Abrão desceu ao Egito para ali viver algum tempo, pois a fome era rigorosa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (309, 'nvi', 'Gênesis', 'gn', 12, 11, 'Quando estava chegando ao Egito, disse a Sarai, sua mulher: "Bem sei que você é bonita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (310, 'nvi', 'Gênesis', 'gn', 12, 12, 'Quando os egípcios a virem, dirão: ‘Esta é a mulher dele’. E me matarão, mas deixarão você viva.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (311, 'nvi', 'Gênesis', 'gn', 12, 13, 'Diga que é minha irmã, para que me tratem bem por amor a você e minha vida seja poupada por sua causa".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (312, 'nvi', 'Gênesis', 'gn', 12, 14, 'Quando Abrão chegou ao Egito, viram os egípcios que Sarai era uma mulher muito bonita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (313, 'nvi', 'Gênesis', 'gn', 12, 15, 'Vendo-a, os homens da corte do faraó a elogiaram diante do faraó, e ela foi levada ao seu palácio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (314, 'nvi', 'Gênesis', 'gn', 12, 16, 'Ele tratou bem a Abrão por causa dela, e Abrão recebeu ovelhas e bois, jumentos e jumentas, servos e servas, e camelos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (315, 'nvi', 'Gênesis', 'gn', 12, 17, 'Mas o Senhor puniu o faraó e sua corte com graves doenças, por causa de Sarai, mulher de Abrão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (316, 'nvi', 'Gênesis', 'gn', 12, 18, 'Por isso o faraó mandou chamar Abrão e disse: "O que você fez comigo? Por que não me falou que ela era sua mulher?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (317, 'nvi', 'Gênesis', 'gn', 12, 19, 'Por que disse que ela era sua irmã? Foi por isso que eu a tomei para ser minha mulher. Aí está a sua mulher. Tome-a e vá! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (318, 'nvi', 'Gênesis', 'gn', 12, 20, 'A seguir o faraó deu ordens para que providenciassem o necessário para que Abrão partisse, com sua mulher e com tudo o que possuía.');
