INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (337, 'nvi', 'Gênesis', 'gn', 14, 1, 'Naquela época Anrafel, rei de Sinear, Arioque, rei de Elasar, Quedorlaomer, rei de Elão, e Tidal, rei de Goim,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (338, 'nvi', 'Gênesis', 'gn', 14, 2, 'foram à guerra contra Bera, rei de Sodoma, contra Birsa, rei de Gomorra, contra Sinabe, rei de Admá, contra Semeber, rei de Zeboim, e contra o rei de Belá, que é Zoar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (339, 'nvi', 'Gênesis', 'gn', 14, 3, 'Todos esses últimos juntaram suas tropas no vale de Sidim, onde fica o mar Salgado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (340, 'nvi', 'Gênesis', 'gn', 14, 4, 'Doze anos estiveram sujeitos a Quedorlaomer, mas no décimo terceiro ano se rebelaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (341, 'nvi', 'Gênesis', 'gn', 14, 5, 'No décimo quarto ano, Quedorlaomer e os reis que a ele se aliaram derrotaram os refains em Asterote-Carnaim, os zuzins em Hã, os emins em Savé-Quiriataim');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (342, 'nvi', 'Gênesis', 'gn', 14, 6, 'e os horeus desde os montes de Seir até El-Parã, próximo ao deserto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (343, 'nvi', 'Gênesis', 'gn', 14, 7, 'Depois, voltaram e foram para En-Mispate, que é Cades, e conquistaram todo o território dos amalequitas e dos amorreus que viviam em Hazazom-Tamar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (344, 'nvi', 'Gênesis', 'gn', 14, 8, 'Então os reis de Sodoma, de Gomorra, de Admá, de Zeboim e de Belá, que é Zoar, marcharam e tomaram posição de combate no vale de Sidim');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (345, 'nvi', 'Gênesis', 'gn', 14, 9, 'contra Quedorlaomer, rei de Elão, contra Tidal, rei de Goim, contra Anrafel, rei de Sinear, e contra Arioque, rei de Elasar. Eram quatro reis contra cinco.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (346, 'nvi', 'Gênesis', 'gn', 14, 10, 'Ora, o vale de Sidim era cheio de poços de betume e, quando os reis de Sodoma e de Gomorra fugiram, alguns dos seus homens caíram nos poços e o restante escapou para os montes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (347, 'nvi', 'Gênesis', 'gn', 14, 11, 'Os vencedores saquearam todos os bens de Sodoma e de Gomorra e todo o seu mantimento, e partiram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (348, 'nvi', 'Gênesis', 'gn', 14, 12, 'Levaram também Ló, sobrinho de Abrão, e os bens que ele possuía, visto que morava em Sodoma.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (349, 'nvi', 'Gênesis', 'gn', 14, 13, 'Mas alguém que tinha escapado veio e relatou tudo a Abrão, o hebreu. Abrão vivia próximo aos carvalhos de Manre, o amorreu, irmão de Escol e de Aner, aliados de Abrão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (350, 'nvi', 'Gênesis', 'gn', 14, 14, 'Quando Abrão ouviu que seu parente fora levado prisioneiro, mandou convocar os trezentos e dezoito homens treinados, nascidos em sua casa, e saiu em perseguição aos inimigos até Dã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (351, 'nvi', 'Gênesis', 'gn', 14, 15, 'Atacou-os durante a noite em grupos, e assim os derrotou, perseguindo-os até Hobá, ao norte de Damasco.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (352, 'nvi', 'Gênesis', 'gn', 14, 16, 'Recuperou todos os bens e trouxe de volta seu parente Ló com tudo o que possuía, juntamente com as mulheres e o restante dos prisioneiros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (353, 'nvi', 'Gênesis', 'gn', 14, 17, 'Voltando Abrão da vitória sobre Quedorlaomer e sobre os reis que a ele se haviam aliado, o rei de Sodoma foi ao seu encontro no vale de Savé, isto é, o vale do Rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (354, 'nvi', 'Gênesis', 'gn', 14, 18, 'Então Melquisedeque, rei de Salém e sacerdote do Deus Altíssimo, trouxe pão e vinho');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (355, 'nvi', 'Gênesis', 'gn', 14, 19, 'e abençoou Abrão, dizendo: "Bendito seja Abrão pelo Deus Altíssimo, Criador dos céus e da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (356, 'nvi', 'Gênesis', 'gn', 14, 20, 'E bendito seja o Deus Altíssimo, que entregou seus inimigos em suas mãos". E Abrão lhe deu o dízimo de tudo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (357, 'nvi', 'Gênesis', 'gn', 14, 21, 'O rei de Sodoma disse a Abrão: "Dê-me as pessoas e pode ficar com os bens".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (358, 'nvi', 'Gênesis', 'gn', 14, 22, 'Mas Abrão respondeu ao rei de Sodoma: "De mãos levantadas ao Senhor, Deus Altíssimo, Criador dos céus e da terra, juro');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (359, 'nvi', 'Gênesis', 'gn', 14, 23, 'que não aceitarei nada do que lhe pertence, nem mesmo um cordão ou uma correia de sandália, para que você jamais venha a dizer: ‘Eu enriqueci Abrão’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (360, 'nvi', 'Gênesis', 'gn', 14, 24, 'Nada aceitarei, a não ser o que os meus servos comeram e a porção pertencente a Aner, Escol e Manre, os quais me acompanharam. Que eles recebam a sua porção".');
