INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (361, 'nvi', 'Gênesis', 'gn', 15, 1, 'Depois dessas coisas o Senhor falou a Abrão numa visão: "Não tenha medo, Abrão! Eu sou o seu escudo; grande será a sua recompensa! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (362, 'nvi', 'Gênesis', 'gn', 15, 2, 'Mas Abrão perguntou: "Ó Soberano Senhor, que me darás, se continuo sem filhos e o herdeiro do que possuo é Eliézer de Damasco? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (363, 'nvi', 'Gênesis', 'gn', 15, 3, 'E acrescentou: "Tu não me deste filho algum! Um servo da minha casa será o meu herdeiro! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (364, 'nvi', 'Gênesis', 'gn', 15, 4, 'Então o Senhor deu-lhe a seguinte resposta: "Seu herdeiro não será esse. Um filho gerado por você mesmo será o seu herdeiro".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (365, 'nvi', 'Gênesis', 'gn', 15, 5, 'Levando-o para fora da tenda, disse-lhe: "Olhe para o céu e conte as estrelas, se é que pode contá-las". E prosseguiu: "Assim será a sua descendência".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (366, 'nvi', 'Gênesis', 'gn', 15, 6, 'Abrão creu no Senhor, e isso lhe foi creditado como justiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (367, 'nvi', 'Gênesis', 'gn', 15, 7, 'Disse-lhe ainda: "Eu sou o Senhor, que o tirei de Ur dos caldeus para dar-lhe esta terra como herança".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (368, 'nvi', 'Gênesis', 'gn', 15, 8, 'Perguntou-lhe Abrão: "Ó Soberano Senhor, como posso saber que tomarei posse dela? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (369, 'nvi', 'Gênesis', 'gn', 15, 9, 'Respondeu-lhe o Senhor: "Traga-me uma novilha, uma cabra e um carneiro, todos com três anos de vida, e também uma rolinha e um pombinho".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (370, 'nvi', 'Gênesis', 'gn', 15, 10, 'Abrão trouxe todos esses animais, cortou-os ao meio e colocou cada metade em frente à outra; as aves, porém, ele não cortou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (371, 'nvi', 'Gênesis', 'gn', 15, 11, 'Nisso, aves de rapina começaram a descer sobre os cadáveres, mas Abrão as enxotava.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (372, 'nvi', 'Gênesis', 'gn', 15, 12, 'Ao pôr-do-sol, Abrão foi tomado de sono profundo, e eis que vieram sobre ele trevas densas e apavorantes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (373, 'nvi', 'Gênesis', 'gn', 15, 13, 'Então o Senhor lhe disse: "Saiba que os seus descendentes serão estrangeiros numa terra que não lhes pertencerá, onde também serão escravizados e oprimidos por quatrocentos anos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (374, 'nvi', 'Gênesis', 'gn', 15, 14, 'Mas eu castigarei a nação a quem servirão como escravos e, depois de tudo, sairão com muitos bens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (375, 'nvi', 'Gênesis', 'gn', 15, 15, 'Você, porém, irá em paz a seus antepassados e será sepultado em boa velhice.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (376, 'nvi', 'Gênesis', 'gn', 15, 16, 'Na quarta geração, os seus descendentes voltarão para cá, porque a maldade dos amorreus ainda não atingiu a medida completa".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (377, 'nvi', 'Gênesis', 'gn', 15, 17, 'Depois que o sol se pôs e veio a escuridão, eis que um fogareiro esfumaçante, com uma tocha acesa, passou por entre os pedaços dos animais.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (378, 'nvi', 'Gênesis', 'gn', 15, 18, 'Naquele dia o Senhor fez a seguinte aliança com Abrão: "Aos seus descendentes dei esta terra, desde o ribeiro do Egito até o grande rio, o Eufrates:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (379, 'nvi', 'Gênesis', 'gn', 15, 19, 'a terra dos queneus, dos quenezeus, dos cadmoneus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (380, 'nvi', 'Gênesis', 'gn', 15, 20, 'dos hititas, dos ferezeus, dos refains,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (381, 'nvi', 'Gênesis', 'gn', 15, 21, 'dos amorreus, dos cananeus, dos girgaseus e dos jebuseus".');
