INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (398, 'nvi', 'Gênesis', 'gn', 17, 1, 'Quando Abrão estava com noventa e nove anos de idade o Senhor lhe apareceu e disse: "Eu sou o Deus Todo-poderoso; ande segundo a minha vontade e seja íntegro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (399, 'nvi', 'Gênesis', 'gn', 17, 2, 'Estabelecerei a minha aliança entre mim e você e multiplicarei muitíssimo a sua descendência".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (400, 'nvi', 'Gênesis', 'gn', 17, 3, 'Abrão prostrou-se, rosto em terra, e Deus lhe disse:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (401, 'nvi', 'Gênesis', 'gn', 17, 4, '"De minha parte, esta é a minha aliança com você. Você será o pai de muitas nações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (402, 'nvi', 'Gênesis', 'gn', 17, 5, 'Não será mais chamado Abrão; seu nome será Abraão, porque eu o constituí pai de muitas nações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (403, 'nvi', 'Gênesis', 'gn', 17, 6, 'Eu o tornarei extremamente prolífero; de você farei nações e de você procederão reis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (404, 'nvi', 'Gênesis', 'gn', 17, 7, 'Estabelecerei a minha aliança como aliança eterna entre mim e você e os seus futuros descendentes, para ser o seu Deus e o Deus dos seus descendentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (405, 'nvi', 'Gênesis', 'gn', 17, 8, 'Toda a terra de Canaã, onde agora você é estrangeiro, darei como propriedade perpétua a você e a seus descendentes; e serei o Deus deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (406, 'nvi', 'Gênesis', 'gn', 17, 9, '"De sua parte", disse Deus a Abraão, "guarde a minha aliança, tanto você como os seus futuros descendentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (407, 'nvi', 'Gênesis', 'gn', 17, 10, 'Esta é a minha aliança com você e com os seus descendentes, aliança que terá que ser guardada: Todos os do sexo masculino entre vocês serão circuncidados na carne.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (408, 'nvi', 'Gênesis', 'gn', 17, 11, 'Terão que fazer essa marca, que será o sinal da aliança entre mim e vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (409, 'nvi', 'Gênesis', 'gn', 17, 12, 'Da sua geração em diante, todo menino de oito dias de idade entre vocês terá que ser circuncidado, tanto os nascidos em sua casa quanto os que forem comprados de estrangeiros e que não forem descendentes de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (410, 'nvi', 'Gênesis', 'gn', 17, 13, 'Sejam nascidos em sua casa, sejam comprados, terão que ser circuncidados. Minha aliança, marcada no corpo de vocês, será uma aliança perpétua.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (411, 'nvi', 'Gênesis', 'gn', 17, 14, 'Qualquer do sexo masculino que for incircunciso, que não tiver sido circuncidado, será eliminado do meio do seu povo; quebrou a minha aliança".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (412, 'nvi', 'Gênesis', 'gn', 17, 15, 'Disse também Deus a Abraão: "De agora em diante sua mulher já não se chamará Sarai; seu nome será Sara.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (413, 'nvi', 'Gênesis', 'gn', 17, 16, 'Eu a abençoarei e também por meio dela darei a você um filho. Sim, eu a abençoarei e dela procederão nações e reis de povos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (414, 'nvi', 'Gênesis', 'gn', 17, 17, 'Abraão prostrou-se, rosto em terra; riu-se e disse a si mesmo: "Poderá um homem de cem anos de idade gerar filhos? Poderá Sara dar à luz aos noventa anos? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (415, 'nvi', 'Gênesis', 'gn', 17, 18, 'E Abraão disse a Deus: "Permite que Ismael seja o meu herdeiro! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (416, 'nvi', 'Gênesis', 'gn', 17, 19, 'Então Deus respondeu: "Na verdade Sara, sua mulher, lhe dará um filho, e você lhe chamará Isaque. Com ele estabelecerei a minha aliança, que será aliança eterna para os seus futuros descendentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (417, 'nvi', 'Gênesis', 'gn', 17, 20, 'E no caso de Ismael, levarei em conta o seu pedido. Também o abençoarei; eu o farei prolífero e multiplicarei muito a sua descendência. Ele será pai de doze príncipes e dele farei um grande povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (418, 'nvi', 'Gênesis', 'gn', 17, 21, 'Mas a minha aliança, eu a estabelecerei com Isaque, filho que Sara lhe dará no ano que vem, por esta época".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (419, 'nvi', 'Gênesis', 'gn', 17, 22, 'Quando terminou de falar com Abraão, Deus subiu e retirou-se da presença dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (420, 'nvi', 'Gênesis', 'gn', 17, 23, 'Naquele mesmo dia Abraão tomou seu filho Ismael, todos os nascidos em sua casa e os que foram comprados, todos os do sexo masculino de sua casa, e os circuncidou, como Deus lhe ordenara.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (421, 'nvi', 'Gênesis', 'gn', 17, 24, 'Abraão tinha noventa e nove anos quando foi circuncidado,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (422, 'nvi', 'Gênesis', 'gn', 17, 25, 'e seu filho Ismael tinha treze;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (423, 'nvi', 'Gênesis', 'gn', 17, 26, 'Abraão e seu filho Ismael foram circuncidados naquele mesmo dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (424, 'nvi', 'Gênesis', 'gn', 17, 27, 'E com ele foram circuncidados todos os de sua casa, tanto os nascidos em casa como os comprados de estrangeiros.');
