INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (425, 'nvi', 'Gênesis', 'gn', 18, 1, 'O Senhor apareceu a Abraão perto dos carvalhos de Manre, quando ele estava sentado à entrada de sua tenda, na hora mais quente do dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (426, 'nvi', 'Gênesis', 'gn', 18, 2, 'Abraão ergueu os olhos e viu três homens em pé, a pouca distância. Quando os viu, saiu da entrada de sua tenda, correu ao encontro deles e curvou-se até ao chão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (427, 'nvi', 'Gênesis', 'gn', 18, 3, 'Disse ele: "Meu senhor, se mereço o seu favor, não passe pelo seu servo sem fazer uma parada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (428, 'nvi', 'Gênesis', 'gn', 18, 4, 'Mandarei buscar um pouco d’água para que lavem os pés e descansem debaixo desta árvore.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (429, 'nvi', 'Gênesis', 'gn', 18, 5, 'Vou trazer-lhes também o que comer, para que recobrem forças e prossigam pelo caminho, agora que já chegaram até este seu servo". "Está bem; faça como está dizendo", responderam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (430, 'nvi', 'Gênesis', 'gn', 18, 6, 'Abraão foi apressadamente à tenda e disse a Sara: "Depressa, pegue três medidas da melhor farinha, amasse-a e faça uns pães".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (431, 'nvi', 'Gênesis', 'gn', 18, 7, 'Depois correu ao rebanho e escolheu o melhor novilho, e o deu a um servo, que se apressou em prepará-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (432, 'nvi', 'Gênesis', 'gn', 18, 8, 'Trouxe então coalhada, leite e o novilho que havia sido preparado, e os serviu. Enquanto comiam, ele ficou perto deles em pé, debaixo da árvore.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (433, 'nvi', 'Gênesis', 'gn', 18, 9, '"Onde está Sara, sua mulher? ", perguntaram. "Ali na tenda", respondeu ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (434, 'nvi', 'Gênesis', 'gn', 18, 10, 'Então disse o Senhor: "Voltarei a você na primavera, e Sara, sua mulher, terá um filho". Sara escutava à entrada da tenda, atrás dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (435, 'nvi', 'Gênesis', 'gn', 18, 11, 'Abraão e Sara já eram velhos, de idade bem avançada, e Sara já tinha passado da idade de ter filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (436, 'nvi', 'Gênesis', 'gn', 18, 12, 'Por isso riu consigo mesma, quando pensou: "Depois de já estar velha e meu senhor já idoso, ainda terei esse prazer? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (437, 'nvi', 'Gênesis', 'gn', 18, 13, 'Mas o Senhor disse a Abraão: "Por que Sara riu e disse: ‘Poderei realmente dar à luz, agora que sou idosa? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (438, 'nvi', 'Gênesis', 'gn', 18, 14, 'Existe alguma coisa impossível para o Senhor? Na primavera voltarei a você, e Sara terá um filho".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (439, 'nvi', 'Gênesis', 'gn', 18, 15, 'Sara teve medo, e por isso mentiu: "Eu não ri". Mas ele disse: "Não negue, você riu".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (440, 'nvi', 'Gênesis', 'gn', 18, 16, 'Quando os homens se levantaram para partir, avistaram lá embaixo Sodoma; e Abraão os acompanhou para despedir-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (441, 'nvi', 'Gênesis', 'gn', 18, 17, 'Então o Senhor disse: "Esconderei de Abraão o que estou para fazer?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (442, 'nvi', 'Gênesis', 'gn', 18, 18, 'Abraão será o pai de uma nação grande e poderosa, e por meio dele todas as nações da terra serão abençoadas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (443, 'nvi', 'Gênesis', 'gn', 18, 19, 'Pois eu o escolhi, para que ordene aos seus filhos e aos seus descendentes que se conservem no caminho do Senhor, fazendo o que é justo e direito, para que o Senhor faça vir a Abraão o que lhe havia prometido".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (444, 'nvi', 'Gênesis', 'gn', 18, 20, 'Disse-lhe, pois, o Senhor: "As acusações contra Sodoma e Gomorra são tantas e o seu pecado é tão grave');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (445, 'nvi', 'Gênesis', 'gn', 18, 21, 'que descerei para ver se o que eles têm feito corresponde ao que tenho ouvido. Se não, eu saberei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (446, 'nvi', 'Gênesis', 'gn', 18, 22, 'Os homens partiram dali e foram para Sodoma, mas Abraão permaneceu diante do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (447, 'nvi', 'Gênesis', 'gn', 18, 23, 'Abraão aproximou-se dele e disse: "Exterminarás o justo com o ímpio?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (448, 'nvi', 'Gênesis', 'gn', 18, 24, 'E se houver cinqüenta justos na cidade? Ainda a destruirás e não pouparás o lugar por amor aos cinqüenta justos que nele estão?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (449, 'nvi', 'Gênesis', 'gn', 18, 25, 'Longe de ti fazer tal coisa: matar o justo com o ímpio, tratando o justo e o ímpio da mesma maneira. Longe de ti! Não agirá com justiça o Juiz de toda a terra? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (450, 'nvi', 'Gênesis', 'gn', 18, 26, 'Respondeu o Senhor: "Se eu encontrar cinqüenta justos em Sodoma, pouparei a cidade toda por amor a eles".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (451, 'nvi', 'Gênesis', 'gn', 18, 27, 'Mas Abraão tornou a falar: "Sei que já fui muito ousado a ponto de falar ao Senhor, eu que não passo de pó e cinza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (452, 'nvi', 'Gênesis', 'gn', 18, 28, 'Ainda assim pergunto: E se faltarem cinco para completar os cinqüenta justos? Destruirás a cidade por causa dos cinco? " Disse ele: "Se encontrar ali quarenta e cinco, não a destruirei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (453, 'nvi', 'Gênesis', 'gn', 18, 29, '"E se encontrares apenas quarenta? ", insistiu Abraão. Ele respondeu: "Por amor aos quarenta não a destruirei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (454, 'nvi', 'Gênesis', 'gn', 18, 30, 'Então continuou ele: "Não te ires, Senhor, mas permite-me falar. E se apenas trinta forem encontrados ali? " Ele respondeu: "Se encontrar trinta, não a destruirei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (455, 'nvi', 'Gênesis', 'gn', 18, 31, 'Prosseguiu Abraão: "Agora que já fui tão ousado falando ao Senhor, pergunto: E se apenas vinte forem encontrados ali? " Ele respondeu: "Por amor aos vinte não a destruirei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (456, 'nvi', 'Gênesis', 'gn', 18, 32, 'Então Abraão disse ainda: "Não te ires, Senhor, mas permite-me falar só mais uma vez. E se apenas dez forem encontrados? " Ele respondeu: "Por amor aos dez não a destruirei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (457, 'nvi', 'Gênesis', 'gn', 18, 33, 'Tendo acabado de falar com Abraão, o Senhor partiu, e Abraão voltou para casa.');
