INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31, 'nvi', 'Gênesis', 'gn', 2, 1, 'Assim foram concluídos os céus e a terra, e tudo o que neles há.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (32, 'nvi', 'Gênesis', 'gn', 2, 2, 'No sétimo dia Deus já havia concluído a obra que realizara, e nesse dia descansou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (33, 'nvi', 'Gênesis', 'gn', 2, 3, 'Abençoou Deus o sétimo dia e o santificou, porque nele descansou de toda a obra que realizara na criação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (34, 'nvi', 'Gênesis', 'gn', 2, 4, 'Esta é a história das origens dos céus e da terra, no tempo em que foram criados: Quando o Senhor Deus fez a terra e os céus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (35, 'nvi', 'Gênesis', 'gn', 2, 5, 'ainda não tinha brotado nenhum arbusto no campo, e nenhuma planta havia germinado, porque o Senhor Deus ainda não tinha feito chover sobre a terra, e também não havia homem para cultivar o solo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (36, 'nvi', 'Gênesis', 'gn', 2, 6, 'Todavia brotava água da terra e irrigava toda a superfície do solo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (37, 'nvi', 'Gênesis', 'gn', 2, 7, 'Então o Senhor Deus formou o homem do pó da terra e soprou em suas narinas o fôlego de vida, e o homem se tornou um ser vivente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (38, 'nvi', 'Gênesis', 'gn', 2, 8, 'Ora, o Senhor Deus tinha plantado um jardim no Éden, para os lados do leste; e ali colocou o homem que formara.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (39, 'nvi', 'Gênesis', 'gn', 2, 9, 'O Senhor Deus fez nascer então do solo todo tipo de árvores agradáveis aos olhos e boas para alimento. E no meio do jardim estavam a árvore da vida e a árvore do conhecimento do bem e do mal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (40, 'nvi', 'Gênesis', 'gn', 2, 10, 'No Éden nascia um rio que irrigava o jardim, e depois se dividia em quatro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (41, 'nvi', 'Gênesis', 'gn', 2, 11, 'O nome do primeiro é Pisom. Ele percorre toda a terra de Havilá, onde existe ouro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (42, 'nvi', 'Gênesis', 'gn', 2, 12, 'O ouro daquela terra é excelente; lá também existem o bdélio e a pedra de ônix.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (43, 'nvi', 'Gênesis', 'gn', 2, 13, 'O segundo, que percorre toda a terra de Cuxe, é o Giom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (44, 'nvi', 'Gênesis', 'gn', 2, 14, 'O terceiro, que corre pelo lado leste da Assíria, é o Tigre. E o quarto rio é o Eufrates.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (45, 'nvi', 'Gênesis', 'gn', 2, 15, 'O Senhor Deus colocou o homem no jardim do Éden para cuidar dele e cultivá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (46, 'nvi', 'Gênesis', 'gn', 2, 16, 'E o Senhor Deus ordenou ao homem: "Coma livremente de qualquer árvore do jardim,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (47, 'nvi', 'Gênesis', 'gn', 2, 17, 'mas não coma da árvore do conhecimento do bem e do mal, porque no dia em que dela comer, certamente você morrerá".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (48, 'nvi', 'Gênesis', 'gn', 2, 18, 'Então o Senhor Deus declarou: "Não é bom que o homem esteja só; farei para ele alguém que o auxilie e lhe corresponda".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (49, 'nvi', 'Gênesis', 'gn', 2, 19, 'Depois que formou da terra todos os animais do campo e todas as aves do céu, o Senhor Deus os trouxe ao homem para ver como este lhes chamaria; e o nome que o homem desse a cada ser vivo, esse seria o seu nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (50, 'nvi', 'Gênesis', 'gn', 2, 20, 'Assim o homem deu nomes a todos os rebanhos domésticos, às aves do céu e a todos os animais selvagens. Todavia não se encontrou para o homem alguém que o auxiliasse e lhe correspondesse.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (51, 'nvi', 'Gênesis', 'gn', 2, 21, 'Então o Senhor Deus fez o homem cair em profundo sono e, enquanto este dormia, tirou-lhe uma das costelas, fechando o lugar com carne.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (52, 'nvi', 'Gênesis', 'gn', 2, 22, 'Com a costela que havia tirado do homem, o Senhor Deus fez uma mulher e a trouxe a ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (53, 'nvi', 'Gênesis', 'gn', 2, 23, 'Disse então o homem: "Esta, sim, é osso dos meus ossos e carne da minha carne! Ela será chamada mulher, porque do homem foi tirada".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (54, 'nvi', 'Gênesis', 'gn', 2, 24, 'Por essa razão, o homem deixará pai e mãe e se unirá à sua mulher, e eles se tornarão uma só carne.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (55, 'nvi', 'Gênesis', 'gn', 2, 25, 'O homem e sua mulher viviam nus, e não sentiam vergonha.');
