INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (514, 'nvi', 'Gênesis', 'gn', 21, 1, 'O Senhor foi bondoso com Sara, como lhe dissera, e fez por ela o que prometera.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (515, 'nvi', 'Gênesis', 'gn', 21, 2, 'Sara engravidou e deu um filho a Abraão em sua velhice, na época fixada por Deus em sua promessa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (516, 'nvi', 'Gênesis', 'gn', 21, 3, 'Abraão deu o nome de Isaque ao filho que Sara lhe dera.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (517, 'nvi', 'Gênesis', 'gn', 21, 4, 'Quando seu filho Isaque tinha oito dias de vida, Abraão o circuncidou, conforme Deus lhe havia ordenado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (518, 'nvi', 'Gênesis', 'gn', 21, 5, 'Estava ele com cem anos de idade quando lhe nasceu Isaque, seu filho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (519, 'nvi', 'Gênesis', 'gn', 21, 6, 'E Sara disse: "Deus me encheu de riso, e todos os que souberem disso rirão comigo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (520, 'nvi', 'Gênesis', 'gn', 21, 7, 'E acrescentou: "Quem diria a Abraão que Sara amamentaria filhos? Contudo eu lhe dei um filho em sua velhice! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (521, 'nvi', 'Gênesis', 'gn', 21, 8, 'O menino cresceu e foi desmamado. No dia em que Isaque foi desmamado, Abraão deu uma grande festa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (522, 'nvi', 'Gênesis', 'gn', 21, 9, 'Sara, porém, viu que o filho que Hagar, a egípcia, dera a Abraão estava rindo de Isaque,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (523, 'nvi', 'Gênesis', 'gn', 21, 10, 'e disse a Abraão: "Livre-se daquela escrava e do seu filho, porque ele jamais será herdeiro com o meu filho Isaque".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (524, 'nvi', 'Gênesis', 'gn', 21, 11, 'Isso perturbou demais Abraão, pois envolvia um filho seu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (525, 'nvi', 'Gênesis', 'gn', 21, 12, 'Mas Deus lhe disse: "Não se perturbe por causa do menino e da escrava. Atenda a tudo o que Sara lhe pedir, porque será por meio de Isaque que a sua descendência há de ser considerada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (526, 'nvi', 'Gênesis', 'gn', 21, 13, 'Mas também do filho da escrava farei um povo; afinal ele é seu descendente".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (527, 'nvi', 'Gênesis', 'gn', 21, 14, 'Na manhã seguinte, Abraão pegou alguns pães e uma vasilha de couro cheia d’água, entregou-os a Hagar e, tendo-os colocado nos ombros dela, despediu-a com o menino. Ela se pôs a caminho e ficou vagando pelo deserto de Berseba.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (528, 'nvi', 'Gênesis', 'gn', 21, 15, 'Quando acabou a água da vasilha, ela deixou o menino debaixo de um arbusto');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (529, 'nvi', 'Gênesis', 'gn', 21, 16, 'e foi sentar-se perto dali, à distância de um tiro de flecha, porque pensou: "Não posso ver o menino morrer". Sentada ali perto, começou a chorar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (530, 'nvi', 'Gênesis', 'gn', 21, 17, 'Deus ouviu o choro do menino, e o anjo de Deus, do céu, chamou Hagar e lhe disse: "O que a aflige, Hagar? Não tenha medo; Deus ouviu o menino chorar, lá onde você o deixou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (531, 'nvi', 'Gênesis', 'gn', 21, 18, 'Levante o menino e tome-o pela mão, porque dele farei um grande povo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (532, 'nvi', 'Gênesis', 'gn', 21, 19, 'Então Deus lhe abriu os olhos, e ela viu uma fonte. Foi até lá, encheu de água a vasilha e deu de beber ao menino.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (533, 'nvi', 'Gênesis', 'gn', 21, 20, 'Deus estava com o menino. Ele cresceu, viveu no deserto e tornou-se flecheiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (534, 'nvi', 'Gênesis', 'gn', 21, 21, 'Vivia no deserto de Parã, e sua mãe conseguiu-lhe uma mulher da terra do Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (535, 'nvi', 'Gênesis', 'gn', 21, 22, 'Naquela ocasião, Abimeleque, acompanhado de Ficol, comandante do seu exército, disse a Abraão: "Deus está contigo em tudo o que fazes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (536, 'nvi', 'Gênesis', 'gn', 21, 23, 'Agora, jura-me, diante de Deus, que não vais enganar-me, nem a mim nem a meus filhos e descendentes. Trata a nação que te acolheu como estrangeiro com a mesma bondade com que te tratei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (537, 'nvi', 'Gênesis', 'gn', 21, 24, 'Respondeu Abraão: "Eu juro! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (538, 'nvi', 'Gênesis', 'gn', 21, 25, 'Todavia Abraão reclamou com Abimeleque a respeito de um poço que os servos de Abimeleque lhe tinham tomado à força.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (539, 'nvi', 'Gênesis', 'gn', 21, 26, 'Mas Abimeleque lhe respondeu: "Não sei quem fez isso. Nunca me disseste nada, e só fiquei sabendo disso hoje".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (540, 'nvi', 'Gênesis', 'gn', 21, 27, 'Então Abraão trouxe ovelhas e bois, deu-os a Abimeleque, e os dois firmaram um acordo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (541, 'nvi', 'Gênesis', 'gn', 21, 28, 'Abraão separou sete ovelhas do rebanho,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (542, 'nvi', 'Gênesis', 'gn', 21, 29, 'pelo que Abimeleque lhe perguntou: "Que significam estas sete ovelhas que separaste das demais? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (543, 'nvi', 'Gênesis', 'gn', 21, 30, 'Ele respondeu: "Aceita estas sete ovelhas de minhas mãos como testemunho de que eu cavei este poço".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (544, 'nvi', 'Gênesis', 'gn', 21, 31, 'Por isso aquele lugar foi chamado Berseba, porque ali os dois fizeram um juramento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (545, 'nvi', 'Gênesis', 'gn', 21, 32, 'Firmado esse acordo em Berseba, Abimeleque e Ficol, comandante das suas tropas, voltaram para a terra dos filisteus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (546, 'nvi', 'Gênesis', 'gn', 21, 33, 'Abraão, por sua vez, plantou uma tamargueira em Berseba e ali invocou o nome do Senhor, o Deus Eterno.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (547, 'nvi', 'Gênesis', 'gn', 21, 34, 'E morou Abraão na terra dos filisteus por longo tempo.');
