INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (548, 'nvi', 'Gênesis', 'gn', 22, 1, 'Passado algum tempo, Deus pôs Abraão à prova, dizendo-lhe: "Abraão! " Ele respondeu: "Eis-me aqui".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (549, 'nvi', 'Gênesis', 'gn', 22, 2, 'Então disse Deus: "Tome seu filho, seu único filho, Isaque, a quem você ama, e vá para a região de Moriá. Sacrifique-o ali como holocausto num dos montes que lhe indicarei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (550, 'nvi', 'Gênesis', 'gn', 22, 3, 'Na manhã seguinte, Abraão levantou-se e preparou o seu jumento. Levou consigo dois de seus servos e Isaque seu filho. Depois de cortar lenha para o holocausto, partiu em direção ao lugar que Deus lhe havia indicado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (551, 'nvi', 'Gênesis', 'gn', 22, 4, 'No terceiro dia de viagem, Abraão olhou e viu o lugar ao longe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (552, 'nvi', 'Gênesis', 'gn', 22, 5, 'Disse ele a seus servos: "Fiquem aqui com o jumento enquanto eu e o rapaz vamos até lá. Depois de adorarmos, voltaremos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (553, 'nvi', 'Gênesis', 'gn', 22, 6, 'Abraão pegou a lenha para o holocausto e a colocou nos ombros de seu filho Isaque, e ele mesmo levou as brasas para o fogo, e a faca. E caminhando os dois juntos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (554, 'nvi', 'Gênesis', 'gn', 22, 7, 'Isaque disse a seu pai Abraão: "Meu pai! " "Sim, meu filho", respondeu Abraão. Isaque perguntou: "As brasas e a lenha estão aqui, mas onde está o cordeiro para o holocausto? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (555, 'nvi', 'Gênesis', 'gn', 22, 8, 'Respondeu Abraão: "Deus mesmo há de prover o cordeiro para o holocausto, meu filho". E os dois continuaram a caminhar juntos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (556, 'nvi', 'Gênesis', 'gn', 22, 9, 'Quando chegaram ao lugar que Deus lhe havia indicado, Abraão construiu um altar e sobre ele arrumou a lenha. Amarrou seu filho Isaque e o colocou sobre o altar, em cima da lenha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (557, 'nvi', 'Gênesis', 'gn', 22, 10, 'Então estendeu a mão e pegou a faca para sacrificar seu filho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (558, 'nvi', 'Gênesis', 'gn', 22, 11, 'Mas o Anjo do Senhor o chamou do céu: "Abraão! Abraão! " "Eis-me aqui", respondeu ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (559, 'nvi', 'Gênesis', 'gn', 22, 12, '"Não toque no rapaz", disse o Anjo. "Não lhe faça nada. Agora sei que você teme a Deus, porque não me negou seu filho, o seu único filho. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (560, 'nvi', 'Gênesis', 'gn', 22, 13, 'Abraão ergueu os olhos e viu um carneiro preso pelos chifres num arbusto. Foi lá, pegou-o e sacrificou-o como holocausto em lugar de seu filho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (561, 'nvi', 'Gênesis', 'gn', 22, 14, 'Abraão deu àquele lugar o nome de "O Senhor proverá". Por isso até hoje se diz: "No monte do Senhor se proverá".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (562, 'nvi', 'Gênesis', 'gn', 22, 15, 'Pela segunda vez o Anjo do Senhor chamou do céu a Abraão');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (563, 'nvi', 'Gênesis', 'gn', 22, 16, 'e disse: "Juro por mim mesmo", declara o Senhor, "que por ter feito o que fez, não me negando seu filho, o seu único filho,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (564, 'nvi', 'Gênesis', 'gn', 22, 17, 'esteja certo de que o abençoarei e farei seus descendentes tão numerosos como as estrelas do céu e como a areia das praias do mar. Sua descendência conquistará as cidades dos que lhe forem inimigos');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (565, 'nvi', 'Gênesis', 'gn', 22, 18, 'e, por meio dela, todos povos da terra serão abençoados, porque você me obedeceu".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (566, 'nvi', 'Gênesis', 'gn', 22, 19, 'Então voltou Abraão a seus servos, e juntos partiram para Berseba, onde passou a viver.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (567, 'nvi', 'Gênesis', 'gn', 22, 20, 'Passado algum tempo, disseram a Abraão que Milca dera filhos a seu irmão Naor:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (568, 'nvi', 'Gênesis', 'gn', 22, 21, 'Uz, o mais velho, Buz, seu irmão, Quemuel, pai de Arã,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (569, 'nvi', 'Gênesis', 'gn', 22, 22, 'Quésede, Hazo, Pildas, Jidlafe e Betuel,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (570, 'nvi', 'Gênesis', 'gn', 22, 23, 'pai de Rebeca. Estes foram os oito filhos que Milca deu a Naor, irmão de Abraão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (571, 'nvi', 'Gênesis', 'gn', 22, 24, 'E sua concubina, chamada Reumá, teve os seguintes filhos: Tebá, Gaã, Taás e Maaca.');
