INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (572, 'nvi', 'Gênesis', 'gn', 23, 1, 'Sara viveu cento e vinte e sete anos');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (573, 'nvi', 'Gênesis', 'gn', 23, 2, 'e morreu em Quiriate-Arba, que é Hebrom, em Canaã; e Abraão foi lamentar e chorar por ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (574, 'nvi', 'Gênesis', 'gn', 23, 3, 'Depois Abraão deixou ali o corpo de sua mulher e foi falar com os hititas:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (575, 'nvi', 'Gênesis', 'gn', 23, 4, '"Sou apenas um estrangeiro entre vocês. Cedam-me alguma propriedade para sepultura, para que eu tenha onde enterrar a minha mulher".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (576, 'nvi', 'Gênesis', 'gn', 23, 5, 'Responderam os hititas a Abraão:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (577, 'nvi', 'Gênesis', 'gn', 23, 6, '"Ouça-nos, senhor; o senhor é um príncipe de Deus em nosso meio. Enterre a sua mulher numa de nossas sepulturas, na que lhe parecer melhor. Nenhum de nós recusará ceder-lhe sua sepultura para que enterre a sua mulher".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (578, 'nvi', 'Gênesis', 'gn', 23, 7, 'Abraão levantou-se, curvou-se perante o povo daquela terra, os hititas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (579, 'nvi', 'Gênesis', 'gn', 23, 8, 'e disse-lhes: "Já que vocês me dão permissão para sepultar minha mulher, peço que intercedam por mim junto a Efrom, filho de Zoar,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (580, 'nvi', 'Gênesis', 'gn', 23, 9, 'a fim de que ele me ceda a caverna de Macpela, que lhe pertence e se encontra na divisa do seu campo. Peçam-lhe que a ceda a mim pelo preço justo, para que eu tenha uma propriedade para sepultura entre vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (581, 'nvi', 'Gênesis', 'gn', 23, 10, 'Efrom, o hitita, estava sentado no meio do seu povo e respondeu a Abraão, sendo ouvido por todos os hititas que tinham vindo à porta da cidade:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (582, 'nvi', 'Gênesis', 'gn', 23, 11, '"Não, meu senhor. Ouça-me, eu lhe cedo o campo e também a caverna que nele está. Cedo-os na presença do meu povo. Sepulte a sua mulher".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (583, 'nvi', 'Gênesis', 'gn', 23, 12, 'Novamente Abraão curvou-se perante o povo daquela terra');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (584, 'nvi', 'Gênesis', 'gn', 23, 13, 'e disse a Efrom, sendo ouvido por todos: "Ouça-me, por favor. Pagarei o preço do campo. Aceite-o, para que eu possa sepultar a minha mulher".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (585, 'nvi', 'Gênesis', 'gn', 23, 14, 'Efrom respondeu a Abraão:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (586, 'nvi', 'Gênesis', 'gn', 23, 15, '"Ouça-me, meu senhor: aquele pedaço de terra vale quatrocentas peças de prata, mas o que significa isso entre mim e você? Sepulte a sua mulher".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (587, 'nvi', 'Gênesis', 'gn', 23, 16, 'Abraão concordou com Efrom e pesou-lhe o valor por ele estipulado diante dos hititas: quatrocentas peças de prata, de acordo com o peso corrente entre os mercadores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (588, 'nvi', 'Gênesis', 'gn', 23, 17, 'Assim o campo de Efrom em Macpela, perto de Manre, o próprio campo com a caverna que nele há e todas as árvores dentro das divisas do campo, foi transferido');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (589, 'nvi', 'Gênesis', 'gn', 23, 18, 'a Abraão como sua propriedade diante de todos os hititas que tinham vindo à porta da cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (590, 'nvi', 'Gênesis', 'gn', 23, 19, 'Depois disso, Abraão sepultou sua mulher Sara na caverna do campo de Macpela, perto de Manre, que se encontra em Hebrom, na terra de Canaã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (591, 'nvi', 'Gênesis', 'gn', 23, 20, 'Assim o campo e a caverna que nele há foram transferidos a Abraão pelos hititas como propriedade para sepultura.');
