INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (659, 'nvi', 'Gênesis', 'gn', 25, 1, 'Abraão casou-se com outra mulher, chamada Quetura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (660, 'nvi', 'Gênesis', 'gn', 25, 2, 'Ela lhe deu os seguintes filhos: Zinrã, Jocsã, Medã, Midiã, Isbaque e Suá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (661, 'nvi', 'Gênesis', 'gn', 25, 3, 'Jocsã gerou Sabá e Dedã; os descendentes de Dedã foram os assuritas, os letusitas e os leumitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (662, 'nvi', 'Gênesis', 'gn', 25, 4, 'Os filhos de Midiã foram Efá, Éfer, Enoque, Abida e Elda. Todos esses foram descendentes de Quetura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (663, 'nvi', 'Gênesis', 'gn', 25, 5, 'Abraão deixou tudo o que tinha para Isaque.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (664, 'nvi', 'Gênesis', 'gn', 25, 6, 'Mas para os filhos de suas concubinas deu presentes; e, ainda em vida, enviou-os para longe de Isaque, para a terra do Oriente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (665, 'nvi', 'Gênesis', 'gn', 25, 7, 'Abraão viveu cento e setenta e cinco anos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (666, 'nvi', 'Gênesis', 'gn', 25, 8, 'Morreu em boa velhice, em idade bem avançada, e foi reunido aos seus antepassados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (667, 'nvi', 'Gênesis', 'gn', 25, 9, 'Seus filhos, Isaque e Ismael, o sepultaram na caverna de Macpela, perto de Manre, no campo de Efrom, filho de Zoar, o hitita,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (668, 'nvi', 'Gênesis', 'gn', 25, 10, 'campo que Abraão comprara dos hititas. Foi ali que Abraão e Sara, sua mulher, foram sepultados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (669, 'nvi', 'Gênesis', 'gn', 25, 11, 'Depois da morte de Abraão, Deus abençoou seu filho Isaque. Isaque morava próximo a Beer-Laai-Roi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (670, 'nvi', 'Gênesis', 'gn', 25, 12, 'Este é o registro da descendência de Ismael, o filho de Abraão que Hagar, a serva egípcia de Sara, deu a ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (671, 'nvi', 'Gênesis', 'gn', 25, 13, 'São estes os nomes dos filhos de Ismael, alistados por ordem de nascimento: Nebaiote, o filho mais velho de Ismael, Quedar, Adbeel, Mibsão,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (672, 'nvi', 'Gênesis', 'gn', 25, 14, 'Misma, Dumá, Massá,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (673, 'nvi', 'Gênesis', 'gn', 25, 15, 'Hadade, Temá, Jetur, Nafis e Quedemá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (674, 'nvi', 'Gênesis', 'gn', 25, 16, 'Foram esses os doze filhos de Ismael, que se tornaram os líderes de suas tribos; os seus povoados e acampamentos receberam os seus nomes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (675, 'nvi', 'Gênesis', 'gn', 25, 17, 'Ismael viveu cento e trinta e sete anos. Morreu e foi reunido aos seus antepassados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (676, 'nvi', 'Gênesis', 'gn', 25, 18, 'Seus descendentes se estabeleceram na região que vai de Havilá a Sur, próximo à fronteira com o Egito, na direção de quem vai para Assur. E viveram em hostilidade contra todos os seus irmãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (677, 'nvi', 'Gênesis', 'gn', 25, 19, 'Esta é a história da família de Isaque, filho de Abraão: Abraão gerou Isaque,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (678, 'nvi', 'Gênesis', 'gn', 25, 20, 'o qual aos quarenta anos se casou com Rebeca, filha de Betuel, o arameu de Padã-Arã, e irmã de Labão, também arameu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (679, 'nvi', 'Gênesis', 'gn', 25, 21, 'Isaque orou ao Senhor em favor de sua mulher, porque era estéril. O SENHOR respondeu à sua oração, e Rebeca, sua mulher, engravidou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (680, 'nvi', 'Gênesis', 'gn', 25, 22, 'Os meninos se empurravam dentro dela, pelo que disse: "Por que está me acontecendo isso? " Foi então consultar o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (681, 'nvi', 'Gênesis', 'gn', 25, 23, 'Disse-lhe o Senhor: "Duas nações estão em seu ventre, já desde as suas entranhas dois povos se separarão; um deles será mais forte que o outro, mas o mais velho servirá ao mais novo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (682, 'nvi', 'Gênesis', 'gn', 25, 24, 'Ao chegar a época de dar à luz, confirmou-se que havia gêmeos em seu ventre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (683, 'nvi', 'Gênesis', 'gn', 25, 25, 'O primeiro a sair era ruivo, e todo o seu corpo era como um manto de pêlos; por isso lhe deram o nome de Esaú.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (684, 'nvi', 'Gênesis', 'gn', 25, 26, 'Depois saiu seu irmão, com a mão agarrada no calcanhar de Esaú; pelo que lhe deram o nome de Jacó. Tinha Isaque sessenta anos de idade quando Rebeca os deu à luz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (685, 'nvi', 'Gênesis', 'gn', 25, 27, 'Os meninos cresceram. Esaú tornou-se caçador habilidoso e vivia percorrendo os campos, ao passo que Jacó cuidava do rebanho e vivia nas tendas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (686, 'nvi', 'Gênesis', 'gn', 25, 28, 'Isaque preferia Esaú, porque gostava de comer de suas caças; Rebeca preferia Jacó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (687, 'nvi', 'Gênesis', 'gn', 25, 29, 'Certa vez, quando Jacó preparava um ensopado, Esaú chegou faminto, voltando do campo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (688, 'nvi', 'Gênesis', 'gn', 25, 30, 'e pediu-lhe: "Dê-me um pouco desse ensopado vermelho aí. Estou faminto! " Por isso também foi chamado Edom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (689, 'nvi', 'Gênesis', 'gn', 25, 31, 'Respondeu-lhe Jacó: "Venda-me primeiro o seu direito de filho mais velho".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (690, 'nvi', 'Gênesis', 'gn', 25, 32, 'Disse Esaú: "Estou quase morrendo. De que me vale esse direito? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (691, 'nvi', 'Gênesis', 'gn', 25, 33, 'Jacó, porém, insistiu: "Jure primeiro". Então ele fez um juramento, vendendo o seu direito de filho mais velho a Jacó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (692, 'nvi', 'Gênesis', 'gn', 25, 34, 'Então Jacó serviu a Esaú pão com ensopado de lentilhas. Ele comeu e bebeu, levantou-se e se foi. Assim Esaú desprezou o seu direito de filho mais velho.');
