INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (728, 'nvi', 'Gênesis', 'gn', 27, 1, 'Tendo Isaque envelhecido, seus olhos ficaram tão fracos que ele já não podia enxergar. Certo dia chamou Esaú, seu filho mais velho, e lhe disse: "Meu filho! " Ele respondeu: "Estou aqui".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (729, 'nvi', 'Gênesis', 'gn', 27, 2, 'Disse-lhe Isaque: "Já estou velho e não sei o dia da minha morte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (730, 'nvi', 'Gênesis', 'gn', 27, 3, 'Pegue agora suas armas, o arco e a aljava, e vá ao campo caçar alguma coisa para mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (731, 'nvi', 'Gênesis', 'gn', 27, 4, 'Prepare-me aquela comida saborosa que tanto aprecio e traga-me, para que eu a coma e o abençoe antes de morrer".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (732, 'nvi', 'Gênesis', 'gn', 27, 5, 'Ora, Rebeca estava ouvindo o que Isaque dizia a seu filho Esaú. Quando Esaú saiu ao campo para caçar,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (733, 'nvi', 'Gênesis', 'gn', 27, 6, 'Rebeca disse a seu filho Jacó: "Ouvi seu pai dizer a seu irmão Esaú:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (734, 'nvi', 'Gênesis', 'gn', 27, 7, '‘Traga-me alguma caça e prepare-me aquela comida saborosa, para que eu a coma e o abençoe na presença do Senhor antes de morrer’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (735, 'nvi', 'Gênesis', 'gn', 27, 8, 'Agora, meu filho, ouça bem e faça o que lhe ordeno:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (736, 'nvi', 'Gênesis', 'gn', 27, 9, 'Vá ao rebanho e traga-me dois cabritos escolhidos, para que eu prepare uma comida saborosa para seu pai, como ele aprecia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (737, 'nvi', 'Gênesis', 'gn', 27, 10, 'Leve-a então a seu pai, para que ele a coma e o abençoe antes de morrer".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (738, 'nvi', 'Gênesis', 'gn', 27, 11, 'Disse Jacó a Rebeca, sua mãe: "Mas o meu irmão Esaú é homem peludo, e eu tenho a pele lisa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (739, 'nvi', 'Gênesis', 'gn', 27, 12, 'E se meu pai me apalpar? Vai parecer que estou tentando enganá-lo, fazendo-o de tolo e, em vez de bênção, trarei sobre mim maldição".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (740, 'nvi', 'Gênesis', 'gn', 27, 13, 'Disse-lhe sua mãe: "Caia sobre mim a maldição, meu filho. Faça apenas o que eu digo: Vá e traga-os para mim".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (741, 'nvi', 'Gênesis', 'gn', 27, 14, 'Então ele foi, apanhou-os e os trouxe à sua mãe, que preparou uma comida saborosa, como seu pai apreciava.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (742, 'nvi', 'Gênesis', 'gn', 27, 15, 'Rebeca pegou as melhores roupas de Esaú, seu filho mais velho, roupas que tinha em casa, e colocou-as em Jacó, seu filho mais novo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (743, 'nvi', 'Gênesis', 'gn', 27, 16, 'Depois cobriu-lhe as mãos e a parte lisa do pescoço com as peles dos cabritos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (744, 'nvi', 'Gênesis', 'gn', 27, 17, 'e por fim entregou a Jacó a refeição saborosa e o pão que tinha feito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (745, 'nvi', 'Gênesis', 'gn', 27, 18, 'Ele se dirigiu ao pai e disse: "Meu pai". Respondeu ele: "Sim, meu filho. Quem é você? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (746, 'nvi', 'Gênesis', 'gn', 27, 19, 'Jacó disse a seu pai: "Sou Esaú, seu filho mais velho. Fiz como o senhor me disse. Agora, assente-se e coma do que cacei para que me abençoe".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (747, 'nvi', 'Gênesis', 'gn', 27, 20, 'Isaque perguntou ao filho: "Como encontrou a caça tão depressa, meu filho? " Ele respondeu: "O Senhor, o seu Deus, a colocou no meu caminho".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (748, 'nvi', 'Gênesis', 'gn', 27, 21, 'Então Isaque disse a Jacó: "Chegue mais perto, meu filho, para que eu possa apalpá-lo e saber se você é realmente meu filho Esaú".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (749, 'nvi', 'Gênesis', 'gn', 27, 22, 'Jacó aproximou-se do seu pai Isaque, que o apalpou e disse: "A voz é de Jacó, mas os braços são de Esaú".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (750, 'nvi', 'Gênesis', 'gn', 27, 23, 'Não o reconheceu, pois seus braços estavam peludos como os de Esaú, seu irmão; e o abençoou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (751, 'nvi', 'Gênesis', 'gn', 27, 24, 'Isaque perguntou-lhe outra vez: "Você é mesmo meu filho Esaú? " E ele respondeu: "Sou".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (752, 'nvi', 'Gênesis', 'gn', 27, 25, 'Então lhe disse: "Meu filho, traga-me da sua caça para que eu coma e o abençoe". Jacó a trouxe, e o pai comeu; também trouxe vinho, e ele bebeu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (753, 'nvi', 'Gênesis', 'gn', 27, 26, 'Então seu pai Isaque lhe disse: "Venha cá, meu filho, dê-me um beijo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (754, 'nvi', 'Gênesis', 'gn', 27, 27, 'Ele se aproximou e o beijou. Quando sentiu o cheiro de suas roupas, Isaque o abençoou, dizendo: "Ah, o cheiro de meu filho é como o cheiro de um campo que o Senhor abençoou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (755, 'nvi', 'Gênesis', 'gn', 27, 28, 'Que Deus lhe conceda do céu o orvalho e da terra a riqueza, com muito cereal e muito vinho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (756, 'nvi', 'Gênesis', 'gn', 27, 29, 'Que as nações o sirvam e os povos se curvem diante de você. Seja senhor dos seus irmãos, e curvem-se diante de você os filhos de sua mãe. Malditos sejam os que o amaldiçoarem e benditos sejam os que o abençoarem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (757, 'nvi', 'Gênesis', 'gn', 27, 30, 'Quando Isaque acabou de abençoar Jacó, mal tendo ele saído da presença do pai, seu irmão Esaú chegou da caçada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (758, 'nvi', 'Gênesis', 'gn', 27, 31, 'Ele também preparou uma comida saborosa e a trouxe a seu pai. E lhe disse: "Meu pai, levante-se e coma da minha caça, para que o senhor me dê sua bênção".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (759, 'nvi', 'Gênesis', 'gn', 27, 32, 'Perguntou-lhe seu pai Isaque: "Quem é você? " Ele respondeu: "Sou Esaú, seu filho mais velho".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (760, 'nvi', 'Gênesis', 'gn', 27, 33, 'Profundamente abalado, Isaque começou a tremer muito e disse: "Quem então apanhou a caça e a trouxe para mim? Acabei de comê-la antes de você entrar e a ele abençoei; e abençoado ele será! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (761, 'nvi', 'Gênesis', 'gn', 27, 34, 'Quando Esaú ouviu as palavras de seu pai, deu um forte grito e, cheio de amargura, implorou ao pai: "Abençoe também a mim, meu pai! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (762, 'nvi', 'Gênesis', 'gn', 27, 35, 'Mas ele respondeu: "Seu irmão chegou astutamente e recebeu a bênção que pertencia a você".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (763, 'nvi', 'Gênesis', 'gn', 27, 36, 'E disse Esaú: "Não é com razão que o seu nome é Jacó? Já é a segunda vez que ele me engana! Primeiro, tomou o meu direito de filho mais velho e agora recebeu a minha bênção! " Então perguntou ao pai: "O senhor não reservou nenhuma bênção para mim? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (764, 'nvi', 'Gênesis', 'gn', 27, 37, 'Isaque respondeu a Esaú: "Eu o constituí senhor sobre você, e a todos os seus parentes tornei servos dele; a ele supri de cereal e de vinho. Que é que eu poderia fazer por você, meu filho? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (765, 'nvi', 'Gênesis', 'gn', 27, 38, 'Esaú pediu ao pai: "Meu pai, o senhor tem apenas uma bênção? Abençoe-me também, meu pai! " Então chorou Esaú em alta voz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (766, 'nvi', 'Gênesis', 'gn', 27, 39, 'Seu pai Isaque respondeu-lhe: "Sua habitação será longe das terras férteis, distante do orvalho que desce do alto céu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (767, 'nvi', 'Gênesis', 'gn', 27, 40, 'Você viverá por sua espada e servirá a seu irmão. Mas quando você não suportar mais, arrancará do pescoço o jugo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (768, 'nvi', 'Gênesis', 'gn', 27, 41, 'Esaú guardou rancor contra Jacó por causa da bênção que seu pai lhe dera. E disse a si mesmo: "Os dias de luto pela morte de meu pai estão próximos; então matarei meu irmão Jacó".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (769, 'nvi', 'Gênesis', 'gn', 27, 42, 'Quando contaram a Rebeca o que seu filho Esaú dissera, ela mandou chamar Jacó, seu filho mais novo, e lhe disse: "Esaú está se consolando com a idéia de matá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (770, 'nvi', 'Gênesis', 'gn', 27, 43, 'Ouça, pois, o que lhe digo, meu filho: Fuja imediatamente para a casa de meu irmão Labão, em Harã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (771, 'nvi', 'Gênesis', 'gn', 27, 44, 'Fique com ele algum tempo, até que passe o furor de seu irmão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (772, 'nvi', 'Gênesis', 'gn', 27, 45, 'Quando seu irmão não estiver mais irado contra você e esquecer o que você lhe fez, mandarei buscá-lo. Por que perderia eu vocês dois num só dia? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (773, 'nvi', 'Gênesis', 'gn', 27, 46, 'Então Rebeca disse a Isaque: "Estou desgostosa da vida, por causa destas mulheres hititas. Se Jacó escolher esposa entre as mulheres desta terra, entre mulheres hititas como estas, perderei a razão de viver".');
