INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (774, 'nvi', 'Gênesis', 'gn', 28, 1, 'Então Isaque chamou Jacó, deu-lhe sua bênção e lhe ordenou: "Não se case com mulher cananéia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (775, 'nvi', 'Gênesis', 'gn', 28, 2, 'Vá a Padã-Arã, à casa de Betuel, seu avô materno, e case-se com uma das filhas de Labão, irmão de sua mãe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (776, 'nvi', 'Gênesis', 'gn', 28, 3, 'Que o Deus Todo-poderoso o abençoe, faça-o prolífero e multiplique os seus descendentes, para que você se torne uma comunidade de povos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (777, 'nvi', 'Gênesis', 'gn', 28, 4, 'Que ele dê a você e a seus descendentes a bênção de Abraão, para que você tome posse da terra na qual vive como estrangeiro, a terra dada por Deus a Abraão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (778, 'nvi', 'Gênesis', 'gn', 28, 5, 'Então Isaque despediu Jacó e este foi a Padã-Arã, a Labão, filho de Betuel, o arameu, e irmão de Rebeca, mãe de Jacó e Esaú.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (779, 'nvi', 'Gênesis', 'gn', 28, 6, 'Esaú viu que Isaque havia abençoado a Jacó e o havia mandado a Padã-Arã para escolher ali uma mulher e que, ao abençoá-lo, dera-lhe a ordem de não se casar com mulher cananéia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (780, 'nvi', 'Gênesis', 'gn', 28, 7, 'Também soube que Jacó obedecera a seu pai e a sua mãe e fora para Padã-Arã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (781, 'nvi', 'Gênesis', 'gn', 28, 8, 'Percebendo então Esaú que seu pai Isaque não aprovava as mulheres cananéias,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (782, 'nvi', 'Gênesis', 'gn', 28, 9, 'foi à casa de Ismael e tomou a Maalate, irmã de Nebaiote, filha de Ismael, filho de Abraão, além das outras mulheres que já tinha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (783, 'nvi', 'Gênesis', 'gn', 28, 10, 'Jacó partiu de Berseba e foi para Harã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (784, 'nvi', 'Gênesis', 'gn', 28, 11, 'Chegando a determinado lugar, parou para pernoitar, porque o sol já se havia posto. Tomando uma das pedras dali, usou-a como travesseiro e deitou-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (785, 'nvi', 'Gênesis', 'gn', 28, 12, 'E teve um sonho no qual viu uma escada apoiada na terra; o seu topo alcançava os céus, e os anjos de Deus subiam e desciam por ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (786, 'nvi', 'Gênesis', 'gn', 28, 13, 'Ao lado dele estava o Senhor, que lhe disse: "Eu sou o Senhor, o Deus de seu pai Abraão e o Deus de Isaque. Darei a você e a seus descendentes a terra na qual você está deitado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (787, 'nvi', 'Gênesis', 'gn', 28, 14, 'Seus descendentes serão como o pó da terra, e se espalharão para o Oeste e para o Leste, para o Norte e para o Sul. Todos os povos da terra serão abençoados por meio de você e da sua descendência.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (788, 'nvi', 'Gênesis', 'gn', 28, 15, 'Estou com você e cuidarei de você, aonde quer que vá; e eu o trarei de volta a esta terra. Não o deixarei enquanto não fizer o que lhe prometi".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (789, 'nvi', 'Gênesis', 'gn', 28, 16, 'Quando Jacó acordou do sono, disse: "Sem dúvida o Senhor está neste lugar, mas eu não sabia! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (790, 'nvi', 'Gênesis', 'gn', 28, 17, 'Teve medo e disse: "Temível é este lugar! Não é outro, senão a casa de Deus; esta é a porta dos céus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (791, 'nvi', 'Gênesis', 'gn', 28, 18, 'Na manhã seguinte, Jacó pegou a pedra que tinha usado como travesseiro, colocou-a de pé como coluna e derramou óleo sobre o seu topo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (792, 'nvi', 'Gênesis', 'gn', 28, 19, 'E deu o nome de Betel àquele lugar, embora a cidade anteriormente se chamasse Luz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (793, 'nvi', 'Gênesis', 'gn', 28, 20, 'Então Jacó fez um voto, dizendo: "Se Deus estiver comigo, cuidar de mim nesta viagem que estou fazendo, prover-me de comida e roupa,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (794, 'nvi', 'Gênesis', 'gn', 28, 21, 'e levar-me de volta em segurança à casa de meu pai, então o Senhor será o meu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (795, 'nvi', 'Gênesis', 'gn', 28, 22, 'E esta pedra que hoje coloquei como coluna servirá de santuário de Deus; e de tudo o que me deres certamente te darei o dízimo".');
