INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (796, 'nvi', 'Gênesis', 'gn', 29, 1, 'Então Jacó seguiu viagem e chegou à Mesopotâmia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (797, 'nvi', 'Gênesis', 'gn', 29, 2, 'Certo dia, olhando ao redor, viu um poço no campo e três rebanhos de ovelhas deitadas por perto, pois os rebanhos bebiam daquele poço, que era tapado por uma grande pedra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (798, 'nvi', 'Gênesis', 'gn', 29, 3, 'Por isso, quando todos os rebanhos se reuniam ali, os pastores rolavam a pedra da boca do poço e davam água às ovelhas. Depois recolocavam a pedra em seu lugar, sobre o poço.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (799, 'nvi', 'Gênesis', 'gn', 29, 4, 'Jacó perguntou aos pastores: "Meus amigos, de onde são vocês? " "Somos de Harã", responderam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (800, 'nvi', 'Gênesis', 'gn', 29, 5, '"Vocês conhecem Labão, neto de Naor? ", perguntou-lhes Jacó. Eles responderam: "Sim, nós o conhecemos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (801, 'nvi', 'Gênesis', 'gn', 29, 6, 'Então Jacó perguntou: "Ele vai bem? " "Sim, vai bem", disseram eles, "e ali vem sua filha Raquel com as ovelhas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (802, 'nvi', 'Gênesis', 'gn', 29, 7, 'Disse ele: "Olhem, o sol ainda vai alto e não é hora de recolher os rebanhos. Dêem de beber às ovelhas e levem-nas de volta ao pasto".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (803, 'nvi', 'Gênesis', 'gn', 29, 8, 'Mas eles responderam: "Não podemos, enquanto os rebanhos não se agruparem e a pedra não for removida da boca do poço. Só então daremos de beber às ovelhas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (804, 'nvi', 'Gênesis', 'gn', 29, 9, 'Ele ainda estava conversando, quando chegou Raquel com as ovelhas de seu pai, pois ela era pastora.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (805, 'nvi', 'Gênesis', 'gn', 29, 10, 'Quando Jacó viu Raquel, filha de Labão, irmão de sua mãe, e as ovelhas de Labão, aproximou-se, removeu a pedra da boca do poço e deu de beber às ovelhas de seu tio Labão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (806, 'nvi', 'Gênesis', 'gn', 29, 11, 'Depois Jacó beijou Raquel e começou a chorar bem alto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (807, 'nvi', 'Gênesis', 'gn', 29, 12, 'Então contou a Raquel que era parente do pai dela e filho de Rebeca. E ela foi correndo contar tudo a seu pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (808, 'nvi', 'Gênesis', 'gn', 29, 13, 'Logo que Labão ouviu as notícias acerca de Jacó, seu sobrinho, correu ao seu encontro, abraçou-o e o beijou. Depois, levou-o para casa, e Jacó contou-lhe tudo o que havia ocorrido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (809, 'nvi', 'Gênesis', 'gn', 29, 14, 'Então Labão lhe disse: "Você é sangue do meu sangue". Já fazia um mês que Jacó estava na casa de Labão,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (810, 'nvi', 'Gênesis', 'gn', 29, 15, 'quando este lhe disse: "Só por ser meu parente você vai trabalhar de graça? Diga-me qual deve ser o seu salário".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (811, 'nvi', 'Gênesis', 'gn', 29, 16, 'Ora, Labão tinha duas filhas; o nome da mais velha era Lia, e o da mais nova, Raquel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (812, 'nvi', 'Gênesis', 'gn', 29, 17, 'Lia tinha olhos meigos, mas Raquel era bonita e atraente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (813, 'nvi', 'Gênesis', 'gn', 29, 18, 'Como Jacó gostava muito de Raquel, disse: "Trabalharei sete anos em troca de Raquel, sua filha mais nova".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (814, 'nvi', 'Gênesis', 'gn', 29, 19, 'Labão respondeu: "Será melhor dá-la a você do que a algum outro homem. Fique aqui comigo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (815, 'nvi', 'Gênesis', 'gn', 29, 20, 'Então Jacó trabalhou sete anos por Raquel, mas lhe pareceram poucos dias, pelo tanto que a amava.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (816, 'nvi', 'Gênesis', 'gn', 29, 21, 'Então disse Jacó a Labão: "Entregue-me a minha mulher. Cumpri o prazo previsto e quero deitar-me com ela".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (817, 'nvi', 'Gênesis', 'gn', 29, 22, 'Então Labão reuniu todo o povo daquele lugar e deu uma festa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (818, 'nvi', 'Gênesis', 'gn', 29, 23, 'Mas quando a noite chegou, deu sua filha Lia a Jacó, e Jacó deitou-se com ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (819, 'nvi', 'Gênesis', 'gn', 29, 24, 'Labão também entregou sua serva Zilpa à sua filha, para que ficasse a serviço dela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (820, 'nvi', 'Gênesis', 'gn', 29, 25, 'Quando chegou a manhã, lá estava Lia. Então Jacó disse a Labão: "Que foi que você me fez? Eu não trabalhei por Raquel? Por que você me enganou? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (821, 'nvi', 'Gênesis', 'gn', 29, 26, 'Labão respondeu: "Aqui não é costume entregar em casamento a filha mais nova antes da mais velha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (822, 'nvi', 'Gênesis', 'gn', 29, 27, 'Deixe passar esta semana de núpcias e lhe daremos também a mais nova, em troca de mais sete anos de trabalho".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (823, 'nvi', 'Gênesis', 'gn', 29, 28, 'Jacó concordou. Passou aquela semana de núpcias com Lia, e Labão lhe deu sua filha Raquel por mulher.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (824, 'nvi', 'Gênesis', 'gn', 29, 29, 'Labão deu a Raquel sua serva Bila, para que ficasse a serviço dela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (825, 'nvi', 'Gênesis', 'gn', 29, 30, 'Jacó deitou-se também com Raquel, que era a sua preferida. E trabalhou para Labão outros sete anos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (826, 'nvi', 'Gênesis', 'gn', 29, 31, 'Quando o Senhor viu que Lia era desprezada, concedeu-lhe filhos; Raquel, porém, era estéril.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (827, 'nvi', 'Gênesis', 'gn', 29, 32, 'Lia engravidou, deu à luz um filho, e deu-lhe o nome de Rúben, pois dizia: "O Senhor viu a minha infelicidade. Agora, certamente o meu marido me amará".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (828, 'nvi', 'Gênesis', 'gn', 29, 33, 'Lia engravidou de novo e, quando deu à luz outro filho, disse: "Porque o Senhor ouviu que sou desprezada, deu-me também este". Pelo que o chamou Simeão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (829, 'nvi', 'Gênesis', 'gn', 29, 34, 'De novo engravidou e, quando deu à luz mais um filho, disse: "Agora, finalmente, meu marido se apegará a mim, porque já lhe dei três filhos". Por isso deu-lhe o nome de Levi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (830, 'nvi', 'Gênesis', 'gn', 29, 35, 'Engravidou ainda outra vez e, quando deu à luz mais outro filho, disse: "Desta vez louvarei ao SENHOR". Assim deu-lhe o nome de Judá. Então parou de ter filhos.');
