INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (56, 'nvi', 'Gênesis', 'gn', 3, 1, 'Ora, a serpente era o mais astuto de todos os animais selvagens que o Senhor Deus tinha feito. E ela perguntou à mulher: "Foi isto mesmo que Deus disse: ‘Não comam de nenhum fruto das árvores do jardim’? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (57, 'nvi', 'Gênesis', 'gn', 3, 2, 'Respondeu a mulher à serpente: "Podemos comer do fruto das árvores do jardim,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (58, 'nvi', 'Gênesis', 'gn', 3, 3, 'mas Deus disse: ‘Não comam do fruto da árvore que está no meio do jardim, nem toquem nele; do contrário vocês morrerão’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (59, 'nvi', 'Gênesis', 'gn', 3, 4, 'Disse a serpente à mulher: "Certamente não morrerão!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (60, 'nvi', 'Gênesis', 'gn', 3, 5, 'Deus sabe que, no dia em que dele comerem, seus olhos se abrirão, e vocês serão como Deus, conhecedores do bem e do mal".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (61, 'nvi', 'Gênesis', 'gn', 3, 6, 'Quando a mulher viu que a árvore parecia agradável ao paladar, era atraente aos olhos e, além disso, desejável para dela se obter discernimento, tomou do seu fruto, comeu-o e o deu a seu marido, que comeu também.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (62, 'nvi', 'Gênesis', 'gn', 3, 7, 'Os olhos dos dois se abriram, e perceberam que estavam nus; então juntaram folhas de figueira para cobrir-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (63, 'nvi', 'Gênesis', 'gn', 3, 8, 'Ouvindo o homem e sua mulher os passos do Senhor Deus que andava pelo jardim quando soprava a brisa do dia, esconderam-se da presença do Senhor Deus entre as árvores do jardim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (64, 'nvi', 'Gênesis', 'gn', 3, 9, 'Mas o Senhor Deus chamou o homem, perguntando: "Onde está você? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (65, 'nvi', 'Gênesis', 'gn', 3, 10, 'E ele respondeu: "Ouvi teus passos no jardim e fiquei com medo, porque estava nu; por isso me escondi".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (66, 'nvi', 'Gênesis', 'gn', 3, 11, 'E Deus perguntou: "Quem lhe disse que você estava nu? Você comeu do fruto da árvore da qual lhe proibi comer? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (67, 'nvi', 'Gênesis', 'gn', 3, 12, 'Disse o homem: "Foi a mulher que me deste por companheira que me deu do fruto da árvore, e eu comi".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (68, 'nvi', 'Gênesis', 'gn', 3, 13, 'O Senhor Deus perguntou então à mulher: "Que foi que você fez? " Respondeu a mulher: "A serpente me enganou, e eu comi".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (69, 'nvi', 'Gênesis', 'gn', 3, 14, 'Então o Senhor Deus declarou à serpente: "Já que você fez isso, maldita é você entre todos os rebanhos domésticos e entre todos os animais selvagens! Sobre o seu ventre você rastejará, e pó comerá todos os dias da sua vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (70, 'nvi', 'Gênesis', 'gn', 3, 15, 'Porei inimizade entre você e a mulher, entre a sua descendência e o descendente dela; este lhe ferirá a cabeça, e você lhe ferirá o calcanhar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (71, 'nvi', 'Gênesis', 'gn', 3, 16, 'À mulher, ele declarou: "Multiplicarei grandemente o seu sofrimento na gravidez; com sofrimento você dará à luz filhos. Seu desejo será para o seu marido, e ele a dominará".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (72, 'nvi', 'Gênesis', 'gn', 3, 17, 'E ao homem declarou: "Visto que você deu ouvidos à sua mulher e comeu do fruto da árvore da qual eu lhe ordenara que não comesse, maldita é a terra por sua causa; com sofrimento você se alimentará dela todos os dias da sua vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (73, 'nvi', 'Gênesis', 'gn', 3, 18, 'Ela lhe dará espinhos e ervas daninhas, e você terá que alimentar-se das plantas do campo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (74, 'nvi', 'Gênesis', 'gn', 3, 19, 'Com o suor do seu rosto você comerá o seu pão, até que volte à terra, visto que dela foi tirado; porque você é pó e ao pó voltará".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (75, 'nvi', 'Gênesis', 'gn', 3, 20, 'Adão deu à sua mulher o nome de Eva, pois ela seria mãe de toda a humanidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (76, 'nvi', 'Gênesis', 'gn', 3, 21, 'O Senhor Deus fez roupas de pele e com elas vestiu Adão e sua mulher.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (77, 'nvi', 'Gênesis', 'gn', 3, 22, 'Então disse o Senhor Deus: "Agora o homem se tornou como um de nós, conhecendo o bem e o mal. Não se deve, pois, permitir que ele também tome do fruto da árvore da vida e o coma, e viva para sempre".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (78, 'nvi', 'Gênesis', 'gn', 3, 23, 'Por isso o Senhor Deus o mandou embora do jardim do Éden para cultivar o solo do qual fora tirado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (79, 'nvi', 'Gênesis', 'gn', 3, 24, 'Depois de expulsar o homem, colocou a leste do jardim do Éden querubins e uma espada flamejante que se movia, guardando o caminho para a árvore da vida.');
