INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (831, 'nvi', 'Gênesis', 'gn', 30, 1, 'Quando Raquel viu que não dava filhos a Jacó, teve inveja de sua irmã. Por isso disse a Jacó: "Dê-me filhos ou morrerei! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (832, 'nvi', 'Gênesis', 'gn', 30, 2, 'Jacó ficou irritado e disse: "Por acaso estou no lugar de Deus, que a impediu de ter filhos? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (833, 'nvi', 'Gênesis', 'gn', 30, 3, 'Então ela respondeu: "Aqui está Bila, minha serva. Deite-se com ela, para que tenha filhos em meu lugar e por meio dela eu também possa formar família".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (834, 'nvi', 'Gênesis', 'gn', 30, 4, 'Por isso ela deu a Jacó sua serva Bila por mulher. Ele deitou-se com ela,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (835, 'nvi', 'Gênesis', 'gn', 30, 5, 'Bila engravidou e deu-lhe um filho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (836, 'nvi', 'Gênesis', 'gn', 30, 6, 'Então Raquel disse: "Deus me fez justiça, ouviu o meu clamor e deu-me um filho". Por isso deu-lhe o nome de Dã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (837, 'nvi', 'Gênesis', 'gn', 30, 7, 'Bila, serva de Raquel, engravidou novamente e deu a Jacó o segundo filho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (838, 'nvi', 'Gênesis', 'gn', 30, 8, 'Então disse Raquel: "Tive grande luta com minha irmã e venci". Pelo que o chamou Naftali.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (839, 'nvi', 'Gênesis', 'gn', 30, 9, 'Quando Lia viu que tinha parado de ter filhos, tomou sua serva Zilpa e a deu a Jacó por mulher.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (840, 'nvi', 'Gênesis', 'gn', 30, 10, 'Zilpa, serva de Lia, deu a Jacó um filho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (841, 'nvi', 'Gênesis', 'gn', 30, 11, 'Então disse Lia: "Que grande sorte! " Por isso o chamou Gade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (842, 'nvi', 'Gênesis', 'gn', 30, 12, 'Zilpa, serva de Lia, deu a Jacó mais um filho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (843, 'nvi', 'Gênesis', 'gn', 30, 13, 'Então Lia exclamou: "Como sou feliz! As mulheres dirão que sou feliz". Por isso lhe deu o nome de Aser.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (844, 'nvi', 'Gênesis', 'gn', 30, 14, 'Durante a colheita do trigo, Rúben saiu ao campo, encontrou algumas mandrágoras e as trouxe a Lia, sua mãe. Então Raquel disse a Lia: "Dê-me algumas mandrágoras do seu filho".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (845, 'nvi', 'Gênesis', 'gn', 30, 15, 'Mas ela respondeu: "Não lhe foi suficiente tomar de mim o marido? Vai tomar também as mandrágoras que o meu filho trouxe? " Então disse Raquel: "Jacó se deitará com você esta noite, em troca das mandrágoras trazidas pelo seu filho".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (846, 'nvi', 'Gênesis', 'gn', 30, 16, 'Quando Jacó chegou do campo naquela tarde, Lia saiu ao seu encontro e lhe disse: "Hoje você me possuirá, pois eu comprei esse direito com as mandrágoras do meu filho". E naquela noite ele se deitou com ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (847, 'nvi', 'Gênesis', 'gn', 30, 17, 'Deus ouviu Lia, e ela engravidou e deu a Jacó o quinto filho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (848, 'nvi', 'Gênesis', 'gn', 30, 18, 'Disse Lia: "Deus me recompensou por ter dado a minha serva ao meu marido". Por isso deu-lhe o nome de Issacar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (849, 'nvi', 'Gênesis', 'gn', 30, 19, 'Lia engravidou de novo e deu a Jacó o sexto filho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (850, 'nvi', 'Gênesis', 'gn', 30, 20, 'Disse Lia: "Deus presenteou-me com uma dádiva preciosa. Agora meu marido me tratará melhor; afinal já lhe dei seis filhos". Por isso deu-lhe o nome de Zebulom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (851, 'nvi', 'Gênesis', 'gn', 30, 21, 'Algum tempo depois, ela deu à luz uma menina a quem chamou Diná.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (852, 'nvi', 'Gênesis', 'gn', 30, 22, 'Então Deus lembrou-se de Raquel. Deus ouviu o seu clamor e a tornou fértil.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (853, 'nvi', 'Gênesis', 'gn', 30, 23, 'Ela engravidou, e deu à luz um filho e disse: "Deus tirou de mim a minha humilhação".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (854, 'nvi', 'Gênesis', 'gn', 30, 24, 'Deu-lhe o nome de José e disse: "Que o Senhor me acrescente ainda outro filho".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (855, 'nvi', 'Gênesis', 'gn', 30, 25, 'Depois que Raquel deu à luz José, Jacó disse a Labão: "Deixe-me voltar para a minha terra natal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (856, 'nvi', 'Gênesis', 'gn', 30, 26, 'Dê-me as minhas mulheres, pelas quais o servi, e os meus filhos, e partirei. Você bem sabe quanto trabalhei para você".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (857, 'nvi', 'Gênesis', 'gn', 30, 27, 'Mas Labão lhe disse: "Se mereço sua consideração, peço-lhe que fique. Por meio de adivinhação descobri que o Senhor me abençoou por sua causa".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (858, 'nvi', 'Gênesis', 'gn', 30, 28, 'E acrescentou: "Diga o seu salário, e eu lhe pagarei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (859, 'nvi', 'Gênesis', 'gn', 30, 29, 'Jacó lhe respondeu: "Você sabe quanto trabalhei para você e como os seus rebanhos cresceram sob os meus cuidados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (860, 'nvi', 'Gênesis', 'gn', 30, 30, 'O pouco que você possuía antes da minha chegada aumentou muito, pois o Senhor o abençoou depois que vim para cá. Contudo, quando farei algo em favor da minha própria família? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (861, 'nvi', 'Gênesis', 'gn', 30, 31, 'Então Labão perguntou: "Que você quer que eu lhe dê? " "Não me dê coisa alguma", respondeu Jacó. "Voltarei a cuidar dos seus rebanhos se você concordar com o seguinte:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (862, 'nvi', 'Gênesis', 'gn', 30, 32, 'Hoje passarei por todos os seus rebanhos e tirarei do meio deles todas as ovelhas salpicadas e pintadas, todos os cordeiros pretos e todas as cabras pintadas e salpicadas. Eles serão o meu salário.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (863, 'nvi', 'Gênesis', 'gn', 30, 33, 'E a minha honestidade dará testemunho de mim no futuro, toda vez que você resolver verificar o meu salário. Se estiver em meu poder alguma cabra que não seja salpicada ou pintada, e algum cordeiro que não seja preto, poderá considerá-los roubados. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (864, 'nvi', 'Gênesis', 'gn', 30, 34, 'E disse Labão: "De acordo. Seja como você disse".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (865, 'nvi', 'Gênesis', 'gn', 30, 35, 'Naquele mesmo dia Labão separou todos os bodes que tinham listras ou manchas brancas, todas as cabras que tinham pintas ou manchas brancas, e todos os cordeiros pretos e os colocou aos cuidados de seus filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (866, 'nvi', 'Gênesis', 'gn', 30, 36, 'Afastou-se então de Jacó, à distância equivalente a três dias de viagem, e Jacó continuou a apascentar o resto dos rebanhos de Labão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (867, 'nvi', 'Gênesis', 'gn', 30, 37, 'Jacó pegou galhos verdes de estoraque, amendoeira e plátano e neles fez listras brancas, descascando-os parcialmente e expondo assim a parte branca interna dos galhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (868, 'nvi', 'Gênesis', 'gn', 30, 38, 'Depois fixou os galhos descascados junto aos bebedouros, na frente dos rebanhos, no lugar onde costumavam beber água. Na época do cio, os rebanhos vinham beber e');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (869, 'nvi', 'Gênesis', 'gn', 30, 39, 'se acasalavam diante dos galhos. E geravam filhotes listrados, salpicados e pintados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (870, 'nvi', 'Gênesis', 'gn', 30, 40, 'Jacó separava os filhotes do rebanho dos demais, e fazia com que esses ficassem juntos dos animais listrados e pretos de Labão. Assim foi formando o seu próprio rebanho que separou do de Labão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (871, 'nvi', 'Gênesis', 'gn', 30, 41, 'Toda vez que as fêmeas mais fortes estavam no cio, Jacó colocava os galhos nos bebedouros, em frente dos animais, para que estes se acasalassem perto dos galhos;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (872, 'nvi', 'Gênesis', 'gn', 30, 42, 'mas, se os animais eram fracos, não os colocava ali. Desse modo, os animais fracos ficavam para Labão e os mais fortes para Jacó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (873, 'nvi', 'Gênesis', 'gn', 30, 43, 'Assim o homem ficou extremamente rico, tornando-se dono de grandes rebanhos e de servos e servas, camelos e jumentos.');
