INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (929, 'nvi', 'Gênesis', 'gn', 32, 1, 'Jacó também seguiu o seu caminho, e anjos de Deus vieram ao encontro dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (930, 'nvi', 'Gênesis', 'gn', 32, 2, 'Quando Jacó os avistou, disse: "Este é o exército de Deus! " Por isso deu àquele lugar o nome de Maanaim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (931, 'nvi', 'Gênesis', 'gn', 32, 3, 'Jacó mandou mensageiros adiante dele a seu irmão Esaú, na região de Seir, território de Edom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (932, 'nvi', 'Gênesis', 'gn', 32, 4, 'E lhes ordenou: "Vocês dirão o seguinte ao meu senhor Esaú: assim diz teu servo Jacó: Morei na casa de Labão e com ele permaneci até agora.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (933, 'nvi', 'Gênesis', 'gn', 32, 5, 'Tenho bois e jumentos, ovelhas e cabras, servos e servas. Envio agora esta mensagem ao meu senhor, para que me recebas bem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (934, 'nvi', 'Gênesis', 'gn', 32, 6, 'Quando os mensageiros voltaram a Jacó, disseram-lhe: "Fomos até seu irmão Esaú, e ele está vindo ao seu encontro, com quatrocentos homens".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (935, 'nvi', 'Gênesis', 'gn', 32, 7, 'Jacó encheu-se de medo e foi tomado de angústia. Então dividiu em dois grupos todos os que estavam com ele, bem como as ovelhas, as cabras, os bois, e os camelos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (936, 'nvi', 'Gênesis', 'gn', 32, 8, 'pois assim pensou: "Se Esaú vier e atacar um dos grupos, o outro poderá escapar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (937, 'nvi', 'Gênesis', 'gn', 32, 9, 'Então Jacó orou: "Ó Deus de meu pai Abraão, Deus de meu pai Isaque, ó Senhor que me disseste: ‘Volte para a sua terra e para os seus parentes e eu o farei prosperar’;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (938, 'nvi', 'Gênesis', 'gn', 32, 10, 'não sou digno de toda a bondade e lealdade com que trataste o teu servo. Quando atravessei o Jordão eu tinha apenas o meu cajado, mas agora possuo duas caravanas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (939, 'nvi', 'Gênesis', 'gn', 32, 11, 'Livra-me, rogo-te, das mãos de meu irmão Esaú, porque tenho medo que ele venha nos atacar, tanto a mim como às mães e às crianças.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (940, 'nvi', 'Gênesis', 'gn', 32, 12, 'Pois tu prometeste: ‘Esteja certo de que eu o farei prosperar e farei os seus descendentes tão numerosos como a areia do mar, que não se pode contar’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (941, 'nvi', 'Gênesis', 'gn', 32, 13, 'Depois de passar ali a noite, escolheu entre os seus rebanhos um presente para o seu irmão Esaú:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (942, 'nvi', 'Gênesis', 'gn', 32, 14, 'duzentas cabras e vinte bodes, duzentas ovelhas e vinte carneiros,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (943, 'nvi', 'Gênesis', 'gn', 32, 15, 'trinta fêmeas de camelo com seus filhotes, quarenta vacas e dez touros, vinte jumentas e dez jumentos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (944, 'nvi', 'Gênesis', 'gn', 32, 16, 'Colocou cada rebanho sob o cuidado de um servo, e disse-lhes: "Vão à minha frente e mantenham certa distância entre um rebanho e outro".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (945, 'nvi', 'Gênesis', 'gn', 32, 17, 'Ao que ia à frente deu a seguinte instrução: "Quando meu irmão Esaú encontrar-se com você e lhe perguntar: ‘A quem você pertence, para onde vai e de quem é todo este rebanho à sua frente? ’,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (946, 'nvi', 'Gênesis', 'gn', 32, 18, 'você responderá: É do teu servo Jacó. É um presente para o meu senhor Esaú; e ele mesmo está vindo atrás de nós".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (947, 'nvi', 'Gênesis', 'gn', 32, 19, 'Também instruiu o segundo, o terceiro e todos os outros que acompanhavam os rebanhos: "Digam também a mesma coisa a Esaú quando o encontrarem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (948, 'nvi', 'Gênesis', 'gn', 32, 20, 'E acrescentem: Teu servo Jacó está vindo atrás de nós". Porque pensava: "Eu o apaziguarei com esses presentes que estou enviando antes de mim; mais tarde, quando eu o vir, talvez me receba".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (949, 'nvi', 'Gênesis', 'gn', 32, 21, 'Assim os presentes de Jacó seguiram à sua frente; ele, porém, passou a noite no acampamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (950, 'nvi', 'Gênesis', 'gn', 32, 22, 'Naquela noite Jacó levantou-se, tomou suas duas mulheres, suas duas servas e seus onze filhos para atravessar o lugar de passagem do Jaboque.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (951, 'nvi', 'Gênesis', 'gn', 32, 23, 'Depois de havê-los feito atravessar o ribeiro, fez passar também tudo o que possuía.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (952, 'nvi', 'Gênesis', 'gn', 32, 24, 'E Jacó ficou sozinho. Então veio um homem que se pôs a lutar com ele até o amanhecer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (953, 'nvi', 'Gênesis', 'gn', 32, 25, 'Quando o homem viu que não poderia dominá-lo, tocou na articulação da coxa de Jacó, de forma que lhe deslocou a coxa, enquanto lutavam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (954, 'nvi', 'Gênesis', 'gn', 32, 26, 'Então o homem disse: "Deixe-me ir, pois o dia já desponta". Mas Jacó lhe respondeu: "Não te deixarei ir, a não ser que me abençoes".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (955, 'nvi', 'Gênesis', 'gn', 32, 27, 'O homem lhe perguntou: "Qual é o seu nome? " "Jacó", respondeu ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (956, 'nvi', 'Gênesis', 'gn', 32, 28, 'Então disse o homem: "Seu nome não será mais Jacó, mas sim Israel, porque você lutou com Deus e com homens e venceu".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (957, 'nvi', 'Gênesis', 'gn', 32, 29, 'Prosseguiu Jacó: "Peço-te que digas o teu nome". Mas ele respondeu: "Por que pergunta o meu nome? " E o abençoou ali.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (958, 'nvi', 'Gênesis', 'gn', 32, 30, 'Jacó chamou àquele lugar Peniel, pois disse: "Vi a Deus face a face e, todavia, minha vida foi poupada".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (959, 'nvi', 'Gênesis', 'gn', 32, 31, 'Ao nascer do sol atravessou Peniel, mancando por causa da coxa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (960, 'nvi', 'Gênesis', 'gn', 32, 32, 'Por isso, até o dia de hoje, os israelitas não comem o músculo ligado à articulação do quadril, porque nesse músculo Jacó foi ferido.');
