INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (961, 'nvi', 'Gênesis', 'gn', 33, 1, 'Quando Jacó olhou e viu que Esaú estava se aproximando, com quatrocentos homens, dividiu as crianças entre Lia, Raquel e as duas servas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (962, 'nvi', 'Gênesis', 'gn', 33, 2, 'Colocou as servas e os seus filhos à frente, Lia e seus filhos depois, e Raquel com José por último.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (963, 'nvi', 'Gênesis', 'gn', 33, 3, 'Ele mesmo passou à frente e, ao aproximar-se do seu irmão, curvou-se até o chão sete vezes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (964, 'nvi', 'Gênesis', 'gn', 33, 4, 'Mas Esaú correu ao encontro de Jacó e abraçou-se ao seu pescoço, e o beijou. E eles choraram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (965, 'nvi', 'Gênesis', 'gn', 33, 5, 'Então Esaú ergueu o olhar e viu as mulheres e as crianças. E perguntou: "Quem são estes? " Jacó respondeu: "São os filhos que Deus concedeu ao teu servo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (966, 'nvi', 'Gênesis', 'gn', 33, 6, 'Então as servas e os seus filhos se aproximaram e se curvaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (967, 'nvi', 'Gênesis', 'gn', 33, 7, 'Depois, Lia e os seus filhos vieram e se curvaram. Por último, chegaram José e Raquel, e também se curvaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (968, 'nvi', 'Gênesis', 'gn', 33, 8, 'Esaú perguntou: "O que você pretende com todos os rebanhos que encontrei pelo caminho? " "Ser bem recebido por ti, meu senhor", respondeu Jacó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (969, 'nvi', 'Gênesis', 'gn', 33, 9, 'Disse, porém, Esaú: "Eu já tenho muito, meu irmão. Guarde para você o que é seu".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (970, 'nvi', 'Gênesis', 'gn', 33, 10, 'Mas Jacó insistiu: "Não! Se te agradaste de mim, aceita este presente de minha parte, porque ver a tua face é como contemplar a face de Deus; além disso, tu me recebeste tão bem!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (971, 'nvi', 'Gênesis', 'gn', 33, 11, 'Aceita, pois, o presente que te foi trazido, pois Deus tem sido favorável para comigo, e eu já tenho tudo o que necessito". Jacó tanto insistiu que Esaú acabou aceitando.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (972, 'nvi', 'Gênesis', 'gn', 33, 12, 'Então disse Esaú: "Vamos seguir em frente. Eu o acompanharei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (973, 'nvi', 'Gênesis', 'gn', 33, 13, 'Jacó, porém, lhe disse: "Meu senhor sabe que as crianças são frágeis e que estão sob os meus cuidados ovelhas e vacas que amamentam suas crias. Se forçá-las demais na caminhada, um só dia que seja, todo o rebanho morrerá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (974, 'nvi', 'Gênesis', 'gn', 33, 14, 'Por isso, meu senhor, vai à frente do teu servo, e eu sigo atrás, devagar, no passo dos rebanhos e das crianças, até que eu chegue ao meu senhor em Seir".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (975, 'nvi', 'Gênesis', 'gn', 33, 15, 'Esaú sugeriu: "Permita-me, então, deixar alguns homens com você". Jacó perguntou: "Mas para quê, meu senhor? Ter sido bem recebido já me foi suficiente! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (976, 'nvi', 'Gênesis', 'gn', 33, 16, 'Naquele dia Esaú voltou para Seir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (977, 'nvi', 'Gênesis', 'gn', 33, 17, 'Jacó, todavia, foi para Sucote, onde construiu uma casa para si e abrigos para o seu gado. Foi por isso que o lugar recebeu o nome de Sucote.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (978, 'nvi', 'Gênesis', 'gn', 33, 18, 'Tendo voltado de Padã-Arã, Jacó chegou a salvo à cidade de Siquém, em Canaã, e acampou próximo da cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (979, 'nvi', 'Gênesis', 'gn', 33, 19, 'Por cem peças de prata comprou dos filhos de Hamor, pai de Siquém, a parte do campo onde tinha armado acampamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (980, 'nvi', 'Gênesis', 'gn', 33, 20, 'Ali edificou um altar e lhe chamou El Elohe Israel.');
