INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (981, 'nvi', 'Gênesis', 'gn', 34, 1, 'Certa vez, Diná, a filha que Lia dera a Jacó, saiu para conhecer as mulheres daquela terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (982, 'nvi', 'Gênesis', 'gn', 34, 2, 'Siquém, filho de Hamor, o heveu, governador daquela região, viu-a, agarrou-a e violentou-a.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (983, 'nvi', 'Gênesis', 'gn', 34, 3, 'Mas o seu coração foi atraído por Diná, filha de Jacó, e ele amou a moça e falou-lhe com ternura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (984, 'nvi', 'Gênesis', 'gn', 34, 4, 'Por isso Siquém foi dizer a seu pai Hamor: "Consiga-me aquela moça para que seja minha mulher".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (985, 'nvi', 'Gênesis', 'gn', 34, 5, 'Quando Jacó soube que sua filha Diná tinha sido desonrada, seus filhos estavam no campo, com os rebanhos; por isso esperou calado, até que regressassem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (986, 'nvi', 'Gênesis', 'gn', 34, 6, 'Então Hamor, pai de Siquém, foi conversar com Jacó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (987, 'nvi', 'Gênesis', 'gn', 34, 7, 'Quando os filhos de Jacó voltaram do campo e souberam de tudo, ficaram profundamente entristecidos e irados, porque Siquém tinha cometido um ato vergonhoso em Israel, ao deitar-se com a filha de Jacó — coisa que não se faz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (988, 'nvi', 'Gênesis', 'gn', 34, 8, 'Mas Hamor lhes disse: "Meu filho Siquém apaixonou-se pela filha de vocês. Por favor, entreguem-na a ele para que seja sua mulher.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (989, 'nvi', 'Gênesis', 'gn', 34, 9, 'Casem-se entre nós; dêem-nos suas filhas e tomem para si as nossas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (990, 'nvi', 'Gênesis', 'gn', 34, 10, 'Estabeleçam-se entre nós. A terra está aberta para vocês: Habitem-na, façam comércio nela e adquiram propriedades".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (991, 'nvi', 'Gênesis', 'gn', 34, 11, 'Então Siquém disse ao pai e aos irmãos de Diná: "Concedam-me este favor, e eu lhes darei o que me pedirem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (992, 'nvi', 'Gênesis', 'gn', 34, 12, 'Aumentem quanto quiserem o preço e o presente pela noiva, e pagarei o que me pedirem. Tão-somente me dêem a moça por mulher".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (993, 'nvi', 'Gênesis', 'gn', 34, 13, 'Os filhos de Jacó, porém, responderam com falsidade a Siquém e a seu pai Hamor, por ter Siquém desonrado Diná, a irmã deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (994, 'nvi', 'Gênesis', 'gn', 34, 14, 'Disseram: "Não podemos fazer isso; jamais entregaremos nossa irmã a um homem que não seja circuncidado. Seria uma vergonha para nós.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (995, 'nvi', 'Gênesis', 'gn', 34, 15, 'Daremos nosso consentimento a vocês com uma condição: que vocês se tornem como nós, circuncidando todos os do sexo masculino.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (996, 'nvi', 'Gênesis', 'gn', 34, 16, 'Só então lhes daremos as nossas filhas e poderemos casar-nos com as suas. Nós nos estabeleceremos entre vocês e seremos um só povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (997, 'nvi', 'Gênesis', 'gn', 34, 17, 'Mas se não aceitarem circuncidar-se, tomaremos nossa irmã e partiremos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (998, 'nvi', 'Gênesis', 'gn', 34, 18, 'A proposta deles pareceu boa a Hamor e a seu filho Siquém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (999, 'nvi', 'Gênesis', 'gn', 34, 19, 'O jovem, que era o mais respeitado de todos os da casa de seu pai, não demorou em cumprir o que pediram, porque realmente gostava da filha de Jacó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1000, 'nvi', 'Gênesis', 'gn', 34, 20, 'Assim Hamor e seu filho Siquém dirigiram-se à porta da cidade para conversar com os seus concidadãos. E disseram:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1001, 'nvi', 'Gênesis', 'gn', 34, 21, '"Esses homens são de paz. Permitam que eles habitem em nossa terra e façam comércio entre nós; a terra tem bastante lugar para eles. Poderemos casar com as suas filhas, e eles com as nossas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1002, 'nvi', 'Gênesis', 'gn', 34, 22, 'Mas eles só consentirão em viver conosco como um só povo sob a condição de que todos os nossos homens sejam circuncidados, como eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1003, 'nvi', 'Gênesis', 'gn', 34, 23, 'Lembrem-se de que os seus rebanhos, os seus bens e todos os seus outros animais passarão a ser nossos. Aceitemos então a condição para que se estabeleçam em nosso meio".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1004, 'nvi', 'Gênesis', 'gn', 34, 24, 'Todos os que saíram para reunir-se à porta da cidade concordaram com Hamor e com seu filho Siquém, e todos os homens e meninos da cidade foram circuncidados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1005, 'nvi', 'Gênesis', 'gn', 34, 25, 'Três dias depois, quando ainda sofriam dores, dois filhos de Jacó, Simeão e Levi, irmãos de Diná, pegaram suas espadas e atacaram a cidade desprevenida, matando todos os homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1006, 'nvi', 'Gênesis', 'gn', 34, 26, 'Mataram ao fio da espada Hamor e seu filho Siquém, tiraram Diná da casa de Siquém e partiram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1007, 'nvi', 'Gênesis', 'gn', 34, 27, 'Vieram então os outros filhos de Jacó e, passando pelos corpos, saquearam a cidade onde sua irmã tinha sido desonrada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1008, 'nvi', 'Gênesis', 'gn', 34, 28, 'Apoderaram-se das ovelhas, dos bois e dos jumentos, e de tudo o que havia na cidade e no campo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1009, 'nvi', 'Gênesis', 'gn', 34, 29, 'Levaram as mulheres e as crianças, e saquearam todos os bens e tudo o que havia nas casas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1010, 'nvi', 'Gênesis', 'gn', 34, 30, 'Então Jacó disse a Simeão e a Levi: "Vocês me puseram em grandes apuros, atraindo sobre mim o ódio dos cananeus e dos ferezeus, habitantes desta terra. Somos poucos, e se eles juntarem suas forças e nos atacarem, eu e a minha família seremos destruídos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1011, 'nvi', 'Gênesis', 'gn', 34, 31, 'Mas eles responderam: "Está certo ele tratar nossa irmã como uma prostituta? "');
