INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1012, 'nvi', 'Gênesis', 'gn', 35, 1, 'Deus disse a Jacó: "Suba a Betel e se estabeleça lá, e faça um altar ao Deus que lhe apareceu quando você fugia do seu irmão Esaú".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1013, 'nvi', 'Gênesis', 'gn', 35, 2, 'Disse, pois, Jacó aos de sua casa e a todos os que estavam com ele: "Livrem-se dos deuses estrangeiros que estão entre vocês, purifiquem-se e troquem de roupa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1014, 'nvi', 'Gênesis', 'gn', 35, 3, 'Venham! Vamos subir a Betel, onde farei um altar ao Deus que me ouviu no dia da minha angústia e que tem estado comigo por onde tenho andado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1015, 'nvi', 'Gênesis', 'gn', 35, 4, 'Então entregaram a Jacó todos os deuses estrangeiros que possuíam e os brincos que usavam nas orelhas, e Jacó os enterrou ao pé da grande árvore, próximo a Siquém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1016, 'nvi', 'Gênesis', 'gn', 35, 5, 'Quando eles partiram, o terror de Deus caiu de tal maneira sobre as cidades ao redor que ninguém ousou perseguir os filhos de Jacó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1017, 'nvi', 'Gênesis', 'gn', 35, 6, 'Jacó e todos os que com ele estavam chegaram a Luz, que é Betel, na terra de Canaã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1018, 'nvi', 'Gênesis', 'gn', 35, 7, 'Nesse lugar construiu um altar e lhe deu o nome de El-Betel, porque ali Deus havia se revelado a ele, quando fugia do seu irmão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1019, 'nvi', 'Gênesis', 'gn', 35, 8, 'Débora, ama de Rebeca, morreu e foi sepultada perto de Betel, ao pé do Carvalho, que por isso foi chamado Alom-Bacute.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1020, 'nvi', 'Gênesis', 'gn', 35, 9, 'Depois que Jacó retornou de Padã-Arã, Deus lhe apareceu de novo e o abençoou,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1021, 'nvi', 'Gênesis', 'gn', 35, 10, 'dizendo: "Seu nome é Jacó, mas você não será mais chamado Jacó; seu nome será Israel". Assim lhe deu o nome de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1022, 'nvi', 'Gênesis', 'gn', 35, 11, 'E Deus ainda lhe disse: "Eu sou o Deus Todo-poderoso; seja prolífero e multiplique-se. De você procederão uma nação e uma comunidade de nações, e reis estarão entre os seus descendentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1023, 'nvi', 'Gênesis', 'gn', 35, 12, 'A terra que dei a Abraão e a Isaque, dou a você; e também aos seus futuros descendentes darei esta terra".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1024, 'nvi', 'Gênesis', 'gn', 35, 13, 'A seguir, Deus elevou-se do lugar onde estivera falando com Jacó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1025, 'nvi', 'Gênesis', 'gn', 35, 14, 'Jacó levantou uma coluna de pedra no lugar em que Deus lhe falara, e derramou sobre ela uma oferta de bebidas e a ungiu com óleo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1026, 'nvi', 'Gênesis', 'gn', 35, 15, 'Jacó deu o nome de Betel ao lugar onde Deus tinha falado com ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1027, 'nvi', 'Gênesis', 'gn', 35, 16, 'Eles partiram de Betel, e quando ainda estavam a certa distância de Efrata, Raquel começou a dar à luz com grande dificuldade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1028, 'nvi', 'Gênesis', 'gn', 35, 17, 'E, enquanto padecia muito, tentando dar à luz, a parteira lhe disse: "Não tenha medo, pois você ainda terá outro menino".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1029, 'nvi', 'Gênesis', 'gn', 35, 18, 'Já ao ponto de sair-lhe a vida, quando estava morrendo, deu ao filho o nome de Benoni. Mas o pai deu-lhe o nome de Benjamim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1030, 'nvi', 'Gênesis', 'gn', 35, 19, 'Assim morreu Raquel e foi sepultada junto do caminho de Efrata, que é Belém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1031, 'nvi', 'Gênesis', 'gn', 35, 20, 'Sobre a sua sepultura Jacó levantou uma coluna, e até o dia de hoje aquela coluna marca o túmulo de Raquel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1032, 'nvi', 'Gênesis', 'gn', 35, 21, 'Israel partiu novamente e armou acampamento adiante de Migdal-Éder.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1033, 'nvi', 'Gênesis', 'gn', 35, 22, 'Na época em que Israel vivia naquela região, Rúben deitou-se com Bila, concubina de seu pai. E Israel ficou sabendo disso. Jacó teve doze filhos:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1034, 'nvi', 'Gênesis', 'gn', 35, 23, 'Estes foram seus filhos com Lia: Rúben, o filho mais velho de Jacó, Simeão, Levi, Judá, Issacar e Zebulom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1035, 'nvi', 'Gênesis', 'gn', 35, 24, 'Estes foram seus filhos com Raquel: José e Benjamim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1036, 'nvi', 'Gênesis', 'gn', 35, 25, 'Estes foram seus filhos com Bila, serva de Raquel: Dã e Naftali.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1037, 'nvi', 'Gênesis', 'gn', 35, 26, 'Estes foram seus filhos com Zilpa, serva de Lia: Gade e Aser. Foram esses os filhos de Jacó, nascidos em Padã-Arã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1038, 'nvi', 'Gênesis', 'gn', 35, 27, 'Depois Jacó foi visitar seu pai Isaque em Manre, perto de Quiriate-Arba, que é Hebrom, onde Abraão e Isaque tinham morado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1039, 'nvi', 'Gênesis', 'gn', 35, 28, 'Isaque viveu cento e oitenta anos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1040, 'nvi', 'Gênesis', 'gn', 35, 29, 'Morreu em idade bem avançada e foi reunido aos seus antepassados. E seus filhos Esaú e Jacó o sepultaram.');
