INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1084, 'nvi', 'Gênesis', 'gn', 37, 1, 'Jacó habitou na terra de Canaã, onde seu pai tinha vivido como estrangeiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1085, 'nvi', 'Gênesis', 'gn', 37, 2, 'Esta é a história da família de Jacó: Quando José tinha dezessete anos, pastoreava os rebanhos com os seus irmãos. Ajudava os filhos de Bila e os filhos de Zilpa, mulheres de seu pai; e contava ao pai a má fama deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1086, 'nvi', 'Gênesis', 'gn', 37, 3, 'Ora, Israel gostava mais de José do que de qualquer outro filho, porque lhe havia nascido em sua velhice; por isso mandou fazer para ele uma túnica longa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1087, 'nvi', 'Gênesis', 'gn', 37, 4, 'Quando os seus irmãos viram que o pai gostava mais dele do que de qualquer outro filho, odiaram-no e não conseguiam falar com ele amigavelmente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1088, 'nvi', 'Gênesis', 'gn', 37, 5, 'Certa vez, José teve um sonho e, quando o contou a seus irmãos, eles passaram a odiá-lo ainda mais.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1089, 'nvi', 'Gênesis', 'gn', 37, 6, '"Ouçam o sonho que tive", disse-lhes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1090, 'nvi', 'Gênesis', 'gn', 37, 7, '"Estávamos amarrando os feixes de trigo no campo, quando o meu feixe se levantou e ficou em pé, e os seus feixes se ajuntaram ao redor do meu e se curvaram diante dele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1091, 'nvi', 'Gênesis', 'gn', 37, 8, 'Seus irmãos lhe disseram: "Então você vai reinar sobre nós? Quer dizer que você vai governar sobre nós? " E o odiaram ainda mais, por causa do sonho e do que tinha dito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1092, 'nvi', 'Gênesis', 'gn', 37, 9, 'Depois teve outro sonho e o contou aos seus irmãos: "Tive outro sonho, e desta vez o sol, a lua e onze estrelas se curvavam diante de mim".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1093, 'nvi', 'Gênesis', 'gn', 37, 10, 'Quando o contou ao pai e aos irmãos, o pai o repreendeu e lhe disse: "Que sonho foi esse que você teve? Será que eu, sua mãe, e seus irmãos viremos a nos curvar até o chão diante de você? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1094, 'nvi', 'Gênesis', 'gn', 37, 11, 'Assim seus irmãos tiveram ciúmes dele; o pai, no entanto, refletia naquilo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1095, 'nvi', 'Gênesis', 'gn', 37, 12, 'Os irmãos de José tinham ido cuidar dos rebanhos do pai, perto de Siquém,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1096, 'nvi', 'Gênesis', 'gn', 37, 13, 'e Israel disse a José: "Como você sabe, seus irmãos estão apascentando os rebanhos perto de Siquém. Quero que você vá até lá". "Sim, senhor", respondeu ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1097, 'nvi', 'Gênesis', 'gn', 37, 14, 'Disse-lhe o pai: "Vá ver se está tudo bem com os seus irmãos e com os rebanhos, e traga-me notícias". Jacó o enviou quando estava no vale de Hebrom. Mas José se perdeu quando se aproximava de Siquém;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1098, 'nvi', 'Gênesis', 'gn', 37, 15, 'um homem o encontrou vagueando pelos campos e lhe perguntou: "Que é que você está procurando? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1099, 'nvi', 'Gênesis', 'gn', 37, 16, 'Ele respondeu: "Procuro meus irmãos. Pode me dizer onde eles estão apascentando os rebanhos? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1100, 'nvi', 'Gênesis', 'gn', 37, 17, 'Respondeu o homem: "Eles já partiram daqui. Eu os ouvi dizer: ‘Vamos para Dotã’ ". Assim José foi em busca dos seus irmãos e os encontrou perto de Dotã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1101, 'nvi', 'Gênesis', 'gn', 37, 18, 'Mas eles o viram de longe e, antes que chegasse, planejaram matá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1102, 'nvi', 'Gênesis', 'gn', 37, 19, '"Lá vem aquele sonhador! ", diziam uns aos outros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1103, 'nvi', 'Gênesis', 'gn', 37, 20, '"É agora! Vamos matá-lo e jogá-lo num destes poços, e diremos que um animal selvagem o devorou. Veremos então o que será dos seus sonhos. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1104, 'nvi', 'Gênesis', 'gn', 37, 21, 'Quando Rúben ouviu isso, tentou livrá-lo das mãos deles, dizendo: "Não lhe tiremos a vida! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1105, 'nvi', 'Gênesis', 'gn', 37, 22, 'E acrescentou: "Não derramem sangue. Joguem-no naquele poço no deserto, mas não toquem nele". Rúben propôs isso com a intenção de livrá-lo e levá-lo de volta ao pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1106, 'nvi', 'Gênesis', 'gn', 37, 23, 'Chegando José, seus irmãos lhe arrancaram a túnica longa,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1107, 'nvi', 'Gênesis', 'gn', 37, 24, 'agarraram-no e o jogaram no poço, que estava vazio e sem água.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1108, 'nvi', 'Gênesis', 'gn', 37, 25, 'Ao se assentarem para comer, viram ao longe uma caravana de ismaelitas que vinha de Gileade. Seus camelos estavam carregados de especiarias, bálsamo e mirra, que eles levavam para o Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1109, 'nvi', 'Gênesis', 'gn', 37, 26, 'Judá disse então a seus irmãos: "Que ganharemos se matarmos o nosso irmão e escondermos o seu sangue?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1110, 'nvi', 'Gênesis', 'gn', 37, 27, 'Vamos vendê-lo aos ismaelitas. Não tocaremos nele, afinal é nosso irmão, é nosso próprio sangue". E seus irmãos concordaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1111, 'nvi', 'Gênesis', 'gn', 37, 28, 'Quando os mercadores ismaelitas de Midiã se aproximaram, seus irmãos tiraram José do poço e o venderam por vinte peças de prata aos ismaelitas, que o levaram para o Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1112, 'nvi', 'Gênesis', 'gn', 37, 29, 'Quando Rúben voltou ao poço e viu que José não estava lá, rasgou suas vestes');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1113, 'nvi', 'Gênesis', 'gn', 37, 30, 'e, voltando a seus irmãos, disse: "O jovem não está lá! Para onde irei agora? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1114, 'nvi', 'Gênesis', 'gn', 37, 31, 'Então eles mataram um bode, mergulharam no sangue a túnica de José');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1115, 'nvi', 'Gênesis', 'gn', 37, 32, 'e a mandaram ao pai com este recado: "Achamos isto. Veja se é a túnica de teu filho".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1116, 'nvi', 'Gênesis', 'gn', 37, 33, 'Ele a reconheceu e disse: "É a túnica de meu filho! Um animal selvagem o devorou! José foi despedaçado! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1117, 'nvi', 'Gênesis', 'gn', 37, 34, 'Então Jacó rasgou suas vestes, vestiu-se de pano de saco e chorou muitos dias por seu filho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1118, 'nvi', 'Gênesis', 'gn', 37, 35, 'Todos os seus filhos e filhas vieram consolá-lo, mas ele recusou ser consolado, dizendo: "Não! Chorando descerei à sepultura para junto de meu filho". E continuou a chorar por ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1119, 'nvi', 'Gênesis', 'gn', 37, 36, 'Nesse meio tempo, no Egito, os midianitas venderam José a Potifar, oficial do faraó e capitão da guarda.');
