INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1120, 'nvi', 'Gênesis', 'gn', 38, 1, 'Por essa época, Judá deixou seus irmãos e passou a viver na casa de um homem de Adulão, chamado Hira.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1121, 'nvi', 'Gênesis', 'gn', 38, 2, 'Ali Judá encontrou a filha de um cananeu chamado Suá, e casou-se com ela. Ele a possuiu,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1122, 'nvi', 'Gênesis', 'gn', 38, 3, 'ela engravidou e deu à luz um filho, ao qual ele deu o nome de Er.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1123, 'nvi', 'Gênesis', 'gn', 38, 4, 'Tornou a engravidar, teve um filho e deu-lhe o nome de Onã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1124, 'nvi', 'Gênesis', 'gn', 38, 5, 'Quando estava em Quezibe, ela teve ainda outro filho e chamou-o Selá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1125, 'nvi', 'Gênesis', 'gn', 38, 6, 'Judá escolheu uma mulher chamada Tamar, para Er, seu filho mais velho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1126, 'nvi', 'Gênesis', 'gn', 38, 7, 'Mas o Senhor reprovou a conduta perversa de Er, filho mais velho de Judá, e por isso o matou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1127, 'nvi', 'Gênesis', 'gn', 38, 8, 'Então Judá disse a Onã: "Case-se com a mulher do seu irmão, cumpra as suas obrigações de cunhado para com ela e dê uma descendência a seu irmão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1128, 'nvi', 'Gênesis', 'gn', 38, 9, 'Mas Onã sabia que a descendência não seria sua; assim, toda vez que possuía a mulher do seu irmão, derramava o sêmen no chão para evitar que seu irmão tivesse descendência.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1129, 'nvi', 'Gênesis', 'gn', 38, 10, 'O Senhor reprovou o que ele fazia, e por isso o matou também.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1130, 'nvi', 'Gênesis', 'gn', 38, 11, 'Disse então Judá à sua nora Tamar: "More como viúva na casa de seu pai até que o meu filho Selá cresça", porque pensou: "Ele também poderá morrer, como os seus irmãos". Assim Tamar foi morar na casa do pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1131, 'nvi', 'Gênesis', 'gn', 38, 12, 'Tempos depois morreu a mulher de Judá, filha de Suá. Passado o luto, Judá foi ver os tosquiadores do seu rebanho em Timna com o seu amigo Hira, o adulamita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1132, 'nvi', 'Gênesis', 'gn', 38, 13, 'Quando foi dito a Tamar: "Seu sogro está a caminho de Timna para tosquiar suas ovelhas",');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1133, 'nvi', 'Gênesis', 'gn', 38, 14, 'ela trocou suas roupas de viúva, cobriu-se com um véu para se disfarçar e foi sentar-se à entrada de Enaim, que fica no caminho de Timna. Ela fez isso porque viu que, embora Selá já fosse crescido, ela não lhe tinha sido dada em casamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1134, 'nvi', 'Gênesis', 'gn', 38, 15, 'Quando a viu, Judá pensou que fosse uma prostituta, porque ela havia encoberto o rosto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1135, 'nvi', 'Gênesis', 'gn', 38, 16, 'Não sabendo que era a sua nora, dirigiu-se a ela, à beira da estrada, e disse: "Venha cá, quero deitar-me com você". Ela lhe perguntou: "O que você me dará para deitar-se comigo? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1136, 'nvi', 'Gênesis', 'gn', 38, 17, 'Disse ele: "Eu lhe mandarei um cabritinho do meu rebanho". E ela perguntou: "Você me deixará alguma coisa como garantia até que o mande? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1137, 'nvi', 'Gênesis', 'gn', 38, 18, 'Disse Judá: "Que garantia devo dar-lhe? " Respondeu ela: "O seu selo com o cordão, e o cajado que você tem na mão". Ele os entregou e a possuiu, e Tamar engravidou dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1138, 'nvi', 'Gênesis', 'gn', 38, 19, 'Ela se foi, tirou o véu e tornou a vestir as roupas de viúva.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1139, 'nvi', 'Gênesis', 'gn', 38, 20, 'Judá mandou o cabritinho por meio de seu amigo adulamita, a fim de reaver da mulher sua garantia, mas ele não a encontrou,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1140, 'nvi', 'Gênesis', 'gn', 38, 21, 'e perguntou aos homens do lugar: "Onde está a prostituta cultual que costuma ficar à beira do caminho de Enaim? " Eles responderam: "Aqui não há nenhuma prostituta cultual".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1141, 'nvi', 'Gênesis', 'gn', 38, 22, 'Assim ele voltou a Judá e disse: "Não a encontrei. Além disso, os homens do lugar disseram que lá não há nenhuma prostituta cultual".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1142, 'nvi', 'Gênesis', 'gn', 38, 23, 'Disse Judá: "Fique ela com o que lhe dei. Não quero que nos tornemos motivo de zombaria. Afinal de contas, mandei a ela este cabritinho, mas você não a encontrou".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1143, 'nvi', 'Gênesis', 'gn', 38, 24, 'Cerca de três meses mais tarde, disseram a Judá: "Sua nora Tamar prostituiu-se, e na sua prostituição ficou grávida". Disse Judá: "Tragam-na para fora e queimem-na viva! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1144, 'nvi', 'Gênesis', 'gn', 38, 25, 'Quando ela estava sendo levada para fora, mandou o seguinte recado ao sogro: "Estou grávida do homem que é dono destas coisas". E acrescentou: "Veja se o senhor reconhece a quem pertencem este selo, este cordão e este cajado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1145, 'nvi', 'Gênesis', 'gn', 38, 26, 'Judá os reconheceu e disse: "Ela é mais justa do que eu, pois eu devia tê-la entregue a meu filho Selá". E não voltou a ter relações com ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1146, 'nvi', 'Gênesis', 'gn', 38, 27, 'Quando lhe chegou a época de dar à luz, havia gêmeos em seu ventre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1147, 'nvi', 'Gênesis', 'gn', 38, 28, 'Enquanto ela dava à luz, um deles pôs a mão para fora; então a parteira pegou um fio vermelho e amarrou o pulso do menino, dizendo: "Este saiu primeiro".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1148, 'nvi', 'Gênesis', 'gn', 38, 29, 'Mas quando ele recolheu a mão, seu irmão saiu e ela disse: "Então você conseguiu uma brecha para sair! " E deu-lhe o nome de Perez.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1149, 'nvi', 'Gênesis', 'gn', 38, 30, 'Depois saiu seu irmão que estava com o fio vermelho no pulso, e foi-lhe dado o nome de Zerá.');
